INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Wallace et Gromit : le Mystère du lapin-garou", "../img/Covers/movies/wallace-et-gromit-2005.jpg", 85, 2005, "Animation, Aventure, Comédie", "A quelques jours du Grand Concours Annuel de Légumes, les affaires de Wallace et Gromit n'ont jamais été aussi florissantes, et tout irait pour le mieux dans le meilleur des mondes, si un lapin-garou ne venait s'attaquer aux potagers de la ville...", "Peter Sallis, Helena Bonham Carter, Ralph Fiennes", "Steve Box, Nick Park");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Légende de Zorro", "../img/Covers/movies/la-legende-de-zorro-2005.jpg", 129, 2005, "Action, Aventure, Romance", "En 1850, la Californie aspire à devenir le 31e Etat de l'Union, mais les membres de la mystérieuse confrérie médiévale des Chevaliers d'Aragon sont décidés à l'en empêcher par tous les moyens... Seul Zorro peut intervenir...", "Antonio Banderas, Catherine Zeta-Jones, Rufus Sewell", "Martin Campbell");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Chicken Little", "../img/Covers/movies/chicken-little-2005.jpg", 81, 2005, "Animation, Aventure, Comédie", "Chicken Little, un petit poulet, est la risée d'Oakey Oaks, un village peuplé d'animaux. Un jour, il reçoit un vrai morceau de ciel sur la tête. Mais cette fois, qui le croira ? Avec sa petite taille, pourra-t-il sauver sa ville toute entière ?", "Zach Braff, Joan Cusack, Garry Marshall", "Mark Dindal");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Espace détente", "../img/Covers/movies/espace-detente-2005.jpg", 102, 2005, "Comédie", "Au fond de la Veule, imaginaire province française, vivote la Geugène Electro Stim, une moyenne entreprise. L'opportunité d'un nouveau produit, le BodyCompact, va jeter cette communauté tranquille dans les affres d'un libéralisme sans foi ni loi...", "Bruno Solo, Yvan Le Bolloc'h, Thierry Frémont", "Yvan Le Bolloc'h, Bruno Solo");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ray", "../img/Covers/movies/ray-2005.jpg", 152, 2005, "Biopic, Drame, Musical", "Ray Charles, c'est d'abord un mythe : cinq décennies de succès, une carrière musicale exceptionnellement riche, féconde et diverse, émaillée de dizaines de classiques qui ont fait le tour du monde et inspiré des générations de jeunes artistes. Mais derrière cette image légendaire se profile l'histoire émouvante, méconnue, d'une vie, l'itinéraire d'un homme qui réussit à surmonter ses handicaps et ses drames personnels.", "Ray Harryhausen, Forrest J. Ackerman, Jim Aupperle, Rick Baker", "Ray Harryhausen");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Island", "../img/Covers/movies/the-island-2005.jpg", 136, 2005, "Action, Science-fiction, Thriller", "Dans un futur proche, un être prend conscience de son existence et du piège que représente la société utopique dans laquelle il vit...", "Scarlett Johansson, Ewan McGregor, Djimon Hounsou", "Michael Bay");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Batman Begins", "../img/Covers/movies/batman-begins-2005.jpg", 140, 2005, "Action, Crime, Drame", "Comment un homme seul peut-il changer le monde ? Telle est la question qui hante Bruce Wayne depuis cette nuit tragique où ses parents furent abattus sous ses yeux. Il décide de fuir Gotham City pour un long et discret voyage à travers le monde...", "Christian Bale, Michael Caine, Ken Watanabe", "Christopher Nolan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Oliver Twist", "../img/Covers/movies/oliver-twist-2005.jpg", 130, 2005, "Crime, Drame", "Dans un orphelinat d'Angleterre, Oliver Twist survit au milieu de ses compagnons d'infortune. Il ne connaît que privations et mauvais traitements. Il endure tout, jusqu'au jour où une provocation de trop le pousse à s'enfuir vers Londres...", "Barney Clark, Ben Kingsley, Jeremy Swift", "Roman Polanski");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Match Point", "../img/Covers/movies/match-point-2005.jpg", 124, 2005, "Drame, Romance, Thriller", "Prof de tennis, Chris se fait embaucher dans un club huppé de Londres. Il sympathise avec Tom, un jeune homme de la haute société, et séduit Chloe, sa soeur. Alors qu'il s'apprête à l'épouser, il rencontre la fiancée de Tom, Nola...", "Scarlett Johansson, Jonathan Rhys Meyers, Emily Mortimer", "Woody Allen");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Tout pour plaire", "../img/Covers/movies/tout-pour-plaire-2005.jpg", 105, 2005, "Comédie", "Chronique de moeurs en milieu urbain, où le trio de choc Godrèche-Parillaud-Seigner arrive aux fatidique cap des 35 ans", "Mathilde Seigner, Anne Parillaud, Judith Godrèche", "Cécile Telerman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Kirikou et les bêtes sauvages", "../img/Covers/movies/kirikou-2-2005.jpg", 75, 2005, "Animation, Aventure, Famille", "Le grand-père, trônant dans sa grotte bleue, explique comment l'inventif Kirikou est devenu jardinier, détective, potier, marchand, voyageur et médecin, toujours le plus petit et le plus vaillant des héros.", "Pierre-Ndoffé Sarr, Awa Sene Sarr, Robert Liensol", "Bénédicte Galup, Michel Ocelot");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Kingdom of Heaven", "../img/Covers/movies/kingdom-of-heaven-2005.jpg", 144, 2005, "Action, Aventure, Drame", "L'aventure extraordinaire d'un homme ordinaire, précipité dans un conflit qui va durer des décennies : les croisades. Il lui faudra protéger les habitants de Jérusalem, dont une immense armée a entrepris le siège...", "Orlando Bloom, Eva Green, Liam Neeson", "Ridley Scott");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Constantine", "../img/Covers/movies/constantine-2005.jpg", 121, 2005, "Action, Fantastique, Horreur", "John Constantine, extralucide anticonformiste, qui a fait un aller-retour aux enfers, doit aider Katelin Dodson, une femme policier incrédule, à lever le voile sur le suicide mystérieux de sa soeur jumelle.", "Keanu Reeves, Rachel Weisz, Djimon Hounsou", "Francis Lawrence");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Robots", "../img/Covers/movies/robots-2005.jpg", 91, 2005, "Animation, Aventure, Comédie", "Rodney est un jeune robot plein d'idées, mais son village est trop petit pour ses rêves. Il part à Robot Ville rencontrer Bigweld, le plus génial inventeur et bienfaiteur de tous les robots, pour lui proposer ses services...", "Ewan McGregor, Halle Berry, Mel Brooks", "Chris Wedge, Carlos Saldanha");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("L'Âge de glace 2", "../img/Covers/movies/age-de-glace-2-2006.jpg", 91, 2006, "Animation, Adventure, Comedy", "Manfred, Sid et Diego vont vite s'apercevoir que leur petit coin de paradis est en danger. La fonte des neiges menace d'inonder leur vallée. Nos héros doivent donc trouver une solution pour sauver les animaux du déluge imminent...", "Ray Romano, John Leguizamo, Denis Leary", "Carlos Saldanha");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Camping", "../img/Covers/movies/camping-2006.jpg", 95, 2006, "Comédie", "Michel Saint-Josse, un chirurgien esthétique, emmène, à bord de son Aston Martin, sa fille Vanessa en vacances à Marbella. Mais en cours de route, son véhicule tombe en panne, et tous deux échouent au camping des Flots Bleus, au Cap-Ferret...", "Gérard Lanvin, Mathilde Seigner, Franck Dubosc", "Fabien Onteniente");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Da Vinci Code", "../img/Covers/movies/da-vinci-code-2006.jpg", 149, 2006, "Mystère, Thriller", "'The Da Vinci code' commence par un meurtre spectaculaire perpétré dans le Musée du Louvre. Tous les indices pointent vers une organisation religieuse clandestine prête à tout pour protéger un secret qui menace un dogme acquis depuis 2000 ans.", "Tom Hanks, Audrey Tautou, Jean Reno", "Ron Howard");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("X-Men l'affrontement final", "../img/Covers/movies/x-men-3-2006.jpg", 104, 2006, "Action, Aventure, Science-fiction", "Un traitement permet aux mutants d'échapper à ce qu'ils sont. Ils ont le choix : conserver ce qui fait leur caractère unique mais leur vaut la méfiance de l'humanité, ou bien abandonner leurs pouvoirs et devenir des humains à part entière.", "Patrick Stewart, Hugh Jackman, Halle Berry", "Brett Ratner");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("OSS 117, Le Caire nid d'espions", "../img/Covers/movies/oss-117-2006.jpg", 99, 2006, "Action, Adventure, Comedy", "Égypte, 1955, le Caire est un véritable nid d'espions. Le Président de la République Française, Monsieur René Coty, envoie son arme maîtresse mettre de l'ordre dans cette pétaudière au bord du chaos : Hubert Bonisseur de la Bath, dit OSS 117.", "François Damiens, Jean Dujardin, Khalid Maadour", "Michel Hazanavicius");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Cars", "../img/Covers/movies/cars-2006.jpg", 117, 2006, "Animation, Aventure, Comédie", "Flash McQueen, une splendide voiture de course toute neuve promise au succès, découvre que dans la vie, ce n'est pas de franchir la ligne d'arrivée qui compte, mais le parcours que l'on a suivi. Parti pour participer à la prestigieuse Piston Cup, il atterrit suite à une déviation dans la petite ville tranquille de Radiator Springs, sur la Route 66.", "Owen Wilson, Bonnie Hunt, Paul Newman", "John Lasseter, Joe Ranft");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Mission: Impossible III", "../img/Covers/movies/mission-impossible-3-2006.jpg", 126, 2006, "Action, Aventure, Thriller", "Ethan a quitté le service actif de la Force Mission Impossible pour un poste de formateur ; pouvoir enfin mener une vie 'normale'... Mais lorsque la plus brillante recrue de l'IMF 'tombe' à Berlin, Ethan se sent obligé de lui porter secours...", "Tom Cruise, Michelle Monaghan, Ving Rhames", "J.J. Abrams");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Bambi 2", "../img/Covers/movies/bambi-2-2006.jpg", 75, 2006, "Animation, Aventure, Drame, Famille, Fantastique, Romance", "Bambi est élevé par son père, le Grand Prince de la forêt. Sans tendresse, il lui enseigne toute les prérogatives dues à son rang, mais semble toujours déçu par son fils. Lorsque Bambi revoit Féline, la jeune biche, beaucoup d'émotions l'envahissent...", "Patrick Stewart, Alexander Gould, Keith Ferguson, Brendon Baerg", "Brian Pimental");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Azur et Asmar", "../img/Covers/movies/azur-et-asmar-2006.jpg", 99, 2006, "Animation, Aventure, Famille", "Azur est un blond aux yeux bleus, Asmar un brun aux yeux noirs. Petits, ils se battent et s'aiment comme des frères. Grands, ils se retrouvent rivaux, en quête de la Fée des Djinns, dans un Maghreb médiéval, plein de dangers et de merveilles.", "Cyril Mourali, Karim M'Riba, Hiam Abbass", "Michel Ocelot, Ian McIntyre");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Eragon", "../img/Covers/movies/eragon-2006.jpg", 104, 2006, "Action, Aventure, Famille", "L'incroyable voyage d'un jeune garçon et de son dragon Saphira dans le monde d'Alagaësia. Devenu Dragonnier, Eragon doit quitter les siens pour combattre la tyrannie des Urgals et des Ombres. Le destin de l'empire est entre ses mains.", "Ed Speleers, Sienna Guillory, Jeremy Irons", "Stefen Fangmeier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Miami Vice", "../img/Covers/movies/miami-vice-2006.jpg", 132, 2006, "Action, Crime, Drame", "Miami... Deux agents fédéraux et la famille d'un informateur ont été sauvagement exécutés. Une nouvelle enquête commence pour Sonny Crockett et son coéquipier Ricardo Tubbs...", "Colin Farrell, Jamie Foxx, Gong Li", "Michael Mann");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Nos jours heureux", "../img/Covers/movies/nos-jours-heureux-2006.jpg", 103, 2006, "Comédie", "Trois semaines en colo : entre petites histoires et gros soucis, la vie mouvementée du camp, de ses animateurs plus ou moins professionnels et des ados pas toujours évidents à gérer...", "Jean-Paul Rouve, Marilou Berry, Omar Sy", "Olivier Nakache, Éric Toledano");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Superman Returns", "../img/Covers/movies/superman-returns-2006.jpg", 154, 2006, "Action, Aventure, Science-fiction", "Alors que Lex Luthor tente de l'affaiblir en lui dérobant ses superpouvoirs, Superman doit faire face à un problème d'un autre genre : Lois Lane, la femme qu'il aime, est partie mener une nouvelle vie loin de lui. Mais l'a-t-elle vraiment oublié ?", "Brandon Routh, Kevin Spacey, Kate Bosworth", "Bryan Singer");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Asterix et les Vikings", "../img/Covers/movies/asterix-et-les-vikings-2006.jpg", 78, 2006, "Animation, Aventure, Comédie", "Le petit village gaulois accueille Goudurix, le neveu du chef, et Astérix et Obélix sont chargés d'en faire un homme, un vrai. Au même moment, les Vikings débarquent en Gaule, décidés à trouver un 'champion de la peur'...", "Marion Game, Roger Carel, Jacques Frantz", "Stefan Fjeldmark, Jesper Møller, Joanna Wizmur");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Garfield 2", "../img/Covers/movies/garfield-2-2006.jpg", 82, 2006, "Aventure, Comédie, Famille", "En Angleterre, Garfield est pris pour Prince, un chat noble venant d'hériter d'un château. Adoré par ses sujets, Garfield se régale. Mais son règne est en danger : Lord Dargis veut se débarrasser de lui et faire du château un parc d'attractions...", "Breckin Meyer, Jennifer Love Hewitt, Billy Connolly", "Tim Hill");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Jean-Philippe", "../img/Covers/movies/jean-philippe-2006.jpg", 90, 2006, "Comédie, Musical", "Fabrice est le plus grand fan de Johnny Hallyday. Un matin, il se réveille dans un monde parallèle où Johnny n'existe pas. Désespéré, il va tenter de convaincre Jean-Philippe Smet, patron de bowling en banlieue parisienne, de 'devenir' Johnny...", "Fabrice Luchini, Johnny Hallyday, Guilaine Londez", "Laurent Tuel");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Marie Antoinette", "../img/Covers/movies/marie-antoinette-2006.jpg", 123, 2006, "Biopic, Drame, Historique", "Évocation de la vie de la reine d'origine autrichienne, épouse mal-aimée de Louis XVI, guillotinée en 1793.", "Kirsten Dunst, Jason Schwartzman, Rip Torn", "Sofia Coppola");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Inside Man - l'homme de l'intérieur", "../img/Covers/movies/inside-man-2006.jpg", 129, 2006, "Crime, Drame, Mystère", "Un flic tenace se met en tête d'arrêter un ingénieux braqueur de banques, dont le nouveau hold up vire à la prise d'otages. L'intervention d'une négociatrice ambitieuse sur les lieux mêmes du casse va compliquer la situation.", "Denzel Washington, Clive Owen, Jodie Foster", "Spike Lee");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Happy Feet", "../img/Covers/movies/happy-feet-2006.jpg", 108, 2006, "Animation, Aventure, Comédie", "Les aventures de Mumble, un jeune manchot dôté d'un don extraordinaire, sur la banquise de l'Antarctique.", "Elijah Wood, Brittany Murphy, Hugh Jackman", "George Miller, Warren Coleman, Judy Morris");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Munich", "../img/Covers/movies/munich-2006.jpg", 160, 2006, " Drame, Historique, Thriller", "Dans la nuit du 5 septembre, un commando de l'organisation palestinienne Septembre Noir s'introduit dans le Village Olympique, force l'entrée du pavillon israélien, abat deux de ses occupants et prend en otages les neuf autres. 21 heures plus tard, tous seront morts, et 900 millions de téléspectateurs auront découvert en direct le nouveau visage du terrorisme.", "Eric Bana, Daniel Craig, Ciarán Hinds", "Steven Spielberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ratatouille", "../img/Covers/movies/ratatouille-2007.jpg", 111, 2007, "Animation, Aventure, Comédie", "Rémy est un jeune rat qui rêve de devenir un grand chef français. Ni l'opposition de sa famille, ni le fait d'être un rongeur dans une profession qui les déteste ne le démotivent. Rémy est prêt à tout pour vivre sa passion de la cuisine... et le fait d'habiter dans les égouts du restaurant ultra coté de la star des fourneaux, Auguste Gusteau, va lui en donner l'occasion ! Malgré le danger et les pièges, la tentation est grande de s'aventurer dans cet univers interdit.", "Brad Garrett, Lou Romano, Patton Oswalt", "Brad Bird, Jan Pinkava");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Spider-Man 3", "../img/Covers/movies/spider-man-3-2007.jpg", 139, 2007, "Action, Aventure, Science-fiction", "Peter Parker a enfin réussi à concilier son amour pour Mary-Jane et ses devoirs de super-héros. Mais l'horizon s'obscurcit. La brutale mutation de son costume, qui devient noir, décuple ses pouvoirs et transforme également sa personnalité...", "Tobey Maguire, Kirsten Dunst, Topher Grace", "Sam Raimi");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Harry Potter et l'Ordre du Phénix", "../img/Covers/movies/harry-potter-5-2007.jpg", 138, 2007, "Action, Aventure, Famille", "Le Ministre de la Magie Cornelius Fudge impose à Poudlard un nouveau professeur de Défense contre les Forces du Mal, Dolorès Ombrage, chargée de maintenir l'ordre à l'école et de surveiller les faits et gestes de Dumbledore...", "Daniel Radcliffe, Emma Watson, Rupert Grint", "David Yates");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Shrek le troisième", "../img/Covers/movies/shrek-3-2007.jpg", 93, 2007, "Animation, Aventure, Comédie", "L'ogre Shrek n'avait pas quitté son marécage pestilentiel et épousé sa chère Fiona pour devenir roi. C'est pourtant ce qui risque de lui arriver après que son beau-père Harold clabote soudain sans crier gare...", "Mike Myers, Cameron Diaz, Eddie Murphy", "Chris Miller, Raman Hui");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Taxi 4", "../img/Covers/movies/taxi-4-2007.jpg", 91, 2007, "Action, Comédie, Crime", "De l'eau a coulé sous les ponts depuis la dernière aventure de Daniel et Emilien : les deux hommes sont désormais pères de famille et ont du mal à s'occuper de leurs fils, deux sacrés canailles. Une chose n'a pas changé cependant : Emilien est toujours le policier le plus maladroit et malchanceux de Marseille et Daniel le conducteur de taxi le plus rapide. Les deux hommes vont devoir faire équipe afin d'arrêter un terrifiant braqueur de banque surnommé 'Le Belge'...", "Samy Naceri, Frédéric Diefenthal, Bernard Farcy", "Gérard Krawczyk");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Nuit au musée", "../img/Covers/movies/la-nuit-au-musee-2006.jpg", 108, 2006, "Aventure, Comédie, Famille", "Le Muséum d'Histoire Naturelle renferme dans ses murs un secret mystérieux et stupéfiant que Larry, nouveau gardien de sécurité, ne va pas tarder à découvrir avec affolement : la nuit, toutes les expositions prennent vie !", "Ben Stiller, Carla Gugino, Ricky Gervais", "Shawn Levy");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Transformers", "../img/Covers/movies/transformers-2007.jpg", 144, 2007, "Action, Aventure, Science-fiction", "Deux races extra-terrestres ennemies, les Autobots et les Decepticons, ont fait de la Terre un vaste champ de bataille. Cette guerre est la leur ; son enjeu est la survie de notre monde...", "Shia LaBeouf, Megan Fox, Josh Duhamel", "Michael Bay");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Je suis une légende", "../img/Covers/movies/je-suis-une-legende-2007.jpg", 101, 2007, "Science-Fiction", "Ayant échappé au contrôle de ses créateurs, un virus décime la quasi-totalité de la population mondiale et transforme les rares survivants en mutants assoiffés de sang. Robert Neville, le dernier Homme sur Terre, organise sa survie à New York...", "Will Smith, Alice Braga, Charlie Tahan", "Francis Lawrence");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ocean's 13", "../img/Covers/movies/oceans-13-2007.jpg", 122, 2007, "Crime, Thriller", "Danny Ocean rameute sa troupe pour un troisième braquage, après que le propriétaire de casino Willy Bank ait trahi un membre de l'équipe.", "George Clooney, Brad Pitt, Matt Damon", "Steven Soderbergh");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Persepolis", "../img/Covers/movies/persepolis-2007.jpg", 96, 2007, "Animation, Biopic", "Adaptation de la célèbre bande dessinée, portrait de la société iranienne vue par une jeune fille et sa famille de Téhéran.", "Chiara Mastroianni, Catherine Deneuve, Gena Rowlands", "Vincent Paronnaud, Marjane Satrapi");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Molière", "../img/Covers/movies/moliere-2007.jpg", 120, 2007, "Biopic, Comédie", "A 22 ans, Molière, libéré de prison, disparait pendant quelques mois. Que s'est-il passé durant cette éclipse ? Ce film se propose de répondre à cette question, en plongeant l'auteur dans une aventure qui va lui ouvrir les yeux et l'esprit.", "Romain Duris, Fabrice Luchini, Laura Morante", "Laurent Tirard");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("American Gangster", "../img/Covers/movies/american-gangster-2007.jpg", 157, 2007, "Biopic, Crime, Drame", "Dans les années 70, les manoeuvres d'un dealer de Harlem ayant trouvé un moyen de transporter de la drogue dans les cercueils de soldats américains tués au Vietnam...", "Denzel Washington, Russell Crowe, Chiwetel Ejiofor", "Ridley Scott");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Zodiac", "../img/Covers/movies/zodiac-2007.jpg", 157, 2007, "Crime, Drame, Mystère", "De 1966 à 1978, l'histoire de trois individus déterminés à mettre un terme aux agissements du Zodiac killer, un tueur en série responsable de la mort de 37 personnes à San Francisco.", "Jake Gyllenhaal, Robert Downey Jr., Mark Ruffalo", "David Fincher");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ghost Rider", "../img/Covers/movies/ghost-rider-2007.jpg", 110, 2007, "Action, Fantastique, Thriller", "Les aventures du super-héros Johnny Blaze, un motard qui pactise malgré lui avec les forces du Mal et doit les combattre pour protéger celle qu'il aime.", "Nicolas Cage, Eva Mendes, Sam Elliott", "Mark Steven Johnson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Astérix aux Jeux Olympiques", "../img/Covers/movies/asterix-aux-jeux-olympiques-2008.jpg", 116, 2008, "Aventure, Comédie, Famille", "Astérix et Obélix décident de participer aux Jeux olympiques. Mais le fils de César, l'arrogant Brutus, entend bien leur barrer la route qui mène au podium.", "Gérard Depardieu, Clovis Cornillac, Benoît Poelvoorde", "Frédéric Forestier, Thomas Langmann");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Indiana Jones et le Royaume du Crâne de Cristal", "../img/Covers/movies/indiana-jones-4-2008.jpg", 122, 2008, "Action, Aventure", "Le quatrième épisode des aventures du célèbre archéologue Indiana Jones, lancé sur les traces des légendaires crânes de cristal. A ses trousses, la cruelle et somptueuse Irina Spalko et ses sinistres sbires russes...", "Harrison Ford, Cate Blanchett, Shia LaBeouf", "Steven Spielberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Quantum of Solace", "../img/Covers/movies/quantum-of-solace-2008.jpg", 106, 2008, "Action, Aventure, Mystère", "James Bond se retrouve cette fois-ci aux prises avec une organisation criminelle dirigée par un certain Dominic Greene, homme d'affaires extrêmement rusé déterminé à faire main basse sur les ressources naturelles du globe terrestre...", "Daniel Craig, Olga Kurylenko, Mathieu Amalric", "Marc Forster");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Kung Fu Panda", "../img/Covers/movies/kung-fu-panda-2008.jpg", 92, 2008, "Animation, Action, Aventure", "Passionné, costaud et maladroit, le panda Po est le plus grand fan de kung fu. Taï Lung, un léopard des neiges fourbe et animé d'un désir de vengeance, approche à grands pas, et ce sera à lui de défendre la vallée face à cette menace grandissante...", "Jack Black, Ian McShane, Angelina Jolie", "Mark Osborne, John Stevenson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Wall-E", "../img/Covers/movies/wall-e-2008.jpg", 98, 2008, "Animation, Aventure, Famille", "Faites la connaissance de WALL•E (prononcez 'Walli') : WALL•E est le dernier être sur Terre et s'avère être un... petit robot ! 700 ans plus tôt, l'humanité a déserté notre planète laissant à cette incroyable petite machine le soin de nettoyer la Terre. Mais au bout de ces longues années, WALL•E a développé un petit défaut technique : une forte personnalité. Extrêmement curieux, très indiscret, il est surtout un peu trop seul...", "Ben Burtt, Elissa Knight, Jeff Garlin", "Andrew Stanton");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Hancock", "../img/Covers/movies/hancock-2008.jpg", 92, 2008, "Action, Comédie, Drame", "Un super-héros impopulaire fait appel à un expert en relations publiques pour améliorer son image.", "Will Smith, Charlize Theron, Jason Bateman", "Peter Berg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Dark Knight, Le Chevalier Noir", "../img/Covers/movies/the-dark-knight-2008.jpg", 152, 2008, "Action, Crime, Drame", "Batman entreprend de démanteler les dernières organisations criminelles de Gotham. Mais il se heurte bientôt à un nouveau génie du crime qui répand la terreur et le chaos dans la ville : le Joker...", "Christian Bale, Heath Ledger, Aaron Eckhart", "Christopher Nolan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Disco", "../img/Covers/movies/disco-2008.jpg", 103, 2008, "Comédie", "Une bande de quadragénaires nostalgiques de la fièvre du samedi soir décide d'exploiter une boîte de nuit et d'y monter un spectacle disco.", "Franck Dubosc, Emmanuelle Béart, Gérard Depardieu", "Fabien Onteniente");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Mesrine : L'Instinct de mort", "../img/Covers/movies/mesrine-1-2008.jpg", 113, 2008, "Action, Biopic, Crime", "Des années 60 à Paris au début des années 70 au Canada, le parcours criminel hors norme d'un petit voyou de Clichy nommé Jacques Mesrine.", "Vincent Cassel, Cécile de France, Gérard Depardieu", "Jean-François Richet");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Iron Man", "../img/Covers/movies/iron-man-2008.jpg", 126, 2008, "Action, Aventure, Science-fiction", "Le magnat de l'industrie, Tony Stark, lutte contre le mal. Pour cela, il est paré d'une armure hors du commun.", "Robert Downey Jr., Gwyneth Paltrow, Terrence Howard", "Jon Favreau");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Sex and the City", "../img/Covers/movies/sex-and-the-city-2008.jpg", 145, 2008, "Comédie, Romance", "Quatre ans ont passé... Carrie, Samantha, Charlotte et Miranda vivent toujours à New York et sont restées d'inséparables amies. Même si leurs folles années sont derrière elles, les quatre copines de 'Sex & the City' continuent de parler haut et fort de ce qui les tracasse : les relations amoureuses. Carrière, amour, réussite, maternité, sexe ou encore engagement... toutes ont vécu une évolution capitale.", "Sarah Jessica Parker, Kim Cattrall, Cynthia Nixon", "Michael Patrick King");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("High School Musical 3 : nos années lycée", "../img/Covers/movies/high-school-musical-3-2008.jpg", 112, 2008, "Comédie, Famille", "La suite des aventures des collégiens du lycée Wildcats, alors qu'ils approchent de la majorité. Le bal de promo n'est pas loin...", "Zac Efron, Vanessa Hudgens, Ashley Tisdale", "Kenny Ortega");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Vicky Cristina Barcelona", "../img/Covers/movies/vicky-cristina-barcelona-2008.jpg", 96, 2008, "Comédie, Drame, Romance", "Un peintre entretient une liaison avec deux touristes américaines, ce qui attise la jalousie de son ex-petite amie.", "Rebecca Hall, Scarlett Johansson, Javier Bardem", "Woody Allen");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Paris", "../img/Covers/movies/paris-2008.jpg", 130, 2008, "Comédie, Drame, Romance", "Un Parisien est malade et se demande s'il va mourir. Son état lui donne un regard neuf et différent sur tous les gens qu'il croise. Le fait d'envisager la mort met soudainement en valeur la vie, la vie des autres et celle de la ville toute entière...", "Fabrice Luchini, Romain Duris, Joffrey Platel", "Cédric Klapisch");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Horton", "../img/Covers/movies/horton-2008.jpg", 86, 2008, "Animation, Aventure, Comédie", "Horton l'éléphant entend un beau jour un appel au secours venant d'une minuscule poussière flottant dans les airs. Convaincu de la présence de vie sur cette poussière, il décide de venir en aide à ses habitants...", "Jim Carrey, Steve Carell, Carol Burnett", "Jimmy Hayward, Steve Martino");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Faubourg 36", "../img/Covers/movies/faubourg-36-2008.jpg", 120, 2008, "Drame, Musical", "En 1935, quatre chômeurs, qui veulent devenir artistes, reprennent un music-hall à la dérive. Leur rêve : que leurs enfants, à l'approche des congés payés, puissent eux aussi voir la mer.", "Gérard Jugnot, Clovis Cornillac, Kad Merad", "Christophe Barratier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Jumper", "../img/Covers/movies/jumper-2008.jpg", 88, 2008, "Action, Aventure, Science-fiction", "David Rice a toujours cru qu'il était un jeune homme comme les autres. Jusqu'à ce qu'il découvre, un jour où il frôle la mort, qu'il est doué d'un fantastique pouvoir... David est un Jumper, ce qui lui permet de se téléporter dans n'importe quel endroit.", "Hayden Christensen, Samuel L. Jackson, Jamie Bell", "Doug Liman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Largo Winch", "../img/Covers/movies/largo-winch-2008.jpg", 108, 2008, "Action, Thriller", "Les aventures de Largo Winch, un jeune héritier milliardaire fraîchement plongé dans les méandres de la haute finance.", "Tomer Sisley, Kristin Scott Thomas, Predrag 'Miki' Manojlovic", "Jérôme Salle");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ca$h", "../img/Covers/movies/cash-2008.jpg", 100, 2008, "Comédie, Crime, Thriller", "Un arnaqueur évite les agressifs. Et encore plus les agressifs armés. Solal a oublié cette règle. Résultat, il est mort, assassiné par un mauvais perdant. L'affaire aurait dû en rester là. Mais Solal a un frère : Cash. Et Cash a décidé de le venger.", "Jean Dujardin, Jean Reno, Valeria Golino", "Éric Besnard");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Wanted : choisis ton destin", "../img/Covers/movies/wanted-2008.jpg", 110, 2008, "Action, Crime, Thriller", "A la mort de son père, un jeune homme découvre que ce dernier était un assassin professionnel. Il est alors recruté par son agence pour reprendre le flambeau...", "Angelina Jolie, James McAvoy, Morgan Freeman", "Timur Bekmambetov");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Agathe Cléry", "../img/Covers/movies/agathe-clery-2008.jpg", 113, 2008, "Comédie", "Le pire cauchemar pour une raciste ? Devenir aussi noire que les personnes qu'elle méprise. C'est exactement ce qui arrive à Agathe Cléry : elle est frappée par un trouble rare des glandes corticosurrénales qui fait que la peau s'assombrit inexorablement...", "Valérie Lemercier, Anthony Kavanagh, Dominique Lavanant", "Étienne Chatiliez");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Burn After Reading", "../img/Covers/movies/burn-after-reading-2008.jpg", 96, 2008, "Comédie", "Un disque dur contenant des informations mystérieuses d'un agent de la CIA se retrouve entre les mains de deux employés de salle de sport sans scrupules et stupides qui tentent de le vendre.", "Brad Pitt, Frances McDormand, George Clooney", "Ethan Coen, Joel Coen");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("L'Incroyable Hulk", "../img/Covers/movies/lincroyable-hulk-2008.jpg", 112, 2008, "Action, Aventure, Science-fiction", "De retour à la civilisation, notre brillant docteur est poursuivi par l'Abomination, un monstre démoniaque faite d'adrénaline et d'agressivité et dont les pouvoirs concordent avec ceux de Hulk. Une lutte digne des plus grands comics...", "Edward Norton, Liv Tyler, Tim Roth", "Louis Leterrier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Taken", "../img/Covers/movies/taken-2008.jpg", 90, 2008, "Action, Crime, Thriller", "Que peut-on imaginer de pire pour un père que d'assister impuissant à l'enlèvement de sa fille via un téléphone portable ? C'est le cauchemar vécu par Bryan, ancien agent des services secrets américains, qui n'a que quelques heures pour sauver Kim.", "Liam Neeson, Maggie Grace, Famke Janssen", "Pierre Morel");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("L'Âge de glace 3 - Le Temps des dinosaures", "../img/Covers/movies/l-age-de-glace-3-2009.jpg", 94, 2009, "Animation, Aventure, Comédie", "Les héros de 'L'Age de glace' sont de retour pour une nouvelle aventure hilarante, où une maladresse de Sid le paresseux va les propulser au coeur d'un monde étrange perdu sous la glace, et peuplé de dinosaures !", "Ray Romano, John Leguizamo, Denis Leary", "Carlos Saldanha, Michael Thurmeier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Harry Potter et le Prince de sang mêlé", "../img/Covers/movies/harry-potter-6-2009.jpg", 153, 2009, "Aventure, Fantastique", "Alors qu'Harry Potter débute sa sixième année à l'école des sorciers, il découvre un mystérieux grimoire sur lequel est inscrit 'ce livre appartient au prince de sang-mêlé'. C'est ainsi qu'Harry en apprend plus sur le sombre passé de Voldemort...", "Daniel Radcliffe, Emma Watson, Rupert Grint", "David Yates");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Là-haut", "../img/Covers/movies/la-haut-2009.jpg", 96, 2009, "Animation, Aventure, Famille", "Carl Fredricksen, vendeur de ballons à la retraite, s’envole vers le ciel en attachant des milliers de ballons à sa maison. Un jeune garçon nommé Russell devient un passager clandestin de son voyage.", "Edward Asner, Jordan Nagai, John Ratzenberger", "Pete Docter, Bob Peterson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Twilight, chapitre I : Fascination", "../img/Covers/movies/twilight-1-2008.jpg", 130, 2008, "Fantastique, Romance", "Lorsque Bella Swan déménage dans une petite ville dans l'Etat de Washington, elle tombe amoureuse d'Edward Cullen, un camarade de classe mystérieux qui se révèle être un vampire de 108 ans.", "Kristen Stewart, Robert Pattinson, Billy Burke", "Catherine Hardwicke");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Coco", "../img/Covers/movies/coco-2009.jpg", 95, 2009, "Comédie", "Coco est l'exemple parfait de la réussite sociale. Parti de rien, immigré, il a réalisé en 15 ans une des plus fulgurantes success story des temps modernes. Mais pour Coco, la plus grande consécration est à venir : la bar-mitsva de son fils Samuel...", "Gad Elmaleh, Pascale Arbillot, Jean Benguigui", "Gad Elmaleh");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Volt, star malgré lui", "../img/Covers/movies/volt-2008.jpg", 96, 2008, "Animation, Aventure, Comédie", "Volt, un chien star des plateaux de télévision à Hollywood, est envoyé accidentellement à New York où sa rencontre avec un chat et un hamster va lui faire prendre conscience des réalités de la vie.", "John Travolta, Miley Cyrus, Susie Essman", "Byron Howard, Chris Williams");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Inglourious Basterds", "../img/Covers/movies/inglourious-basterds-2009.jpg", 153, 2009, "Aventure, Drame, Guerre", "Dans la France occupée de 1940, Shosanna assiste à l'exécution de sa famille tombée entre les mains d'un colonel nazi. Au même moment, le lieutenant Raine forme un groupe de soldats juifs américains pour mener des actions punitives contre les nazis.", "Brad Pitt, Diane Kruger, Eli Roth", "Quentin Tarantino");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("L'Etrange histoire de Benjamin Button", "../img/Covers/movies/benjamin-button-2009.jpg", 175, 2009, "Drame, Fantastique, Romance", "'Curieux destin que le mien...' Ainsi commence l'étrange histoire de Benjamin Button, cet homme qui naquit à 80 ans et vécut sa vie à l'envers, sans pouvoir arrêter le cours du temps.", "David Fincher, Kathleen Kennedy, Joshua Donen, Frank Marshall", "David Prior");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Neuilly sa mère!", "../img/Covers/movies/neuilly-sa-mere-2009.jpg", 90, 2009, "Comédie", "Sami, 14 ans, vivait heureux avec ses copains dans son HLM de Chalon-sur-Saône. Hélas, le destin l'arrache un jour à son paradis et le propulse dans l'enfer d'une famille bourgeoise de Neuilly-sur-Seine !", "Samy Seghir, Jérémy Denisty, Rachida Brakni", "Gabriel Julien-Laferrière");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("OSS 117 : Rio ne répond plus", "../img/Covers/movies/oss-117-2-2009.jpg", 101, 2009, "Action, Aventure, Comédie", "En 1967, douze ans après ses premières aventures au Caire, l'espion Hubert Bonisseur de la Bath, alias OSS 117, est envoyé en mission à Rio de Janeiro pour récupérer un microfilm détenu par un ancien dignitaire nazi...", "Jean Dujardin, Louise Monot, Rüdiger Vogler", "Michel Hazanavicius");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Transformers 2: la Revanche", "../img/Covers/movies/transformers-2-2009.jpg", 149, 2009, "Action, Aventure, Science-fiction", "La guerre n'est pas terminée entre Autobots et Decepticons... De nouveaux robots se lancent dans la bataille.", "Shia LaBeouf, Megan Fox, Josh Duhamel", "Michael Bay");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Anges et démons", "../img/Covers/movies/anges-et-demons-2009.jpg", 140, 2009, "Fantastique, Thriller", "La première enquête de Robert Langdon, éminent spécialiste de l'étude des symboles. Approché par le Vatican, il doit déjouer un complot terroriste mené par les Illuminati.", "Tom Hanks, Ewan McGregor, Stellan Skarsgård", "Ron Howard");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Very Bad Trip", "../img/Covers/movies/very-bad-trip-2009.jpg", 100, 2009, "Comédie", "Au réveil d'un enterrement de vie de garçon bien arrosé, les trois amis du fiancé se rendent compte qu'il a disparu 40 heures avant la cérémonie de mariage. Ils vont alors devoir faire fi de leur gueule de bois pour comprendre ce qui s'est passé...", "Zach Galifianakis, Bradley Cooper, Justin Bartha", "Todd Phillips");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Safari", "../img/Covers/movies/safari-2009.jpg", 98, 2009, "Aventure, Comédie", "A Nairobi, Richard Dacier, dirigeant d'une petite société proposant des safaris, perd au poker contre la mafia locale, laquelle n'hésite pas à nourrir les crocodiles avec les mauvais payeurs. Pour honorer sa dette de jeu, le voilà contraint par un certain monsieur Charles de convoyer une mallette jusqu'à la frontière somalienne, à bord d'un car de touristes français, en guise de couverture. Seul hic, Richard Dacier a peur de la brousse. Il n'y a pas mis les pieds depuis plus de trente ans .", "Kad Merad, Lionel Abelanski, Valérie Benguigui", "Olivier Baroux");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("X-Men Origins: Wolverine", "../img/Covers/movies/x-men-origins-wolverine-2009.jpg", 107, 2009, "Action, Science-fiction", "Ce film nous fait découvrir les origines du plus rebelle des héros Marvel et son histoire avant les événements de la trilogie 'X-Men'. Hugh Jackman reprend son rôle fétiche de héros au pouvoir de régénération convoité par l'armée...", "Hugh Jackman, Liev Schreiber, Ryan Reynolds", "Gavin Hood");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Lucky Luke", "../img/Covers/movies/lucky-luke-2009.jpg", 103, 2009, "Aventure, Comédie, Western", "Au cours de sa mission à Daisy Town, la ville qui l'a vu grandir, Lucky Luke, 'l'homme qui tire plus vite que son ombre', va croiser Billy The Kid, Calamity Jane, Pat Poker, Jesse James et Belle...", "Jean Dujardin, Michaël Youn, Sylvie Testud", "James Huth");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La première étoile", "../img/Covers/movies/la-premiere-etoile-2009.jpg", 90, 2009, "Comédie", "Jean-Gabriel, marié et père de trois enfants, vit de petits boulots et passe son temps au bar PMU du coin. Un jour, pour faire plaisir à sa fille, il promet un peu vite à toute la famille de les emmener en vacances au ski...", "Firmine Richard, Lucien Jean-Baptiste, Anne Consigny", "Lucien Jean-Baptiste");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le code a changé", "../img/Covers/movies/le-code-a-change-2009.jpg", 100, 2009, "Comédie, Drame", "Un dîner, c'est la dictature de l'apparence : on se fait beau, on rit, on raconte, on frime, on partage souvenirs et projets. Les angoisses sont cachées sous l'humour et les chagrins étouffés par les éclats de rire. Pour quelques heures on y croit...", "Karin Viard, Dany Boon, Marina Foïs", "Danièle Thompson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Nuit au musée 2", "../img/Covers/movies/la-nuit-au-musee-2-2009.jpg", 105, 2009, "Aventure, Comédie, Famille", "Larry et ses amis vont devoir contrer les plans maléfiques du pharaon Kahmunrah et de ses acolytes Al Capone, Ivan le Terrible et Napoléon.", "Ben Stiller, Owen Wilson, Amy Adams", "Shawn Levy");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Concert", "../img/Covers/movies/le-concert-2009.jpg", 119, 2009, "Comédie, Drame, Musical", "Sous Brejnev, Andrei Filipov était le plus grand chef d'orchestre d'URSS. Mais après avoir refusé de se séparer de ses musiciens juifs, il a été licencié. 30 ans après, il travaille toujours au Bolchoï mais comme homme de ménage...", "Aleksey Guskov, Mélanie Laurent, Dmitriy Nazarov", "Radu Mihaileanu");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Public Enemies", "../img/Covers/movies/public-enemies-2009.jpg", 140, 2009, "Biopic, Policier, Drame", "Basé sur l'histoire vraie de John Dillinger, un braqueur de banque hors pair qui a sévi à de nombreuses reprises dans l'Amérique des années 30.", "Christian Bale, Johnny Depp, Christian Stolte", "Michael Mann");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Terminator Renaissance", "../img/Covers/movies/terminator-renaissance-2009.jpg", 115, 2009, "Action, Aventure, Science-fiction", "En 2018, John Connor, âgé de 30 ans, leader charismatique de la résistance humaine face aux machines, mène une lutte à mort pour sauver ce qui reste de l'Humanité...", "Christian Bale, Sam Worthington, Anton Yelchin", "McG");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Drôle de Noël de Scrooge", "../img/Covers/movies/le-drole-de-noel-de-scrooge-2009.jpg", 96, 2009, "Animation, Aventure", "Une nouvelle adaptation de 'Un Chant de Noël', le célèbre conte de Charles Dickens qui raconte l'histoire d'Ebenezer Scrooge, un vieil homme bougon et avare qui a consacré sa vie à accumuler des richesses...", "Jim Carrey, Gary Oldman, Colin Firth", "Robert Zemeckis");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Incognito", "../img/Covers/movies/incognito-2009.jpg", 93, 2009, "Comédie", "Lucas est devenu une superstar en s'étant approprié les chansons d'un ami qu'il croyait disparu. Soudainement, cet ami réapparait. Lucas, pour lui cacher sa fortune et sa célébrité, commet l'erreur de demander à Francis, un comédien raté, de prendre sa place.", "Bénabar, Franck Dubosc, Jocelyn Quivrin", "Eric Lavaine");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Welcome", "../img/Covers/movies/welcome-2009.jpg", 110, 2009, "Drame", "Pour impressionner et reconquérir sa femme, Simon, maître nageur à la piscine de Calais, prend le risque d'aider en secret un jeune réfugié kurde qui veut traverser la Manche à la nage.", "Vincent Lindon, Firat Ayverdi, Audrey Dana", "Philippe Lioret");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("District 9", "../img/Covers/movies/district-9-2009.jpg", 112, 2009, "Action, Science-fiction, Thriller", "Il y a 28 ans, des extraterrestres entrèrent en contact avec la Terre... Ces 'visiteurs' étaient des réfugiés et furent installés dans le District 9, en Afrique du Sud, pendant que les nations du monde se querellaient pour savoir quoi en faire...", "Sharlto Copley, David James, Jason Cope", "Neill Blomkamp");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Avatar", "../img/Covers/movies/avatar-2009.jpg", 162, 2009, "Action, Aventure", "Sur la lointaine planète de Pandora, Jake Sully, un héros malgré lui, se lance dans une quête de rédemption, de découverte, d'amour inattendu, dont l'issue sera un combat héroïque pour sauver toute une civilisation.", "Sam Worthington, Zoe Saldana, Sigourney Weaver", "James Cameron");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Harry Potter et les reliques de la mort - partie 1", "../img/Covers/movies/harry-potter-7-1-2010.jpg", 146, 2010, "Aventure, Fantastique", "Le pouvoir de Voldemort s'étend. Celui-ci contrôle maintenant le Ministère de la Magie et Poudlard. Harry, Ron et Hermione décident de terminer le travail commencé par Dumbledore, et de retrouver les derniers Horcruxes pour vaincre le Seigneur des Ténèbres. Mais il reste bien peu d'espoir aux trois sorciers, qui doivent réussir à tout prix.", "Daniel Radcliffe, Emma Watson, Rupert Grint", "David Yates");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les petits mouchoirs", "../img/Covers/movies/les-petits-mouchoirs-2010.jpg", 154, 2010, "Comédie, Drame", "A la suite d'un événement bouleversant, une bande de copains décide, malgré tout, de partir en vacances au bord de la mer comme chaque année. Leur amitié, leurs certitudes, leur culpabilité, leurs amours en seront ébranlées.", "François Cluzet, Marion Cotillard, Benoît Magimel", "Guillaume Canet");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Inception", "../img/Covers/movies/inception-2010.jpg", 148, 2010, "Action, Aventure, Science-fiction", "Au lieu de subtiliser un rêve, un voleur expérimenté et son équipe doivent faire l'inverse : implanter une idée dans l'esprit d'un individu. S'ils y parviennent, il pourrait s'agir du crime parfait ...", "Leonardo DiCaprio, Joseph Gordon-Levitt, Elliot Page", "Christopher Nolan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Shrek 4, il était une fin", "../img/Covers/movies/shrek-4-2010.jpg", 93, 2010, "Animation, Aventure, Comédie", "Que serait devenu le royaume de Fort, Fort Lointain si Shrek n'avait pas existé ? C'est ce que s'apprête à vivre l'ogre vert après avoir signé un pacte avec le beau-parleur démoniaque Rumpelstiltskin alias Tracassin...", "Mike Myers, Cameron Diaz, Eddie Murphy", "Mike Mitchell");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Toy Story 3", "../img/Covers/movies/toy-story-3-2010.jpg", 103, 2010, "Animation, Aventure, Comédie", "Woody le cowboy, Buzz l'Eclair et les autres jouets se retrouvent confrontés à ce qui devait bien arriver un jour : le départ d'Andy pour l'université...", "Tom Hanks, Tim Allen, Joan Cusack", "Lee Unkrich");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Camping 2", "../img/Covers/movies/camping-2-2010.jpg", 99, 2010, "Comédie", "Jean-Pierre Savelli, 45 ans, cadre moyen dans une mutuelle d'assurances de Clermont-Ferrand traverse une passe difficile. Il décide de partir en vacances dans un camping, le Camping des Flots Bleus près d'Arcachon où il retrouve les habitués...", "Franck Dubosc, Mathilde Seigner, Claude Brasseur", "Fabien Onteniente");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Twilight - Chapitre III : hésitation", "../img/Covers/movies/twilight-3-2010.jpg", 124, 2010, "Fantastique, Romance", "Des morts suspectes dans la région de Seattle laissent présager une nouvelle menace d'autant que Victoria cherche toujours à assouvir sa vengeance contre Bella. Pour contrer cette menace, les Cullen et les Quileutes vont devoir se rapprocher...", "Kristen Stewart, Robert Pattinson, Taylor Lautner", "David Slade");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Shutter Island", "../img/Covers/movies/shutter-island-2010.jpg", 138, 2010, "Mystère, Thriller", "1954. Teddy Daniels et Chuck Aule enquêtent sur la disparition d'une patiente échappée d'un hôpital psychiatrique à sécurité maximale. L'hôpital en question est basé sur Shutter Island, une île située à environ une heure au large de Boston.", "Leonardo DiCaprio, Emily Mortimer, Mark Ruffalo", "Martin Scorsese");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Moi, moche et méchant", "../img/Covers/movies/moi-moche-et-mechant-2010.jpg", 95, 2010, "Animation, Aventure, Comédie", "Dans un charmant quartier résidentiel délimité par des clôtures de bois blanc et orné de rosiers fleurissants se dresse une bâtisse noire entourée d’une pelouse en friche. Cette façade sinistre cache un secret : Gru, un méchant vilain, entouré d’une myriade de sous-fifres et armé jusqu’aux dents, qui, à l’insu du voisinage, complote le plus gros casse de tous les temps : voler la lune (Oui, la lune !)...", "Steve Carell, Jason Segel, Russell Brand", "Pierre Coffin, Chris Renaud");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Iron Man 2", "../img/Covers/movies/iron-man-2-2010.jpg", 124, 2010, "Action, Science-fiction", "Le monde sait que l'inventeur Tony Stark et Iron Man ne font qu'un. Malgré la pression du gouvernement, de la presse et du public pour qu'il partage sa technologie avec l'armée, Tony n'est pas disposé à divulguer les secrets de son armure...", "Robert Downey Jr., Mickey Rourke, Gwyneth Paltrow", "Jon Favreau");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Sherlock Holmes", "../img/Covers/movies/sherlock-holmes-2010.jpg", 89, 2010, "Action, Aventure", "Après qu'une série de meurtres rituels a ensanglanté Londres, Holmes et Watson réussissent à intercepter le coupable : Lord Blackwood. A l'approche de son éxécution, ce sinistre adepte de la magie noire annonce qu'il reviendra du royaume des morts pour exercer la plus terrible des vengeances.", "Ben Syder, Gareth David-Lloyd, Dominic Keating, William Huw", "Rachel Lee Goldenberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Prince of Persia : les sables du temps", "../img/Covers/movies/prince-of-persia-2010.jpg", 116, 2010, "Action, Aventure", "Dastan, un jeune prince de la Perse du VIe siècle va devoir unir ses forces à celles de la belle et courageuse princesse Tamina pour empêcher un redoutable noble de s'emparer des Sables du Temps...", "Jake Gyllenhaal, Gemma Arterton, Ben Kingsley", "Mike Newell");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Salt", "../img/Covers/movies/salt-2010.jpg", 100, 2010, "Action, Thriller", "Evelyn Salt, un agent de la CIA, est dénoncée par un déserteur comme étant une 'taupe' russe. Salt doit alors échapper à la capture de ses supérieurs afin de prouver son innocence.", "Angelina Jolie, Liev Schreiber, Chiwetel Ejiofor", "Phillip Noyce");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Social Network", "../img/Covers/movies/the-social-network-2010.jpg", 120, 2010, "Biopic, Drame", "Mark Zuckerberg, étudiant à Harvard, crée le site de réseau social qui deviendra Facebook, mais il se retrouve poursuivi en justice par deux frères qui l'accusent d'avoir volé leur idée ainsi que par le co-fondateur de Facebook qui fut par la suite évincé de l'entreprise.", "Jesse Eisenberg, Andrew Garfield, Justin Timberlake", "David Fincher");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Sex and the City 2", "../img/Covers/movies/sex-and-the-city-2-2010.jpg", 146, 2010, "Comédie, Drame, Romance", "Tout en luttant contre les pressions de la vie, de l'amour et du travail à Manhattan, Carrie, Miranda et Charlotte rejoignent Samantha pour un voyage à Abu Dhabi (Emirats arabes unis), où l'ex de Samantha tourne un nouveau film.", "Sarah Jessica Parker, Kim Cattrall, Kristin Davis", "Michael Patrick King");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Karate Kid", "../img/Covers/movies/the-karate-kid-2010.jpg", 140, 2010, "Action, Drame", "Le travail amène une mère célibataire à déménager en Chine avec son jeune fils ; dans sa nouvelle maison, le garçon devient adepte de kung-fu, enseigné par un maître.", "Jackie Chan, Jaden Smith, Taraji P. Henson", "Harald Zwart");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Percy Jackson: Le voleur de foudre", "../img/Covers/movies/percy-jackson-le-voleur-de-foudre-2010.jpg", 118, 2010, "Action, Aventure, Fantastique", "Un jeune homme découvre qu'il est le descendant d'un dieu grec et s'embarque, avec l'aide d'un satyre et de la fille d'Athena, dans une dangereuse aventure pour résoudre une guerre entre dieux. Sur sa route, il devra affronter une horde d'ennemis...", "Logan Lerman, Kevin McKidd, Steve Coogan", "Chris Columbus");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Fatal", "../img/Covers/movies/fatal-2010.jpg", 95, 2010, "Comédie, Musical", "Fatal Bazooka, c'est un rappeur créé par Michaël Youn dans son 'Morning Live', puis développé dans l'album 'T'as vu' vendu à plus de 500 000 exemplaires. Ce film raconte ce que serait devenu ce rappeur s'il en avait vendu... 15 millions !", "Michaël Youn, Stéphane Rousseau, Isabelle Funaro", "Michaël Youn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Gainsbourg (Vie héroïque)", "../img/Covers/movies/gainsbourg-vie-heroique-2010.jpg", 135, 2010, "Biopic, Drame, Musical", "La vie de Gainsbourg, du jeune Lucien Ginsburg dans le Paris occupé des années 1940, jusqu'au poète, compositeur et chanteur célébré dans le monde entier.", "Eric Elmosnino, Lucy Gordon, Laetitia Casta", "Joann Sfar");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Valentine's Day", "../img/Covers/movies/valentines-day-2010.jpg", 125, 2010, "Comédie, Romance", "Les destins croisés de couples qui se séparent ou se retrouvent, de célibataires qui se rencontrent à Los Angeles, le jour de Saint-Valentin...", "Julia Roberts, Jamie Foxx, Anne Hathaway", "Garry Marshall");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Town", "../img/Covers/movies/the-town-2010.jpg", 125, 2010, "Crime, Drame, Thriller", "A Boston, dans le quartier de Charlestown, un gang de braqueurs de banques nargue le FBI, jusqu'au jour où l'amour et l'appât du gain bouleversent tout.", "Ben Affleck, Rebecca Hall, Jon Hamm", "Ben Affleck");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Ghost Writer", "../img/Covers/movies/the-ghost-writer-2010.jpg", 128, 2010, "Crime, Drame, Mystère", "Alors qu'il est engagé pour finir de rédiger les mémoires d'un ancien Premier Ministre britannique, un écrivain va découvrir des secrets qui vont mettre sa propre vie en danger.", "Ewan McGregor, Pierce Brosnan, Olivia Williams", "Roman Polanski");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Intouchables", "../img/Covers/movies/intouchables-2011.jpg", 112, 2011, "Biopic, Comédie, Drame", "La rencontre improbable, touchante et drôle entre un riche aristocrate, tétraplégique depuis un accident de parapente, et un jeune de banlieue engagé par hasard pour être son aide à domicile...", "François Cluzet, Omar Sy, Anne Le Ny", "Olivier Nakache, Éric Toledano");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Harry Potter et les reliques de la mort - partie 2", "../img/Covers/movies/harry-potter-7-2-2011.jpg", 130, 2011, "Aventure, Famille, Fantastique", "Dans la 2e Partie de cet épisode final, le combat entre les puissances du bien et du mal de l’univers des sorciers se transforme en guerre sans merci. Les enjeux n’ont jamais été si considérables et personne n’est en sécurité. Mais c’est Harry Potter qui peut être appelé pour l’ultime sacrifice alors que se rapproche l’ultime épreuve de force avec Voldemort.", "Daniel Radcliffe, Emma Watson, Rupert Grint", "David Yates");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Aventures de Tintin : Le Secret de la Licorne", "../img/Covers/movies/les-aventures-de-tintin-le-secret-de-la-licorne-2011.jpg", 107, 2011, "Action, Aventure, Animation, Famille", "Parce qu’il achète la maquette d’un bateau appelé la Licorne, Tintin, un jeune reporter, se retrouve entraîné dans une fantastique aventure à la recherche d’un fabuleux secret...", "Jamie Bell, Andy Serkis, Daniel Craig", "Steven Spielberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Twilight - Chapitre IV : Révélation 1ère partie", "../img/Covers/movies/twilight-4-1-2011.jpg", 117, 2011, "Fantastique, Romance", "Bella a fait son choix : elle s’apprête à épouser Edward. Mais le jeune homme honorera-t-il sa part du marché ? Acceptera-t-il de la transformer en vampire et de la voir renoncer à sa vie humaine ?", "Kristen Stewart, Robert Pattinson, Taylor Lautner", "Bill Condon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Cars 2", "../img/Covers/movies/cars-2-2011.jpg", 106, 2011, "Animation, Aventure, Comédie", "Flash McQueen et Martin reviennent sur le devant de la scène, pour faire du monde une piste de circuit. Mais ils auront bien besoin de leur passeport, lorsqu'ils vont se trouver au coeur d'une intrigue qui va les mener aux quatre coins du monde.", "Owen Wilson, Larry the Cable Guy, Michael Caine", "John Lasseter, Bradford Lewis");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Schtroumpfs", "../img/Covers/movies/les-schtroumpfs-2011.jpg", 103, 2011, "Animation, Aventure, Comédie", "Propulsés en plein New York grâce à une faille spatio-temporelle, les Schtroumpfs ont, pour un temps, échappé à l'horrible sorcier Gargamel.", "Hank Azaria, Katy Perry, Jonathan Winters", "Raja Gosnell");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Kung Fu Panda 2", "../img/Covers/movies/kung-fu-panda-2-2011.jpg", 90, 2011, "Aventure, Animation, Famille", "Le rêve de Po s'est réalisé. Il est devenu le Guerrier Dragon, qui protège la Vallée de la Paix avec ses amis les Cinq Cyclones : Tigresse, Grue, Mante, Vipère et Singe. Mais cette vie topissime est menacée par un nouvel ennemi...", "Jack Black, Angelina Jolie, Jackie Chan", "Jennifer Yuh Nelson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Transformers 3 - La Face cachée de la Lune", "../img/Covers/movies/transformers-3-2011.jpg", 154, 2011, "Action, Aventure, Science-fiction", "Un événement mystérieux lié à notre passé éclate au grand jour. C’est la guerre qui menace aujourd’hui notre Terre ; une guerre d’une telle ampleur que l’aide des Transformers pourrait, cette fois, ne pas suffire à nous sauver.", "Shia LaBeouf, Rosie Huntington-Whiteley, Tyrese Gibson", "Michael Bay");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Rio", "../img/Covers/movies/rio-2011.jpg", 96, 2011, "Animation, Aventure, Comédie", "Blu, un perroquet bleu d’une espèce très rare, quitte sa petite ville sous la neige et le confort de sa cage pour s’aventurer au cœur des merveilles exotiques de Rio de Janeiro.", "Jesse Eisenberg, Anne Hathaway, George Lopez", "Carlos Saldanha");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Polisse", "../img/Covers/movies/polisse-2011.jpg", 127, 2011, "Crime, Drame", "Le quotidien des policiers de la BPM ce sont les gardes à vue de pédophiles, les arrestations de pickpockets mineurs mais aussi la pause déjeuner où l’on se raconte ses problèmes de couple, la solidarité entre collègues, les fous rires incontrôlables...", "Karin Viard, JoeyStarr, Marina Foïs", "Maïwenn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("X-Men: Le Commencement", "../img/Covers/movies/x-men-commencement-2011.jpg", 131, 2011, "Action, Science-Fiction", "'X-Men : Le Commencement' nous entraîne aux origines de la saga X-Men, révélant une histoire secrète autour des événements majeurs du XXe siècle.", "Scott Porter, Steve Blum, Jennifer Hale", "Matthew Vaughn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Hollywoo", "../img/Covers/movies/hollywoo-2011.jpg", 107, 2011, "Comédie", "Jeanne est la doubleuse française d’une actrice américaine qui joue dans une série télé à succès. Mais le jour où l’actrice américaine pète les plombs et annonce la fin de sa carrière, la vie de Jeanne bascule à son tour… A moins que...", "Florence Foresti, Jamel Debbouze, Nikki Deloach", "Frédéric Berthe, Pascal Serieis");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Thor", "../img/Covers/movies/thor-2011.jpg", 115, 2011, "Action, Fantastique", "Thor est un guerrier Marvel tout-puissant et arrogant, banni du Royaume mythique d’Asgard et condamné à vivre parmi les humains. Lorsque les forces du mal s’apprêtent à se déchaîner sur la Terre, Thor va apprendre à se comporter en véritable héros…", "Chris Hemsworth, Anthony Hopkins, Natalie Portman", "Kenneth Branagh");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Artist", "../img/Covers/movies/the-artist-2011.jpg", 100, 2011, "Comédie, Drame, Romance", "Hollywood 1927. George Valentin est une vedette du cinéma muet à qui tout sourit. L'arrivée des films parlants va le faire sombrer dans l'oubli. Peppy Miller, jeune figurante, va elle, être propulsée au firmament des stars. Ce film raconte l'histoire de leurs destins croisés, ou comment la célébrité, l'orgueil et l'argent peuvent être autant d'obstacles à leur histoire d'amour.", "Jean Dujardin, Bérénice Bejo, John Goodman", "Michel Hazanavicius");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Drive", "../img/Covers/movies/drive-2011.jpg", 100, 2011, "Action, Thriller", "Un cascadeur tranquille et anonyme se métamorphose dès que la nuit tombe : il devient pilote de voitures pour le compte de la mafia. La combine est bien rodée jusqu’au jour où l'un des casses tourne mal et l’entraîne dans une course-poursuite infernale.", "Ryan Gosling, Carey Mulligan, Bryan Cranston", "Nicolas Winding Refn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Super 8", "../img/Covers/movies/super-8-2011.jpg", 112, 2011, "Science Fiction", "Été 1979, une petite ville de l’Ohio. Alors qu'ils tournent un film en super 8, un groupe d’adolescents est témoin d'une spectaculaire catastrophe ferroviaire. Ils ne tardent pas à comprendre qu'il ne s'agit pas d'un accident.", "Elle Fanning, AJ Michalka, Kyle Chandler", "J.J. Abrams");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Bienvenue à bord", "../img/Covers/movies/bienvenue-a-bord-2011.jpg", 95, 2011, "Comédie", "Isabelle, DRH d’une grande compagnie maritime, a commis l'erreur de choisir pour amant son patron. Avant d'embarquer pour la croisière inaugurale du fleuron de la flotte, il décide de la débarquer de sa vie et de son boulot...", "Franck Dubosc, Valérie Lemercier, Gérard Darmon", "Eric Lavaine");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("X-Men 2", "../img/Covers/movies/x-men-2-2003.jpg", 134, 2003, "Action, Science Fiction", "Les mutants font face à une nouvelle menace : alors que la polémique fait rage autour d'un meurtre commis par l'un d'eux, le mouvement anti-mutants lance une attaque contre leur école. Magnéto se joint aux X-Men dans ce combat pour leur survie...", "Patrick Stewart, Hugh Jackman, Halle Berry", "Bryan Singer");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("8 Mile", "../img/Covers/movies/8-mile-2002.jpg", 110, 2002, "Drame, Musical", "En 1995, Jimmy Smith Jr., un jeune rebelle blanc de Detroit, accumule les déboires familiaux, professionnels et sentimentaux. Après une première humiliation dans une joute oratoire entre rappeurs, il tente de prendre sa revanche...", "Eminem, Brittany Murphy, Kim Basinger", "Curtis Hanson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Choristes", "../img/Covers/movies/les-choristes-2004.jpg", 97, 2004, "Drame, Musical", "En 1948, M. Mathieu, professeur de musique, devient surveillant dans un internat de rééducation pour mineurs et transforme le quotidien des pensionnaires en les familiarisant à la magie du chant.", "Gérard Jugnot, François Berléand, Jean-Baptiste Maunier", "Christophe Barratier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Mr. et Mrs. Smith", "../img/Covers/movies/mr-et-mrs-smith-2005.jpg", 120, 2005, "Action, Comédie, Romance", "Ils sont mari et femme... tueurs à gages tous les deux... mais ne le savent pas ! Jusqu'au jour où les organisations concurrentes pour lesquelles ils travaillent les engagent pour éliminer la même cible. La scène de ménage sera... explosive !", "Brad Pitt, Angelina Jolie, Adam Brody", "Doug Liman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Aviator", "../img/Covers/movies/the-aviator-2004.jpg", 170, 2004, "Biopic, Drame", "'Aviator' couvre près de vingt ans de la vie tumultueuse d'Howard Hughes, industriel, milliardaire, casse-cou, pionnier de l'aviation civile, inventeur, producteur, réalisateur, directeur de studio et séducteur insatiable.", "Leonardo DiCaprio, Cate Blanchett, Kate Beckinsale", "Martin Scorsese");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Simpson - le film", "../img/Covers/movies/les-simpson-le-film-2007.jpg", 87, 2007, "Animation, Aventure, Comédie", "Coupable d'une faute sur son lieu de travail, Homer Simpson bouleverse l'existence de la petite ville de Springfield. Il est chassé avec sa famille par les habitants...", "Dan Castellaneta, Julie Kavner, Nancy Cartwright", "David Silverman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Die Hard 4 - retour en enfer", "../img/Covers/movies/die-hard-4-2007.jpg", 128, 2007, "Action, Policier, Thriller", "John McClane se trouve confronté à un nouveau genre de terrorisme. Le réseau informatique national est détruit de façon systématique, plongeant le pays dans le chaos. Le cerveau de cette opération a tout calculé à la perfection. Ou presque...", "Bruce Willis, Justin Long, Timothy Olyphant", "Len Wiseman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Madagascar 2", "../img/Covers/movies/madagascar-2-2008.jpg", 89, 2008, "Animation, Aventure, Comédie", "Les anciens pensionnaires du zoo de Central Park rencontrent alors pour la première fois leurs familles respectives dans leur habitat naturel. Une occasion unique de renouer avec ses racines...", "Ben Stiller, Chris Rock, David Schwimmer", "Eric Darnell, Tom McGrath");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Mamma Mia!", "../img/Covers/movies/mamma-mia-2008.jpg", 108, 2008, "Comédie, Musical, Romance", "Une future mariée est à la recherche de son père dont sa mère ne veut pas révéler l'identité. Film adapté de la comédie musicale en hommage au groupe Abba.", "Meryl Streep, Pierce Brosnan, Amanda Seyfried", "Phyllida Lloyd");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Into the Wild", "../img/Covers/movies/into-the-wild-2007.jpg", 148, 2007, " Aventure, Biopic, Drame", "Après avoir obtenu son diplôme de l'Université Emory, Christopher McCandless, étudiant brillant et athlète, abandonne ses biens, donne l'intégralité de ses économies de 24 000 dollars à des œuvres de charité et fait du stop jusqu'en Alaska pour vivre dans la nature sauvage.", "Emile Hirsch, Vince Vaughn, Catherine Keener", "Sean Penn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Twilight, chapitre II : Tentation", "../img/Covers/movies/twilight-2-2009.jpg", 130, 2009, "Aventure, Fantastique, Romance", "Abandonnée par Edward, celui qu'elle aime passionnément, Bella ne s'en relève pas. Comment oublier son amour pour un vampire et revenir à une vie normale ? Pour combler son vide affectif, Bella court après le danger et prends des risques de plus en plus importants.", "Kristen Stewart, Robert Pattinson, Taylor Lautner", "Chris Weitz");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Fast and Furious 4", "../img/Covers/movies/fast-and-furious-4-2009.jpg", 107, 2009, "Action", "Un meurtre oblige Don Toretto, un ancien taulard en cavale, et l'agent Brian O'Conner à revenir à L.A. où leur querelle se rallume. Mais confrontés à un ennemi commun, ils sont contraints à former une alliance incertaine s'ils espèrent parvenir à déjouer ses plans. De l'attaque de convoi aux glissades de précision qui les mèneront hors de leurs propres frontières, les deux hommes trouveront le meilleur moyen de prendre leur revanche : en poussant les limites de ce qui est faisable au volant d'un bolide.", "Vin Diesel, Paul Walker, Michelle Rodriguez", "Justin Lin");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Raiponce", "../img/Covers/movies/raiponce-2010.jpg", 100, 2010, "Animation, Famille", "Lorsque Flynn Rider, le bandit le plus recherché du royaume, se réfugie dans une mystérieuse tour, il se retrouve pris en otage par Raiponce, une belle et téméraire jeune fille à l’impressionnante chevelure de 20 mètres de long, gardée prisonnière par Mère Gothel. L’étonnante geôlière de Flynn cherche un moyen de sortir de cette tour où elle est enfermée depuis des années. Elle passe alors un accord avec le séduisant brigand… ", "Mandy Moore, Zachary Levi, Donna Murphy", "Nathan Greno, Byron Howard");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Dragons", "../img/Covers/movies/dragons-2010.jpg", 98, 2010, "Animation, Action, Aventure", "L'histoire d'Harold, jeune Viking peu à son aise dans sa tribu où combattre les dragons est le sport national. Sa vie va être bouleversée par sa rencontre avec un dragon qui va peu à peu amener Harold et les siens à voir le monde d'un point de vue totalement différent.", "Jay Baruchel, Gerard Butler, Christopher Mintz-Plasse", "Dean DeBlois, Chris Sanders");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Expendables : unité spéciale", "../img/Covers/movies/expendables-2010.jpg", 103, 2010, "Action", "La CIA envoie un groupe de mercenaires en Amérique du Sud, pour y renverser un dictateur.", "Sylvester Stallone, Jason Statham, Jet Li", "Sylvester Stallone");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Rien à déclarer", "../img/Covers/movies/rien-a-declarer-2010.jpg", 108, 2010, "Comédie", "Un douanier belge francophobe se voit contraint de faire équipe avec un Français, au moment de la suppression des frontières franco-belges dans les années 90...", "Benoît Poelvoorde, Dany Boon, Christel Pedrinelli", "Dany Boon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Chat Potté", "../img/Covers/movies/le-chat-potte-2011.jpg", 90, 2011, "Aventure, Animation, Comédie", "C’était bien avant que notre mythique Chat Potté ne croise la route de Shrek… Le légendaire félin, et non moins redoutable amant, s’était alors embarqué dans un périple riche en rebondissements, avec la ravissante et rusée Kitty Pattes de Velours et Humpty Alexandre Dumpty, véritable 'cerveau' de l’opération. Leur objectif : s’emparer de la fameuse Oie aux Œufs d’Or pour sauver la ville où le Chat Potté a grandi.", "Antonio Banderas, Salma Hayek, Zach Galifianakis", "Chris Miller");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Black Swan", "../img/Covers/movies/black-swan-2010.jpg", 108, 2010, "Drame, Thriller", "Rivalités dans la troupe du New York City Ballet. Nina est prête à tout pour obtenir le rôle principal du 'Lac des cygnes' que dirige l’ambigu Thomas. Mais elle se trouve bientôt confrontée à la belle et sensuelle nouvelle recrue, Lily...", "Natalie Portman, Mila Kunis, Vincent Cassel", "Darren Aronofsky");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Fast and Furious 5", "../img/Covers/movies/fast-and-furious-5-2011.jpg", 130, 2011, "Action, Crime, Thriller", "L'ex-agent Brian O'Conner fait à nouveau équipe avec l'ancien taulard Dom Toretto, mais pour la première fois, ils se retrouvent tous deux du mauvais côté de la loi dans cette ultime course-poursuite à hauts risques.", "Vin Diesel, Paul Walker, Dwayne Johnson", "Justin Lin");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Very Bad Trip 2", "../img/Covers/movies/very-bad-trip-2-2011.jpg", 102, 2011, "Comédie", "Phil, Stu, Alan et Doug s’offrent un voyage exotique en Thaïlande, à l’occasion du mariage de Stu. Après l’inoubliable soirée d’enterrement de sa vie de garçon à Las Vegas, Stu ne veut rien laisser au hasard et opte pour un brunch léger, sans risque, avant la cérémonie.", "Bradley Cooper, Zach Galifianakis, Ed Helms", "Todd Phillips");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Mission : Impossible - Protocole fantôme", "../img/Covers/movies/mission-impossible-4-2011.jpg", 132, 2011, "Action, Aventure, Thriller", "Après avoir été impliquée dans un complot terroriste mondial, l’agence Mission Impossible est contrainte de cesser ses activités. Le Protocole Fantôme est mis en place : Ethan Hunt et sa nouvelle équipe doivent opérer dans le secret pour blanchir le nom de leur organisation.", "Tom Cruise, Jeremy Renner, Simon Pegg", "Brad Bird");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Alvin et les Chipmunks 3", "../img/Covers/movies/alvin-et-les-chipmunks-3-2011.jpg", 87, 2011, "Aventure, Comédie, Famille", "En vacances sur un bateau de croisière, Alvin, Simon, Théodore et les Chipettes font les 400 coups. Mais leurs bêtises vont les faire échouer tous les six sur une île paradisiaque, à première vue déserte.", "Justin Long, Matthew Gray Gubler, Jesse McCartney", "Mike Mitchell");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Titeuf, le film", "../img/Covers/movies/titeuf-le-film-2011.jpg", 87, 2011, "Animation, Comédie, Famille", "Catastrophe ! Nadia fête son anniversaire et Titeuf n’est pas invité ! Pourquoi ? Comment a-t-elle pu l’oublier alors qu’il soigne son attitude over-séductive à chaque fois qu’il la croise ?", "Donald Reignoux, Mélanie Bernier, Emilie Blon-Metzinger", "Zep");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Rango", "../img/Covers/movies/rango-2011.jpg", 107, 2011, "Animation, Action, Aventure", "Alors qu'il mène sa vie sans histoire d'animal de compagnie, Rango, caméléon peu aventurier, est en pleine crise d'identité : à quoi bon avoir des ambitions quand tout ce qu'on vous demande, c'est de vous fondre dans la masse ?", "Johnny Depp, Isla Fisher, Timothy Olyphant", "Gore Verbinski");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Tron l'héritage", "../img/Covers/movies/tron-l-heritage-2010.jpg", 125, 2010, "Action, Aventure, Science-Fiction", "Sam est un jeune féru de technologie qui enquête sur la disparition de son père Kevin Flynn, un concepteur de jeux vidéos. Sam et son amie Cora se retrouvent alors propulsés dans un programme féroce de jeux de gladiateurs....", "Jeff Bridges, Garrett Hedlund, Olivia Wilde", "Joseph Kosinski");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Captain America : First Avenger", "../img/Covers/movies/captain-america-2011.jpg", 124, 2011, "Action, Aventure, Science-Fiction", "Steve Rogers, frêle et timide, se porte volontaire pour participer à un programme expérimental qui va le transformer en un Super Soldat connu sous le nom de Captain America. Allié à Bucky Barnes et Peggy Carter, il sera confronté à la diabolique organisation HYDRA...", "Chris Evans, Hugo Weaving, Samuel L. Jackson", "Joe Johnston");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Scream 4", "../img/Covers/movies/scream-4-2011.jpg", 111, 2011, "Horreur, Thriller", "10 ans se sont écoulés depuis les terribles meurtres commis par Ghostface. Sidney Prescott est parvenue à tourner la page mais c’est tout de même avec appréhension qu’elle retourne à Woodsboro pour le lancement de son premier roman.", "Neve Campbell, Courteney Cox, David Arquette", "Wes Craven");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Skyfall", "../img/Covers/movies/skyfall-2012.jpg", 143, 2012, "Action, Aventure, Thriller", "Lorsque la dernière mission de Bond tourne mal, plusieurs agents infiltrés se retrouvent exposés dans le monde entier. Le MI6 est attaqué, et M est obligée de relocaliser l’Agence. Ces événements ébranlent son autorité, et elle est remise en cause par Mallory...", "Daniel Craig, Javier Bardem, Naomie Harris", "Sam Mendes");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("L'Âge de glace : La dérive des continents", "../img/Covers/movies/l-age-de-glace-4-2012.jpg", 88, 2012, "Animation, Aventure, Comédie", "Après un cataclysme qui a provoqué la dérive de tout un continent, Manny, Diego et Sid se retrouvent séparés du reste de la horde. Essayant de rejoindre leurs amis à tout prix, ils utilisent un iceberg comme bateau et se retrouvent dans un périple maritime épique !", "Ray Romano, Denis Leary, John Leguizamo", "Steve Martino, Michael Thurmeier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Sur la piste du Marsupilami", "../img/Covers/movies/sur-la-piste-du-marsupilami-2012.jpg", 105, 2012, "Aventure, Comédie, Famille", "Quand un reporter en quête de scoop arrive en Palombie, il ne se doute pas qu’il va faire la plus incroyable des découvertes… Avec un guide local plein de ressources, ils vont aller de surprise en surprise au cours d’une aventure trépidante.", "Jamel Debbouze, Alain Chabat, Fred Testot", "Alain Chabat");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Twilight - Chapitre 5 : Révélation 2e partie", "../img/Covers/movies/twilight-5-2012.jpg", 115, 2012, "Aventure, Drame, Fantastique", "Après la naissance de sa fille Renesmée, Bella s’adapte peu à peu à sa nouvelle vie de vampire avec le soutien d’Edward. Se sentant menacés par cette naissance d’un nouveau genre, les Volturi déclarent la guerre à la famille Cullen.", "Kristen Stewart, Robert Pattinson, Taylor Lautner", "Bill Condon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Avengers", "../img/Covers/movies/avengers-2012.jpg", 143, 2012, "Action, Science-Fiction", "Lorsque Nick Fury, le directeur du S.H.I.E.L.D., l'organisation qui préserve la paix au plan mondial, cherche à former une équipe de choc pour empêcher la destruction du monde, Iron Man, Hulk, Thor, Captain America, Hawkeye et Black Widow répondent présents.", "Robert Downey Jr., Chris Evans, Scarlett Johansson", "Joss Whedon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Dark Knight Rises", "../img/Covers/movies/the-dark-knight-rises-2012.jpg", 164, 2012, "Action, Drame, Thriller", "Il y a huit ans, Batman a disparu dans la nuit : lui qui était un héros est alors devenu un fugitif. Mais l'arrivée à Gotham de Bane, terroriste masqué, risque d'arracher Bruce à l'exil qu'il s'est imposé. Pourtant, Batman n'est peut-être plus de taille à affronter Bane…", "Christian Bale, Tom Hardy, Anne Hathaway", "Christopher Nolan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La vérité si je mens! 3", "../img/Covers/movies/la-verite-si-je-mens-3-2012.jpg", 112, 2012, "Comédie", "Eddie, Dov, Yvan et les autres… Nos chaleureux amis ont migré du Sentier moribond à la banlieue florissante d’Aubervilliers… Là où les vieux entrepreneurs juifs ont laissé le terrain à de jeunes grossistes chinois courageux et dynamiques…", "Richard Anconina, José Garcia, Bruno Solo", "Thomas Gilou");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Astérix et Obélix : au service de Sa Majesté", "../img/Covers/movies/asterix-et-obelix-4-2012.jpg", 110, 2012, "Aventure, Comédie, Famille", "50 avant Jésus Christ. César a soif de conquêtes. A la tête de ses glorieuses légions il décide d'envahir cette île située aux limites du monde connu, ce pays mystérieux appelé Brittania, la Bretagne.", "Gérard Depardieu, Dany Boon, Edouard Baer", "Laurent Tirard");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Madagascar 3, Bons Baisers D’Europe", "../img/Covers/movies/madagascar-3-2012.jpg", 93, 2012, "Animation, Aventure, Comédie", "Alex le lion, Marty le zèbre, Gloria l'hippopotame et Melman la girafe cherchent toujours désespérément à rentrer chez eux ! Évidemment, King Julian, Maurice et les pingouins les suivent dans leurs péripéties…", "Ben Stiller, Jada Pinkett Smith, Chris Rock", "Eric Darnell, Tom McGrath, Conrad Vernon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Hobbit : un voyage inattendu", "../img/Covers/movies/le-hobbit-1-2012.jpg", 169, 2012, "Aventure, Fantastique", "Les aventures de Bilbon Sacquet, entraîné dans une quête héroïque pour reprendre le Royaume perdu des nains d’Erebor, conquis longtemps auparavant par le dragon Smaug.", "Martin Freeman, Ian McKellen, Richard Armitage", "Peter Jackson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Prénom", "../img/Covers/movies/le-prenom-2012.jpg", 109, 2012, "Comédie", "Vincent, la quarantaine triomphante, va être père pour la première fois. Invité à dîner chez Élisabeth et Pierre, sa sœur et son beau-frère, il y retrouve Claude, un ami d’enfance. En attendant l’arrivée d’Anna, sa jeune épouse, on le presse de questions sur sa future paternité...", "Patrick Bruel, Valérie Benguigui, Charles Berling", "Alexandre de La Patellière, Matthieu Delaporte");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Rebelle", "../img/Covers/movies/rebelle-2012.jpg", 93, 2012, "Animation, Action, Aventure", "Merida, la fille du roi Fergus est bien décidée à suivre son propre chemin dans la vie. Elle défie une tradition millénaire sacrée aux yeux de trois seigneurs du royaume, et sans l’avoir voulu, elle va déchaîner le chaos dans le royaume.", "Kelly Macdonald, Billy Connolly, Emma Thompson", "Mark Andrews, Brenda Chapman, Steve Purcell");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Taken 2", "../img/Covers/movies/taken-2-2012.jpg", 92, 2012, "Action, Crime, Thriller", "Dans 'Taken', Bryan Mills, ex-agent de la CIA aux compétences si particulières, a réussi à arracher sa fille des mains d’un gang mafieux. Un an plus tard, le chef du clan réclame vengeance. Cette fois-ci, c’est après lui qu’ils en ont.", "Liam Neeson, Famke Janssen, Maggie Grace", "Olivier Megaton");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Seigneurs", "../img/Covers/movies/les-seigneurs-2012.jpg", 97, 2012, "Comédie, Sport", "Patrick Orbéra, la cinquantaine, est une ancienne gloire du football qui a totalement raté sa reconversion. Sans emploi, alcoolique et ruiné, il n’a même plus le droit de voir sa fille Laura. Contraint par un juge de retrouver un emploi stable, il n’a d’autre choix que de partir sur une petite île bretonne...", "José Garcia, Jean-Pierre Marielle, Franck Dubosc", "Olivier Dahan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Amazing Spider-Man", "../img/Covers/movies/the-amazing-spider-man-2012.jpg", 136, 2012, "Action, Aventure, Science-Fiction", "Abandonné par ses parents, et élevé par son oncle, Peter Parker a tout du lycéen timide, jusqu'à ce qu'il devienne Spider-Man. Il va alors utiliser ses nouveaux pouvoirs pour lever le voile sur les projets secrets du Dr. Connors, et peut-être découvrir la vérité sur son père...", "Andrew Garfield, Emma Stone, Rhys Ifans", "Marc Webb");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Cinq légendes", "../img/Covers/movies/les-cinq-legendes-2012.jpg", 97, 2012, "Animation, Action, Aventure", "L’aventure d’un groupe de héros, tous doués de pouvoirs extraordinaires. Emmenées par Jack Frost, un adolescent rebelle et ingénieux, ces cinq légendes vont devoir, pour la première fois, unir leurs forces pour protéger les espoirs, les rêves et l’imaginaire de tous les enfants.", "Hugh Jackman, Alec Baldwin, Isla Fisher", "Peter Ramsey");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Infidèles", "../img/Covers/movies/les-infideles-2012.jpg", 109, 2012, "Comédie", "L'infidélité masculine et ses nombreuses variations, vues par 7 réalisateurs.", "Jean Dujardin, Gilles Lellouche, Géraldine Nakache", "Emmanuelle Bercot, Fred Cavayé, Alexandre Courtès");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Men In Black III", "../img/Covers/movies/men-in-black-3-2012.jpg", 106, 2012, "Action, Aventure, Comédie", "Un agent luttant contre les aliens retourne dans le passé pour sauver la vie de son partenaire dans le présent, le destin de l'agence et l'avenir de la Terre.", "Will Smith, Tommy Lee Jones, Josh Brolin", "Barry Sonnenfeld");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Blanche Neige", "../img/Covers/movies/blanche-neige-2012.jpg", 106, 2012, "Aventure, Comédie, Drame", "Lorsque son père, le Roi, meurt, Blanche Neige est en danger. Sa belle-mère, cruelle et avide de pouvoir, l’évince pour s’emparer du trône. Quand la jeune femme attire malgré tout l’attention d’un Prince aussi puissant que séduisant, l’horrible marâtre ne lui laisse aucune chance et la bannit. Blanche Neige se réfugie alors dans la forêt…", "Lily Collins, Julia Roberts, Armie Hammer", "Tarsem Singh");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Expendables 2: unité spéciale", "../img/Covers/movies/expendables-2-2012.jpg", 103, 2012, "Action, Aventure, Thriller", "Barney Ross et toute sa bande de mercenaires gros bras sont de retour pour venger la mort de l’un d’entre eux et pour s’emparer d’une importante quantité de plutonium, qui se trouve entre les mains de gens de la pire espèce.", "Sylvester Stallone, Liam Hemsworth, Randy Couture", "Simon West");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Dark Shadows", "../img/Covers/movies/dark-shadows-2012.jpg", 113, 2012, "Comédie, Fantastique, Horreur", "Au XVIIIème siècle, Barnabas Collins est maudit par une sorcière. Il est alors transformé en vampire et enterré vivant. Deux siècles plus tard, Barnabas est libéré de sa tombe par inadvertance et débarque en 1972 dans un monde totalement transformé…", "Johnny Depp, Michelle Pfeiffer, Eva Green", "Tim Burton");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Projet X", "../img/Covers/movies/projet-x-2012.jpg", 88, 2012, "Comédie", "Alors qu'ils semblaient jusque-là se fondre dans la masse, trois lycéens décident de sortir de l'anonymat. En apparence, leur projet est plutôt inoffensif puisqu'ils ont l'intention d'organiser une fête des plus mémorables. Mais rien n'aurait pu les préparer à la soirée qu'ils s'apprêtent à vivre... La rumeur se propage alors rapidement, tandis que les rêves des uns s'effondrent, les résultats scolaires des autres dégringolent, et des légendes se forgent…", "Thomas Mann, Oliver Cooper, Jonathan Daniel Brown", "Nima Nourizadeh");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Prometheus", "../img/Covers/movies/prometheus-2012.jpg", 124, 2012, "Aventure, Mystère, Science-Fiction", "Une équipe d’explorateurs découvre un indice sur l’origine de l’humanité sur Terre. Cette découverte les entraîne dans un voyage fascinant jusqu’aux recoins les plus sombres de l’univers. Là-bas, un affrontement terrifiant qui décidera de l’avenir de l’humanité les attend.", "Noomi Rapace, Logan Marshall-Green, Michael Fassbender", "Ridley Scott");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Hunger Games", "../img/Covers/movies/the-hunger-games-2012.jpg", 142, 2012, "Action, Aventure, Science-Fiction", "Dans le futur, le gouvernement fédéral des USA exige de chacun de ses 12 districts qu'il envoie un jeune garçon et une jeune fille pour participer aux Hunger Games, un combat à mort retransmis en direct à la télévision.", "Jennifer Lawrence, Josh Hutcherson, Liam Hemsworth", "Gary Ross");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("American Pie 4", "../img/Covers/movies/american-pie-4-2012.jpg", 113, 2012, "Comédie", "Une décennie après les événements d'American Pie, toute la bande est de retour... Les jeunes hommes se réunissent le temps d’un week-end pour se remémorer leurs exploits d’antan et y puiser de nouvelles inspirations. Que la fête commence, l’heure du checkup hormonal a sonné…", "Jason Biggs, Alyson Hannigan, Seann William Scott", "Jon Hurwitz, Hayden Schlossberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Clochette et le secret des fées", "../img/Covers/movies/clochette-et-le-secret-des-fees-2012.jpg", 75, 2012, "Animation, Famille, Fantastique", "Il existe au-delà de la Vallée des Fées un royaume où l’hiver est roi : la Forêt Blanche. Bien qu’il soit interdit d’y pénétrer, l’intrépide Clochette décide de s’y aventurer, mais un étrange phénomène se produit dès qu’elle en passe la frontière : ses ailes se mettent à scintiller...", "Mae Whitman, Lucy Hale, Timothy Dalton", "Bobs Gannaway, Peggy Holmes");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ted", "../img/Covers/movies/ted-2012.jpg", 106, 2012, "Comédie", "À 8 ans, le petit John Bennett fit le voeu que son ours en peluche de Noël s’anime et devienne son meilleur ami pour la vie, et il vit son voeu exaucé. Presque 30 ans plus tard, l’histoire n’a plus vraiment les allures d’un conte de Noël. L’omniprésence de Ted aux côtés de John pèse lourdement sur sa relation amoureuse avec Lori. Bien que patiente, Lori voit en cette amitié exclusive, consistant principalement à boire des bières et fumer de l’herbe devant des programmes télé plus ringards les uns que les autres.", "Mark Wahlberg, Mila Kunis, Seth MacFarlane", "Seth MacFarlane");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Voyage au centre de la Terre 2 : L'île mystérieuse", "../img/Covers/movies/voyage-au-centre-de-la-terre-2-2012.jpg", 94, 2012, "Action, Aventure, Comédie", "Sean Anderson, 17 ans, reçoit un message de détresse codé en provenance d'une île mystérieuse dont personne n'a jamais entendu parler… Tout aussi impatient que Sean de savoir d'où vient ce message, Hank, son beau-père, décide de tenter l'aventure.", "Josh Hutcherson, Dwayne Johnson, Michael Caine", "Brad Peyton");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Un Plan Parfait", "../img/Covers/movies/un-plan-parfait-2012.jpg", 104, 2012, "Aventure, Comédie, Romance", "Une jeune femme dont la carrière est brillante vit heureuse avec son petit ami. Mais une malédiction semble régner sur sa famille : chaque premier mariage se termine par un divorce...", "Diane Kruger, Dany Boon, Alice Pol", "Pascal Chaumeil");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Mondes de Ralph", "../img/Covers/movies/les-mondes-de-ralph-2012.jpg", 101, 2012, "Animation, Aventure, Comédie", "Ralph la Casse rêve d'être aussi adulé que le parfait gentil de son jeu vidéo, Fix-It Felix. Problème : personne n'aime un méchant. Mais ils aiment les héros… Alors quand un tout nouveau jeu fait son apparition, Ralph voit là l'occasion d'atteindre la gloire et le bonheur...", "John C. Reilly, Jack McBrayer, Jane Lynch", "Rich Moore");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("De l'autre côté du périph", "../img/Covers/movies/de-l-autre-cote-du-periph-2012.jpg", 96, 2012, "Action, Comédie, Crime", "Un matin à l’aube dans une cité de Bobigny, près d’un vieux tripot clandestin, est retrouvé le corps sans vie de Eponine Chaligny, femme du très influent Jean-Éric Chaligny, premier patron de France, au centre d’un climat social extrême qui secoue la France depuis quelques semaines.", "Omar Sy, Laurent Lafitte, Sabrina Ouazani", "David Charhon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Argo", "../img/Covers/movies/argo-2012.jpg", 120, 2012, "Biopic, Drame, Thriller", "Le 4 novembre 1979, au summum de la révolution iranienne, des militants envahissent l’ambassade américaine de Téhéran, et prennent 52 Américains en otage. Mais au milieu du chaos, 6 Américains réussissent à s’échapper et à se réfugier au domicile de l’ambassadeur canadien.", "Ben Affleck, Bryan Cranston, John Goodman", "Ben Affleck");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Titanic", "../img/Covers/movies/titanic-1997.jpg", 194, 1997, "Drame, Romance", "Southampton, 10 avril 1912. Le paquebot le plus grand et le plus moderne du monde, réputé pour son insubmersibilité, le Titanic appareille pour son premier voyage. Quatre jours plus tard, il heurte un iceberg. A son bord, un artiste pauvre et une grande bourgeoise tombent amoureux.", "Leonardo DiCaprio, Kate Winslet, Billy Zane", "James Cameron");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Kaïra", "../img/Covers/movies/les-kaira-2012.jpg", 95, 2012, "Comédie", "Mousten, Abdelkrim et Momo sont trois potes d’enfance qui ont toujours vécu dans leur cité de Melun. Casquettes enfoncées sur la tête et baskets aux pieds, tous les trois ont le même problème : célibataires, ils passent leurs journées à laisser s’écouler leurs vies...", "Medi Sadoun, Franck Gastambide, Jib Pocthier", "Franck Gastambide");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Moi, moche et méchant 2", "../img/Covers/movies/moi-moche-et-mechant-2-2013.jpg", 98, 2013, "Animation, Aventure, Comédie", "Ayant abandonné la super-criminalité et mis de côté ses activités funestes pour se consacrer à la paternité, Gru, avec le Professeur Néfario et les Minions, doit se trouver de nouvelles occupations.", "Steve Carell, Kristen Wiig, Benjamin Bratt", "Pierre Coffin, Chris Renaud");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Iron Man 3", "../img/Covers/movies/iron-man-3-2013.jpg", 130, 2013, "Action, Aventure, Science-Fiction", "Tony Stark, l’industriel flamboyant qui est aussi Iron Man, est confronté cette fois à un ennemi qui va attaquer sur tous les fronts. Lorsque son univers personnel est détruit, Stark se lance dans une quête acharnée pour retrouver les coupables.", "Robert Downey Jr., Guy Pearce, Gwyneth Paltrow", "Shane Black");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Gravity", "../img/Covers/movies/gravity-2013.jpg", 91, 2013, "Drame, Science-Fiction, Thriller", "Pour sa première expédition à bord d'une navette spatiale, le docteur Ryan Stone, brillante experte en ingénierie médicale, accompagne l'astronaute chevronné Matt Kowalsky qui effectue son dernier vol avant de prendre sa retraite.", "Sandra Bullock, George Clooney, Ed Harris", "Alfonso Cuarón");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Profs", "../img/Covers/movies/les-profs-2013.jpg", 88, 2013, "Comédie", "Avec ses 12% de réussite au bac, le lycée Jules Ferry est le pire lycée de France. L’Inspecteur d’Académie, au désespoir, s’en remet aux conseils de son Adjoint. Ce dernier lui propose de recruter une équipe de professeurs selon une nouvelle formule : aux pires élèves, les pires profs !", "Christian Clavier, Isabelle Nanty, Pierre-François Martin-Laval", "Pierre-François Martin-Laval");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Hobbit : la Désolation de Smaug", "../img/Covers/movies/le-hobbit-la-desolation-de-smaug-2013.jpg", 161, 2013, "Aventure, Drame, Fantastique", "Les aventures de Bilbon Sacquet, paisible hobbit, qui sera entraîné, lui et une compagnie de Nains, par le magicien Gandalf pour récupérer le trésor détenu par le dragon Smaug. Au cours de ce périple, il mettra la main sur l'anneau de pouvoir que possédait Gollum...", "Ian McKellen, Martin Freeman, Richard Armitage", "Peter Jackson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Reine des neiges", "../img/Covers/movies/la-reine-des-neiges-2013.jpg", 102, 2013, "Animation, Aventure, Comédie", "L’accomplissement d’une prophétie a plongé un royaume lointain dans un éternel hiver. Anna, une jeune fille aussi audacieuse qu’optimiste, se lance alors dans un incroyable voyage à la recherche de sa sœur, Elsa, la Reine des Neiges, en compagnie de Kristoff.", "Kristen Bell, Idina Menzel, Jonathan Groff", "Chris Buck, Jennifer Lee");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Insaisissables", "../img/Covers/movies/insaisissables-2013.jpg", 115, 2013, "Crime, Mystère, Thriller", "'Les Quatre Cavaliers', de brillants magiciens et illusionnistes, viennent de donner deux spectacles de magie époustouflants : le premier en braquant une banque sur un autre continent, le deuxième en transférant la fortune d’un banquier véreux sur les comptes en banque du public.", "Jesse Eisenberg, Common, Mark Ruffalo", "Louis Leterrier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Fast & Furious 6", "../img/Covers/movies/fast-furious-6-2013.jpg", 130, 2013, "Action, Aventure, Crime", "Dom, Brian et toute leur équipe, après le casse de Rio, ayant fait tomber un empire en empochant 100 millions de dollars, se sont dispersés aux quatre coins du globe. Mais l’incapacité de rentrer chez eux, et l’obligation de vivre en cavale permanente, laissent à leur vie le goût amer de l’inaccomplissement. Pendant ce temps Hobbs traque aux quatre coins du monde un groupe de chauffeurs mercenaires aux talents redoutables, dont le meneur, Shaw est secondé d’une de main de fer par l’amour que Dom croyait avoir perdu pour toujours : Letty.", "Vin Diesel, Paul Walker, Dwayne Johnson", "Justin Lin");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Hunger Games - L'embrasement", "../img/Covers/movies/hunger-games-lembasement-2013.jpg", 146, 2013, "Action, Aventure, Science-Fiction", "Katniss Everdeen est rentrée chez elle saine et sauve après avoir remporté la 74e édition des Hunger Games avec son partenaire Peeta Mellark. Puisqu’ils ont gagné, ils sont obligés de laisser une fois de plus leur famille et leurs amis pour partir faire la Tournée de la victoire...", "Jennifer Lawrence, Josh Hutcherson, Liam Hemsworth", "Francis Lawrence");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Turbo", "../img/Covers/movies/turbo-2013.jpg", 96, 2013, "Animation, Aventure, Comédie", "Turbo est un escargot qui n’a qu’un seul rêve en tête : être incroyablement rapide ! Son obsession pour la vitesse l’a rendu quelque peu impopulaire chez les siens, où lenteur et prudence sont de rigueur. Mais il est hors de question pour lui de se conformer.", "Ryan Reynolds, Paul Giamatti, Maya Rudolph", "David Soren");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("World War Z", "../img/Covers/movies/world-war-z-2013.jpg", 116, 2013, "Action, Aventure, Horreur", "Un jour comme les autres, Gerry Lane et sa famille se retrouvent coincés dans un embouteillage monstre sur leur trajet quotidien. Ancien enquêteur des Nations Unies, Lane comprend immédiatement que la situation est inhabituelle. Tandis que les hélicoptères de la police sillonnent le ciel et que les motards quadrillent les rues, la ville bascule dans le chaos...", "Brad Pitt, Mireille Enos, Daniella Kertesz", "Marc Forster");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Croods", "../img/Covers/movies/les-croods-2013.jpg", 98, 2013, "Animation, Action, Aventure", "Les Croods est l’histoire de la première famille moderne du monde. Elle se déroule à une époque jusqu’à présent inconnue, l’ère Croodéolithique – lorsque Mère Nature en était aux premières étapes de ses expériences, dont le résultat était une flore et une faune bizarres.", "Nicolas Cage, Ryan Reynolds, Emma Stone", "Kirk DeMicco, Chris Sanders");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Man of Steel", "../img/Covers/movies/man-of-steel-2013.jpg", 143, 2013, "Action, Aventure, Science-Fiction", "Un petit garçon découvre qu'il possède des pouvoirs surnaturels et qu'il n'est pas né sur Terre. Plus tard, il s'engage dans un périple afin de comprendre d'où il vient et pourquoi il a été envoyé sur notre planète. Mais il devra devenir un héros s'il veut sauver le monde de la destruction totale et incarner l'espoir pour toute l'humanité.", "Henry Cavill, Amy Adams, Michael Shannon", "Zack Snyder");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Thor : Le Monde des ténèbres", "../img/Covers/movies/thor-le-monde-des-tenebres-2013.jpg", 112, 2013, "Action, Aventure, Fantastique", "Thor se bat pour restaurer l’ordre dans le cosmos, mais une ancienne race, sous la conduite du terrible Malekith, un être assoiffé de vengeance, revient pour répandre les ténèbres.", "Chris Hemsworth, Natalie Portman, Tom Hiddleston", "Alan Taylor");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Schtroumpfs 2", "../img/Covers/movies/les-schtroumpfs-2-2013.jpg", 105, 2013, "Animation, Aventure, Comédie", "Cherchant désespérément à s’emparer de la puissance de l’essence magique des Schtroumpfs, Gargamel a inventé les Canailles, de minuscules créatures. Mais pour que leur transformation en vrais Schtroumpfs soit complète, il lui manque encore la formule secrète que détient la Schtroumpfette.", "Neil Patrick Harris, Jayma Mays, Katy Perry", "Raja Gosnell");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Monstres Academy", "../img/Covers/movies/monstres-academy-2013.jpg", 104, 2013, "Animation, Aventure, Comédie", "Même quand il n’était qu’un tout petit monstre, Bob Razowski rêvait déjà de devenir une Terreur. Aujourd’hui, il est enfin en première année à la prestigieuse université Monstres Academy, où sont formées les meilleures Terreurs. Son plan de carrière bien préparé est pourtant menacé par sa rencontre avec James P. Sullivan, dit Sulli, un vrai crack qui a un don naturel pour Terrifier. Aveuglés par leur désir de se prouver l’un à l’autre qu’ils sont imbattables, tous deux finissent par se faire renvoyer de l’université. Pire encore : ils se rendent compte que s’ils veulent que les choses aient une chance de rentrer dans l’ordre, ils vont devoir travailler ensemble, et avec un petit groupe de monstres bizarres et mal assortis…", "Billy Crystal, John Goodman, Steve Buscemi", "Dan Scanlon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Boule & Bill", "../img/Covers/movies/boule-bill-2013.jpg", 82, 2013, "Comédie, Famille", "Tout commence à la SPA. Un jeune cocker se morfond dans sa cage. Il ne trouve pas les maîtres de ses rêves. Soudain, apparaît un petit garçon, aussi roux que lui. Qui se ressemble s'assemble : c'est le coup de foudre. Pour Boule et Bill, c'est le début d'une grande amitié.", "Franck Dubosc, Marina Foïs, Charles Crombez", "Alexandre Charlot, Franck Magnier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Wolverine : le combat de l'immortel", "../img/Covers/movies/wolverine-le-combat-de-limmortel-2013.jpg", 126, 2013, "Action, Aventure, Science-Fiction", "Wolverine, l’éternel guerrier marginal, se retrouve au Japon. Vulnérable pour la première fois et poussé au bout de ses limites physiques et émotionnelles, Wolverine affrontera non seulement l’acier mortel du samouraï mais aussi les questions liées à sa propre immortalité.", "Hugh Jackman, Tao Okamoto, Rila Fukushima", "James Mangold");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Majordome", "../img/Covers/movies/le-majordome-2013.jpg", 132, 2013, "Biopic, Drame", "L’histoire vraie d’un majordome à la Maison-Blanche, qui fut le témoin privilégié des bouleversements politiques et des grands événements historiques, durant ses trente années de service auprès de sept présidents différents.", "Forest Whitaker, Oprah Winfrey, John Cusack", "Lee Daniels");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Very Bad Trip 3", "../img/Covers/movies/very-bad-trip-3-2013.jpg", 100, 2013, "Comédie, Crime", "Deux ans ont passé. Phil, Stu et Doug mènent des existences tranquilles et heureuses. Ils ont fait disparaître leurs tatouages et se sont rachetés une conduite. Seul Alan n'a pas trouvé son équilibre et laisse libre cours à ses impulsions.", "Bradley Cooper, Zach Galifianakis, Ed Helms", "Todd Phillips");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Die Hard : belle journée pour mourir", "../img/Covers/movies/die-hard-belle-journee-pour-mourir-2013.jpg", 98, 2013, "Action, Thriller", "L’inspecteur John McClane se porte au secours de son fils Jack, retenu prisonnier en Russie. Tous deux devront également faire face à un puissant leader russe qui s’avère être un danger pour la paix mondiale...", "Bruce Willis, Jai Courtney, Sebastian Koch", "John Moore");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Eyjafjallajökull", "../img/Covers/movies/eyjafjallajokull-2013.jpg", 92, 2013, "Comédie", "Pour les voyageurs du monde entier, l’éruption du volcan islandais Eyjafjallajökull est un coup dur. Pour Alain et Valérie, c’est une catastrophe. Car pour arriver à temps dans le petit village de Grèce où se marie leur fille, ce couple de divorcés, va être amené à prendre la route ensemble.", "Valérie Bonneton, Dany Boon, Denis Ménochet", "Alexandre Coffre");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Belle et Sébastien", "../img/Covers/movies/belle-et-sebastien-2013.jpg", 104, 2013, "Aventure, Drame, Famille", "Ça se passe là-haut, dans les Alpes. Ça se passe là où la neige est immaculée, là où les chamois coursent les marmottes, là où les sommets tutoient les nuages. Ça se passe dans un village paisible jusqu’à l’arrivée des Allemands.", "Félix Bossuet, Tchéky Karyo, Margaux Chatelier", "Nicolas Vanier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les gamins", "../img/Covers/movies/les-gamins-2013.jpg", 95, 2013, "Comédie", "Tout juste fiancé, Thomas rencontre son futur beau-père Gilbert, marié depuis 30 ans à Suzanne et en pleine crise de la cinquantaine. Gilbert, persuadé d'avoir gâché sa vie à cause de son couple, dissuade Thomas d'épouser sa fille Lola et le pousse à tout plaquer à ses côtés.", "Alain Chabat, Max Boublil, Sandrine Kiberlain", "Anthony Marciano");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Planes", "../img/Covers/movies/planes-2013.jpg", 91, 2013, "Animation, Aventure, Comédie", "Chaque jour, alors qu’il pulvérise des traitements agricoles sur les récoltes, le petit avion de ferme Dusty se prend à rêver qu’il pourrait voler en compétition au milieu des avions les plus rapides au monde. Seulement voilà, il n’a pas vraiment le gabarit d’un champion, et en plus, il est sujet au vertige ! Comme il n’est pas du genre à renoncer, Dusty fait appel à Skipper, un as de l’aéronavale, pour l’aider à se qualifier lors des éliminatoires du Grand Rallye du Tour du Ciel et ainsi défier sur son terrain Ripslinger, le redoutable tenant du titre", "Carlos Alazraqui, Dane Cook, Stacy Keach", "Klay Hall");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Gatsby le Magnifique", "../img/Covers/movies/gatsby-le-magnifique-2013.jpg", 143, 2013, "Drame, Romance", "Printemps 1922. L'époque est propice au relâchement des mœurs, à l'essor du jazz et à l'enrichissement des contrebandiers d'alcool… Apprenti écrivain, Nick Carraway quitte la région du Middle-West pour s'installer à New York.", "Leonardo DiCaprio, Carey Mulligan, Joel Edgerton", "Baz Luhrmann");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Monde fantastique d'Oz", "../img/Covers/movies/le-monde-fantastique-doz-2013.jpg", 130, 2013, "Aventure, Famille, Fantastique", "Lorsque Oscar Diggs, un petit magicien de cirque sans envergure à la moralité douteuse, est emporté à bord de sa montgolfière depuis le Kansas poussiéreux jusqu’à l’extravagant Pays d’Oz, il y voit la chance de sa vie. Tout semble tellement possible dans cet endroit stupéfiant ...", "James Franco, Michelle Williams, Rachel Weisz", "Sam Raimi");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Elysium", "../img/Covers/movies/elysium-2013.jpg", 109, 2013, "Action, Drame, Science-Fiction", "En 2154, il existe deux catégories de personnes : ceux très riches, qui vivent sur la parfaite station spatiale crée par les hommes appelée Elysium, et les autres, ceux qui vivent sur la Terre devenue surpeuplée et ruinée.", "Matt Damon, Jodie Foster, Sharlto Copley", "Neill Blomkamp");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Percy Jackson : La mer des monstres", "../img/Covers/movies/percy-jackson-la-mer-des-monstres-2013.jpg", 106, 2013, "Aventure, Famille, Fantastique", "Percy Jackson ne sent pas vraiment l’âme d’un héros. Même s’il a déjà sauvé le monde, ce demi-dieu se demande si ce n’était pas tout simplement un coup de bol extraordinaire. Lorsqu’il découvre que son demi-frère est un monstre, il commence même à penser qu’être le fils de Poséidon est peut-être une malédiction.", "Logan Lerman, Alexandra Daddario, Brandon T. Jackson", "Thor Freudenthal");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("20 ans d'écart", "../img/Covers/movies/20-ans-d-ecart-2013.jpg", 92, 2013, "Comédie, Romance", "Alice Lantins a 38 ans. Elle est belle, ambitieuse et fait preuve d’une impeccable conscience professionnelle au point d’en oublier sa vie privée. Bref, elle a tout pour devenir la prochaine rédactrice en chef du magazine « Rebelle », tout sauf son image de femme coincée.", "Virginie Efira, Pierre Niney, Charles Berling", "David Moreau");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Lincoln", "../img/Covers/movies/lincoln-2013.jpg", 150, 2012, "Biopic, Drame, Historique", "Les derniers mois du mandat du 16e Président des États-Unis. Dans une nation déchirée par la guerre civile et secouée par le vent du changement, Abraham Lincoln met tout en œuvre pour résoudre le conflit, unifier le pays et abolir l'esclavage.", "Daniel Day-Lewis, Sally Field, David Strathairn", "Steven Spielberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Oblivion", "../img/Covers/movies/oblivion-2013.jpg", 124, 2013, "Action, Aventure, Science-Fiction", "Sur une planète Terre devenue méconnaissable, la confrontation d’un homme avec son passé va le mener sur le chemin de la rédemption et de la découverte alors qu’il se bat pour sauver l’humanité de sa perte.", "Tom Cruise, Morgan Freeman, Andrea Riseborough", "Joseph Kosinski");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("After Earth", "../img/Covers/movies/after-earth-2013.jpg", 100, 2013, "Action, Aventure, Science-Fiction", "Après un atterrissage forcé, Kitai Raige et son père, Cypher, se retrouvent sur Terre, mille ans après que l’humanité a été obligée d’évacuer la planète, chassée par des événements cataclysmiques. Cypher est grièvement blessé, et Kitai s’engage dans un périple à haut risque pour signaler leur présence et demander de l’aide. Il va explorer des lieux inconnus, affronter les espèces animales qui ont évolué et dominent à présent la planète, et combattre une créature extraterrestre redoutable qui s’est échappée au moment du crash.", "Jaden Smith, David Denman, Will Smith", "M. Night Shyamalan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Epic : la bataille du royaume secret", "../img/Covers/movies/epic-la-bataille-du-royaume-secret-2013.jpg", 102, 2013, "Animation, Action, Aventure", "L'histoire d'une guerre insoupçonnable qui fait rage autour de nous. Lorsqu'une adolescente se retrouve plongée par magie dans cet univers caché, elle doit s'allier à un groupe improbable de personnages singuliers et pleins d'humour afin de sauver leur monde... et le nôtre.", "Amanda Seyfried, Josh Hutcherson, Beyoncé", "Chris Wedge");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Conjuring : Les dossiers Warren", "../img/Covers/movies/conjuring-les-dossiers-warren-2013.jpg", 112, 2013, "Horreur, Mystère, Thriller", "Avant Amityville, il y avait Harrisville… Conjuring : Les dossiers Warren, raconte l'histoire horrible, mais vraie, d'Ed et Lorraine Warren, enquêteurs paranormaux réputés dans le monde entier, venus en aide à une famille terrorisée par une présence inquiétante dans leur ferme isolée…", "Patrick Wilson, Vera Farmiga, Ron Livingston", "James Wan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Lone Ranger, Naissance d'un héros", "../img/Covers/movies/lone-ranger-naissance-dun-heros-2013.jpg", 150, 2013, "Action, Aventure, Western", "Tonto, le guerrier indien, raconte comment John Reid, un ancien défenseur de la loi, est devenu un justicier légendaire. Ces deux héros à part vont devoir apprendre à faire équipe pour affronter le pire de la cupidité et de la corruption. Le tandem fait des étincelles et entraîne le public dans un tourbillon de surprises et d’humour.", "Johnny Depp, Armie Hammer, William Fichtner", "Gore Verbinski");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Vive la France", "../img/Covers/movies/vive-la-france-2013.jpg", 94, 2013, "Comédie", "Muzafar et Feruz sont deux gentils bergers du Taboulistan… Tout petit pays d’Asie centrale dont personne ne soupçonne l’existence. Afin de faire connaître son pays sur la scène internationale, le fils du président tabouli décide de se lancer dans le terrorisme «publicitaire»...", "José Garcia, Michaël Youn, Isabelle Funaro", "Michaël Youn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Möbius", "../img/Covers/movies/mobius-2013.jpg", 103, 2013, "Drame, Thriller", "Grégory Lioubov, un officier des services secrets russes est envoyé à Monaco afin de surveiller les agissements d’un puissant homme d’affaires. Dans le cadre de cette mission, son équipe recrute Alice, une surdouée de la finance.", "Jean Dujardin, Cécile de France, Tim Roth", "Eric Rochant");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Happiness Therapy", "../img/Covers/movies/happiness-therapy-2012.jpg", 122, 2012, "Comédie, Drame, Romance", "Tout juste sorti d'un hôpital psychiatrique, après 8 mois d'internement, Pat, ancien professeur d'histoire au lycée, tente de renouer des liens avec son ex-femme.", "Bradley Cooper, Jennifer Lawrence, Robert De Niro", "David O. Russell");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Pacific Rim", "../img/Covers/movies/pacific-rim-2013.jpg", 131, 2013, "Action, Aventure, Science-Fiction", "Alors qu'une guerre entre l'humanité et des créatures marines monstrueuses fait rage, un ancien pilote et un stagiaire sont associés pour piloter une arme spéciale apparemment obsolète dans un effort désespéré pour sauver le monde de l'apocalypse.", "Idris Elba, Charlie Hunnam, Rinko Kikuchi", "Guillermo del Toro");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Loup de Wall Street", "../img/Covers/movies/le-loup-de-wall-street-2013.jpg", 180, 2013, "Biopic, Comédie, Crime", "L’argent. Le pouvoir. Les femmes. La drogue. Les tentations étaient là, à portée de main, et les autorités n’avaient aucune prise. Aux yeux de Jordan et de sa meute, la modestie était devenue complètement inutile. Trop n’était jamais assez…", "Leonardo DiCaprio, Jonah Hill, Margot Robbie", "Martin Scorsese");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Qu'est-ce qu'on a fait au Bon Dieu?", "../img/Covers/movies/qu-est-ce-qu-on-a-fait-au-bon-dieu-2014.jpg", 97, 2014, "Comédie", "Claude et Marie Verneuil, issus de la grande bourgeoisie catholique provinciale sont des parents plutôt 'vieille France'. Mais ils se sont toujours obligés à faire preuve d'ouverture d'esprit...", "Christian Clavier, Chantal Lauby, Ary Abittan", "Philippe de Chauveron");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Supercondriaque", "../img/Covers/movies/supercondriaque-2014.jpg", 107, 2014, "Comédie", "Romain Faubert est un homme seul qui, à bientôt 40 ans, n’a ni femme ni enfant. Le métier qu’il exerce, photographe pour dictionnaire médical en ligne, n’arrange rien à une hypocondrie maladive qui guide son style de vie depuis bien trop longtemps et fait de lui un peureux névropathe.", "Dany Boon, Kad Merad, Alice Pol", "Dany Boon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Lucy", "../img/Covers/movies/lucy-2014.jpg", 89, 2014, "Action, Science-Fiction, Thriller", "A la suite de circonstances indépendantes de sa volonté, une jeune étudiante voit ses capacités intellectuelles se développer à l’infini. Elle « colonise » son cerveau, et acquiert des pouvoirs illimités.", "Scarlett Johansson, Morgan Freeman, Choi Min-sik", "Luc Besson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Hobbit : la Bataille des Cinq Armées", "../img/Covers/movies/le-hobbit-la-bataille-des-cinq-armees-2014.jpg", 144, 2014, "Aventure, Fantastique", "Atteignant enfin la Montagne Solitaire, Thorin et les Nains, aidés par Bilbon le Hobbit, ont réussi à récupérer leur royaume et leur trésor. Mais ils ont également réveillé le dragon Smaug qui déchaîne désormais sa colère sur les habitants de Lac-ville. A présent, les Nains, les Elfes, les Humains mais aussi les Wrags et les Orques menés par le Nécromancien, convoitent les richesses de la Montagne Solitaire. La bataille des cinq armées est imminente et Bilbon est le seul à pouvoir unir ses amis contre les puissances obscures de Sauron.", "Ian McKellen, Martin Freeman, Richard Armitage", "Peter Jackson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Planète des singes : l'affrontement", "../img/Covers/movies/la-planete-des-singes-l-affrontement-2014.jpg", 130, 2014, "Action, Aventure, Drame", "Une nation de plus en plus nombreuse de singes évolués, dirigée par César, est menacée par un groupe d’humains qui a survécu au virus dévastateur qui s'est répandu dix ans plus tôt. Ils parviennent à une trêve fragile, mais de courte durée : les deux camps sont sur le point de se livrer une guerre qui décidera de l’espèce dominante sur Terre.", "Gary Oldman, Keri Russell, Andy Serkis", "Matt Reeves");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Dragons 2", "../img/Covers/movies/dragons-2-2014.jpg", 102, 2014, "Animation, Action, Aventure", "Ce film est présenté hors-compétition au Festival de Cannes 2014.Tandis qu’Astrid, Rustik le Morveux et le reste de la bande se défient durant des courses sportives de dragons devenues populaires sur l’île, notre duo désormais inséparable parcourt les cieux, à la découverte de territoires inconnus et de nouveaux mondes.", "Jay Baruchel, Cate Blanchett, Gerard Butler", "Dean DeBlois");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("X-Men: Days of Future Past", "../img/Covers/movies/x-men-days-of-future-past-2014.jpg", 132, 2014, "Action, Aventure, Science-Fiction", "Les X-Men envoient Wolverine dans le passé pour changer un événement historique majeur, qui pourrait impacter mondialement humains et mutants.", "Patrick Stewart, Ian McKellen, Hugh Jackman", "Bryan Singer");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Rio 2", "../img/Covers/movies/rio-2-2014.jpg", 101, 2014, "Animation, Aventure, Comédie", "Blu a pris son envol et se sent désormais chez lui à Rio de Janeiro, aux côtés de Perla et de leurs trois enfants. Mais la vie de perroquet ne s’apprend pas en ville et Perla insiste pour que la famille s’installe dans la forêt amazonienne alors qu'ils découvrent que d'autres aras bleus y vivent.", "Jesse Eisenberg, Anne Hathaway, Jemaine Clement", "Carlos Saldanha");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Samba", "../img/Covers/movies/samba-2014.jpg", 118, 2014, "Comédie, Drame, Romance", "Samba, sénégalais en France depuis 10 ans, collectionne les petits boulots ; Alice est une cadre supérieure épuisée par un burn out. Lui essaye par tous les moyens d'obtenir ses papiers, alors qu'elle tente de se reconstruire par le bénévolat dans une association.", "Omar Sy, Charlotte Gainsbourg, Tahar Rahim", "Olivier Nakache, Éric Toledano");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Labyrinthe", "../img/Covers/movies/le-labyrinthe-2014.jpg", 113, 2014, "Action, Mystère, Science-Fiction", "Quand Thomas reprend connaissance, il est pris au piège avec un groupe d’autres garçons dans un labyrinthe géant dont le plan est modifié chaque nuit. Il n’a plus aucun souvenir du monde extérieur, à part d’étranges rêves à propos d’une mystérieuse organisation appelée W.C.K.D.", "Dylan O'Brien, Kaya Scodelario, Will Poulter", "Wes Ball");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Hunger Games - La Révolte : Partie 1", "../img/Covers/movies/hunger-games-la-revolte-partie-1-2014.jpg", 123, 2014, "Action, Aventure, Science-Fiction", "Katniss Everdeen s’est réfugiée dans le District 13 après avoir détruit à jamais l’arène et les Jeux. Sous le commandement de la Présidente Coin, chef du district, et suivant les conseils de ses amis en qui elle a toute confiance, Katniss déploie ses ailes pour devenir le symbole de la rébellion.", "Jennifer Lawrence, Josh Hutcherson, Liam Hemsworth", "Francis Lawrence");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Astérix - Le Domaine des Dieux", "../img/Covers/movies/asterix-le-domaine-des-dieux-2014.jpg", 85, 2014, "Animation, Aventure, Comédie", "Nous sommes en 50 avant Jésus-Christ ; toute la Gaule est occupée par les Romains… Toute ? Non ! Car un village peuplé d'irréductibles Gaulois résiste encore et toujours à l'envahisseur. Exaspéré par la situation, Jules César décide de changer de tactique...", "Roger Carel, Lorànt Deutsch, Laurent Lafitte", "Louis Clichy, Karl Willems, Alexandre Astier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Interstellar", "../img/Covers/movies/interstellar-2014.jpg", 169, 2014, "Aventure, Drame, Science-Fiction", "Le film raconte les aventures d’un groupe d’explorateurs qui utilisent une faille récemment découverte dans l’espace-temps afin de repousser les limites humaines et partir à la conquête des distances astronomiques dans un voyage interstellaire. ", "Matthew McConaughey, Anne Hathaway, Jessica Chastain", "Christopher Nolan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Vacances du petit Nicolas ", "../img/Covers/movies/les-vacances-du-petit-nicolas-2014.jpg", 97, 2014, "Comédie, Famille", "C’est la fin de l’année scolaire. Le moment tant attendu des vacances est arrivé.Le petit Nicolas, ses parents et Mémé prennent la route en direction de la mer, et s’installent pour quelques temps à l’Hôtel Beau-Rivage.", "Valérie Lemercier, Kad Merad, Dominique Lavanant", "Laurent Tirard");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Babysitting", "../img/Covers/movies/babysitting-2014.jpg", 85, 2014, "Comédie", "Faute de baby-sitter pour le week-end, Marc Schaudel confie son fils Remy à Franck, son employé, 'un type sérieux' selon lui. Sauf que Franck a 30 ans ce soir et que Rémy est un sale gosse capricieux. Au petit matin, Marc et sa femme Claire sont réveillés par un appel de la police. Rémy et Franck ont disparu ! Au milieu de leur maison saccagée, la police a retrouvé une caméra. Marc et Claire découvrent hallucinés les images tournées pendant la soirée.", "Philippe Lacheau, Alice David, Vincent Desagnat", "Nicolas Benamou, Philippe Lacheau");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Famille Bélier", "../img/Covers/movies/la-famille-belier-2014.jpg", 106, 2014, "Comédie, Drame", "Dans la famille Bélier, tout le monde est sourd sauf Paula, 16 ans. Elle est une interprète indispensable à ses parents au quotidien, notamment pour l’exploitation de la ferme familiale. Un jour, poussée par son professeur de musique qui lui a découvert un don pour le chant, elle décide de préparer le concours de Radio France. Un choix de vie qui signifierait pour elle l’éloignement de sa famille et un passage inévitable à l’âge adulte.", "Karin Viard, François Damiens, Eric Elmosnino", "Éric Lartigau");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Gardiens de la Galaxie", "../img/Covers/movies/les-gardiens-de-la-galaxie-2014.jpg", 121, 2014, "Action, Aventure, Comédie", "Peter Quill est un aventurier traqué par tous les chasseurs de primes pour avoir volé un mystérieux globe convoité par le puissant Ronan, dont les agissements menacent l’univers tout entier. Lorsqu’il découvre le véritable pouvoir de ce globe et la menace qui pèse sur la galaxie, il conclut une alliance fragile avec quatre aliens disparates : Rocket, un raton laveur fin tireur, Groot, un humanoïde semblable à un arbre, l’énigmatique et mortelle Gamora, et Drax le Destructeur, qui ne rêve que de vengeance.", "Chris Pratt, Vin Diesel, Bradley Cooper", "James Gunn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Transformers : l'âge de l'extinction", "../img/Covers/movies/transformers-l-age-de-l-extinction-2014.jpg", 165, 2014, "Action, Aventure, Science-Fiction", "Quatre ans après les événements mouvementés de 'Transformers : La Face cachée de la Lune', un groupe de puissants scientifiques cherche à repousser, via des Transformers, les limites de la technologie. Au même moment, un père de famille texan découvre un vieux camion qui n’est autre qu’Optimus Prime...", "Mark Wahlberg, Nicola Peltz Beckham, Jack Reynor", "Michael Bay");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Amazing Spider-Man : le destin d'un Héros", "../img/Covers/movies/the-amazing-spider-man-le-destin-d-un-heros-2014.jpg", 142, 2014, "Action, Aventure, Science-Fiction", "Ce n’est un secret pour personne que le combat le plus rude de Spider-Man est celui qu’il mène contre lui-même en tentant de concilier la vie quotidienne de Peter Parker et les lourdes responsabilités de Spider-Man. Mais Peter Parker va se rendre compte qu’il fait face à un conflit de bien plus grande ampleur.", "Andrew Garfield, Emma Stone, Jamie Foxx", "Marc Webb");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les trois frères, le retour", "../img/Covers/movies/les-trois-freres-le-retour-2014.jpg", 106, 2014, "Comédie", "Ils sont trois, Ils sont frères, Ils sont de retour. 15 ans après, Didier, Bernard et Pascal sont enfin réunis... par leur mère...Cette fois sera peut-être la bonne.", "Didier Bourdon, Bernard Campan, Pascal Légitimus", "Didier Bourdon, Bernard Campan, Pascal Légitimus");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Paddington", "../img/Covers/movies/paddington-2014.jpg", 95, 2014, "Aventure, Comédie, Famille", "Paddington raconte l'histoire d'un jeune ours péruvien fraîchement débarqué à Londres, à la recherche d'un foyer et d'une vie meilleure. Il réalise vite que la ville de ses rêves n'est pas aussi accueillante qu'il croyait. Par chance, il rencontre la famille Brown et en devient peu à peu un membre à part entière.", "Hugh Bonneville, Sally Hawkins, Julie Walters", "Paul King");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Maléfique", "../img/Covers/movies/malefique-2014.jpg", 97, 2014, "Action, Aventure, Drame", "Maléfique est une belle jeune femme au coeur pur qui mène une  vie idyllique au sein d’une paisible forêt dans un royaume où règnent le bonheur et l’harmonie. Un jour, une armée d’envahisseurs menace les frontières du pays et Maléfique, n’écoutant que son courage, s’élève en féroce protectrice de cette terre.", "Angelina Jolie, Elle Fanning, Sharlto Copley", "Robert Stromberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Fiston", "../img/Covers/movies/fiston-2014.jpg", 88, 2014, "Comédie", "Depuis qu’il a 7 ans, Alex n’a qu’une obsession : séduire Sandra Valenti, la plus jolie fille d’Aix en Provence et, à ses yeux, la plus jolie fille du monde. Aujourd’hui, il lui faut un plan infaillible pour pouvoir enfin l’aborder.", "Kev Adams, Franck Dubosc, Nora Arnezeder", "Pascal Bourdiaux");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Gone Girl", "../img/Covers/movies/gone-girl-2014.jpg", 149, 2014, "Drame, Mystère, Thriller", "À l’occasion de son cinquième anniversaire de mariage, Nick Dunne signale la disparition de sa femme, Amy. Sous la pression de la police et l’affolement des médias, l’image du couple modèle commence à s’effriter. Très vite, les mensonges de Nick et son étrange comportement amènent tout le monde à se poser la même question : a-t-il tué sa femme ?", "Ben Affleck, Rosamund Pike, Neil Patrick Harris", "David Fincher");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Captain America, le soldat de l'hiver", "../img/Covers/movies/captain-america-le-soldat-de-l-hiver-2014.jpg", 136, 2014, "Action, Aventure, Science-Fiction", "Après les événements cataclysmiques de New York, Steve Rogers aka Captain America vit tranquillement à Washington, D.C. et essaye de s'adapter au monde moderne. Mais quand un collègue du S.H.I.E.L.D. est attaqué, Steve se retrouve impliqué dans un réseau d'intrigues...", "Chris Evans, Samuel L. Jackson, Scarlett Johansson", "Anthony Russo, Joe Russo");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Belle et la Bête", "../img/Covers/movies/la-belle-et-la-bete-2014.jpg", 112, 2014, "Drame, Famille, Fantastique", "1810. Après le naufrage de ses navires, un marchand ruiné doit s’exiler à la campagne avec ses six enfants. Parmi eux se trouve Belle, la plus jeune de ses filles, joyeuse et pleine de grâce.", "Vincent Cassel, Léa Seydoux, André Dussollier", "Christophe Gans");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ninja Turtles", "../img/Covers/movies/ninja-turtles-2014.jpg", 101, 2014, "Action, Aventure, Comédie", "New York City a sombré dans les ténèbres depuis que Shredder et son cruel clan des Foot ont la mainmise sur la ville, des forces de police aux politiciens. Alors que l’avenir semble sans espoir, quatre frères en marge de la société sortent des entrailles de la ville pour accomplir leur destin de Tortues Ninja.", "Megan Fox, Will Arnett, William Fichtner", "Jonathan Liebesman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Yves Saint Laurent", "../img/Covers/movies/yves-saint-laurent-2014.jpg", 106, 2014, "Biopic, Drame", "Paris, 1957. A tout juste 21 ans, Yves Saint Laurent est appelé à prendre en main les destinées de la prestigieuse maison de haute couture fondée par Christian Dior, récemment décédé. Lors de son premier défilé triomphal, il fait la connaissance de Pierre Bergé, rencontre qui va bouleverser sa vie.", "Pierre Niney, Guillaume Gallienne, Charlotte Le Bon", "Jalil Lespert");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Barbecue", "../img/Covers/movies/barbecue-2014.jpg", 98, 2014, "Comédie", "Pour ses 50 ans, Antoine a reçu un cadeau original : un infarctus. A partir de maintenant, il va devoir 'faire attention'. Or, Antoine a passé sa vie entière à faire attention : attention à sa santé, à ce qu'il mangeait, attention à sa famille, à accepter les travers de ses amis, et à avaler de trop nombreuses couleuvres…", "Lambert Wilson, Franck Dubosc, Florence Foresti", "Eric Lavaine");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Pingouins de Madagascar", "../img/Covers/movies/les-pingouins-de-madagascar-2014.jpg", 92, 2014, "Animation, Action, Aventure", "Vous pensiez connaître les Pingouins de Madagascar ? Pourtant, les quatre frères cachent un lourd secret. Ils sont en fait… agents secrets ! Pour sauver le monde du terrible Docteur Octavius, les pingouins devront s’associer à la très chic organisation de la North Wind menée par le superbe husky au nom classé secret.", "Tom McGrath, Chris Miller, Christopher Knights", "Eric Darnell, Simon J. Smith");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("300 : La naissance d'un Empire", "../img/Covers/movies/300-la-naissance-d-un-empire-2014.jpg", 102, 2014, "Action, Drame, Guerre", "Le général grec Thémistocle tente de mobiliser toutes les forces de la Grèce pour mener une bataille qui changera à jamais le cours de la guerre. Il doit désormais affronter les redoutables Perses, emmenés par Xerxès, homme devenu dieu, et Artémise, à la tête de la marine perse…", "Sullivan Stapleton, Eva Green, Lena Headey", "Noam Murro");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Annabelle", "../img/Covers/movies/annabelle-2014.jpg", 99, 2014, "Horreur, Mystère, Thriller", "John Form est certain d'avoir déniché le cadeau de ses rêves pour sa femme Mia, qui attend un enfant. Il s'agit d'une poupée ancienne, très rare, habillée dans une robe de mariée d'un blanc immaculé. Mais Mia, d'abord ravie par son cadeau, va vite déchanter...", "Ward Horton, Annabelle Wallis, Alfre Woodard", "John R. Leonetti");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Grande Aventure Lego", "../img/Covers/movies/la-grande-aventure-lego-2014.jpg", 100, 2014, "Animation, Action, Aventure", "Emmet est un petit personnage banal et conventionnel que l'on prend par erreur pour un être extraordinaire, capable de sauver le monde. Il se retrouve entraîné, parmi d'autres, dans un périple des plus mouvementés, dans le but de mettre hors d'état de nuire un redoutable despote. Mais le pauvre Emmet n'est absolument pas prêt à relever un tel défi !", "Chris Pratt, Will Ferrell, Elizabeth Banks", "Phil Lord, Christopher Miller");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Minuscule - La vallée des fourmis perdues", "../img/Covers/movies/minuscule-la-vallee-des-fourmis-perdues-2014.jpg", 89, 2013, "Animation, Action, Aventure", "Dans une paisible clairière, les objets d'un pique-nique laissés à l'abandon après un orage vont être le point de départ d'une guerre entre deux bandes rivales de fourmis, ayant pour enjeu une boîte de sucres. C'est dans cette tourmente qu'une jeune coccinelle va se lier d'amitié avec une fourmi noire et l'aider à sauver sa fourmilière des terribles fourmis rouges !", "Holly J. Barrett, Jack Blessing, Rachel Crane", "Hélène Giraud, Thomas Szabo");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Divergente", "../img/Covers/movies/divergente-2014.jpg", 139, 2014, "Action, Aventure, Mystère", "Tris vit dans un monde post apocalyptique où la société est divisée en cinq clans (Audacieux, Erudits, Altruistes, Sincères, Fraternels). A 16 ans, elle doit choisir son appartenance pour le reste de sa vie. Cas rarissime, son test d’aptitudes n’est pas concluant ; elle est Divergente.", "Shailene Woodley, Theo James, Kate Winslet", "Neil Burger");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Grand Budapest Hotel", "../img/Covers/movies/the-grand-budapest-hotel-2014.jpg", 99, 2014, "Aventure, Comédie, Crime", "Le film retrace les aventures de Gustave H, l’homme aux clés d’or d’un célèbre hôtel européen de l’entre-deux-guerres et du garçon d’étage Zéro Moustafa, son allié le plus fidèle.", "Ralph Fiennes, F. Murray Abraham, Mathieu Amalric", "Wes Anderson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("M. Peabody et Sherman : Les Voyages dans le temps", "../img/Covers/movies/m-peabody-et-sherman-2014.jpg", 92, 2014, "Animation, Aventure, Comédie", "M.Peabody est la personne la plus intelligente au monde. Il est à la fois lauréat du prix Nobel, champion olympique, grand chef cuisinier... et il se trouve aussi être un chien ! Bien qu’il soit un génie dans tous les domaines, M. Peabody est sur le point de relever son plus grand défi...", "Ty Burrell, Max Charles, Stephen Colbert", "Rob Minkoff");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Godzilla", "../img/Covers/movies/godzilla-2014.jpg", 123, 2014, "Action, Aventure, Science-Fiction", "Le physicien Joseph Brody a perdu sa femme il y a 15 ans quand un incident nucléaire a irradié la région de Tokyo. La thèse officielle parle de tremblement de terre mais le scientifique est sceptique et mène son enquête avec son fils Ford, soldat dans la Navy. En fait de catastrophe naturelle, il s'agit plutôt des dégâts d'une créature gigantesque créée à la suite d'essais nucléaires dans le Pacifique.", "Aaron Taylor-Johnson, Elizabeth Olsen, Bryan Cranston", "Gareth Edwards");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Sous Les Jupes Des Filles", "../img/Covers/movies/sous-les-jupes-des-filles-2014.jpg", 116, 2014, "Comédie, Romance", "Paris. 28 premiers jours du printemps. 11 femmes. Mères de famille, femmes d'affaires, copines, maîtresses ou épouses... Toutes représentent une facette de la femme d'aujourd'hui.", "Isabelle Adjani, Alice Belaïdi, Laetitia Casta", "Audrey Dana");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La French", "../img/Covers/movies/la-french-2014.jpg", 135, 2014, "Action, Crime, Thriller", "Marseille. 1975. Pierre Michel, jeune magistrat venu de Metz avec femme et enfants, est nommé juge du grand banditisme. Il décide de s’attaquer à la French Connection, organisation mafieuse qui exporte l’héroïne dans le monde entier. N’écoutant aucune mise en garde, le juge Michel part seul en croisade contre Gaëtan Zampa, figure emblématique du milieu et parrain intouchable. Mais il va rapidement comprendre que, pour obtenir des résultats, il doit changer ses méthodes.", "Jean Dujardin, Gilles Lellouche, Céline Sallette", "Cédric Jimenez");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Noé", "../img/Covers/movies/noe-2014.jpg", 138, 2014, "Action, Aventure, Drame", "Russell Crowe est Noé, un homme promis à un destin exceptionnel alors qu’un déluge apocalyptique va détruire le monde. La fin du monde… n’est que le commencement.", "Russell Crowe, Jennifer Connelly, Anthony Hopkins", "Darren Aronofsky");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Monuments Men", "../img/Covers/movies/monuments-men-2014.jpg", 118, 2014, "Comédie, Drame, Historique", "Venus de 13 pays différents, les Monuments Men se lancent dans la plus grande chasse au trésor du XXème siècle : retrouver les œuvres d'art volées par les nazis durant la Seconde Guerre mondiale.", "George Clooney, Matt Damon, Bill Murray", "George Clooney");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Non-Stop", "../img/Covers/movies/non-stop-2014.jpg", 106, 2014, "Action, Mystère, Thriller", "Bill Marks est un vétéran aguerri de la police de l'air : il vient de prendre place à bord d'un énième vol New York-Londres. Alors qu'il survole l'Atlantique avec 200 autres passagers, il reçoit de mystérieux SMS d'un inconnu qui affirme être à bord et vouloir assassiner un passager toutes les 20 minutes si le gouvernement ne lui transfère pas 150 millions de dollars.", "Liam Neeson, Julianne Moore, Scoot McNairy", "Jaume Collet-Serra");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Edge of Tomorrow", "../img/Covers/movies/edge-of-tomorrow-2014.jpg", 113, 2014, "Action, Aventure, Science-Fiction", "Dans un futur proche, des hordes d'extraterrestres extrêmement organisés, appelés les Mimics, ont livré une bataille acharnée contre la Terre, réduisant les grandes villes en cendres et causant la mort de millions d'êtres humains.", "Tom Cruise, Emily Blunt, Bill Paxton", "Doug Liman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Opération Casse-noisette", "../img/Covers/movies/operation-casse-noisette-2014.jpg", 85, 2014, "Animation, Aventure, Comédie", "Surly est un écureuil malin et ingénieux. A peine débarqué en ville, il repère un magasin de noix avec un stock suffisant pour nourrir tous les animaux de la forêt pendant l’hiver. Mais pour pénétrer cette forteresse, il va avoir besoin d’aide. Assisté de ses amis, il va mettre au point un plan rocambolesque pour organiser le vol du siècle. Evidemment, rien ne va se passer comme prévu…", "Will Arnett, Brendan Fraser, Liam Neeson", "Peter Lepeniotis");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le crocodile du Botswanga", "../img/Covers/movies/le-crocodile-du-botswanga-2014.jpg", 90, 2014, "Comédie", "Leslie Konda, jeune footballer français talentueux, repéré à son adolescence par Didier, un agent de faible envergure qui a su le prendre sous sa coupe, vient de signer son premier contrat d’attaquant dans un grand club espagnol.", "Thomas Ngijol, Fabrice Eboué, Claudia Tagbo", "Lionel Steketee, Fabrice Eboué");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Tu veux ou tu veux pas", "../img/Covers/movies/tu-veux-ou-tu-veux-pas-2014.jpg", 88, 2014, "Comédie, Romance", "Lambert, sex addict repenti, tente de se racheter une conduite en devenant… conseiller conjugal. Abstinent depuis plusieurs mois, la situation se complique lorsqu’il recrute une assistante, la séduisante Judith, dont la sexualité débridée va très vite mettre ses résolutions à rude épreuve…", "Sophie Marceau, Patrick Bruel, André Wilms", "Tonie Marshall");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Expendables 3", "../img/Covers/movies/expendables-3-2014.jpg", 126, 2014, "Action, Aventure, Thriller", "Barney, Christmas et le reste de l’équipe affrontent Conrad Stonebanks, qui fut autrefois le fondateur des Expendables avec Barney. Stonebanks devint par la suite un redoutable trafiquant d’armes, que Barney fut obligé d’abattre… Du moins, c’est ce qu’il croyait.", "Sylvester Stallone, Jason Statham, Jet Li", "Patrick Hughes");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Nos étoiles contraires", "../img/Covers/movies/nos-etoiles-contraires-2014.jpg", 126, 2014, "Drame, Romance", "Hazel Grace et Gus sont deux adolescents hors-normes, partageant un humour ravageur et le mépris des conventions. Leur relation est elle-même inhabituelle, étant donné qu’ils se sont rencontrés et sont tombés amoureux lors d'un groupe de soutien pour les malades du cancer.", "Shailene Woodley, Ansel Elgort, Nat Wolff", "Josh Boone");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Clochette et la fée pirate", "../img/Covers/movies/clochette-et-la-fee-pirate-2014.jpg", 78, 2014, "Animation, Aventure, Famille, Fantastique", "Zarina est une fée qui se sent incomprise. Chargée de veiller à la sécurité de la poussière de fée, elle décide par lassitude de tout abandonner, d’emmener avec elle un peu du précieux trésor et de se liguer avec la bande de pirates qui sillonne les mers environnantes.", "Mae Whitman, Christina Hendricks, Tom Hiddleston, Lucy Liu", "Peggy Holmes");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Planes 2", "../img/Covers/movies/planes-2-2014.jpg", 83, 2014, "Animation, Aventure, Comédie", "Quand Dusty apprend que son moteur est endommagé et qu’il ne pourra peut-être plus jamais participer à une course, il décide de changer de voie et de se lancer dans la guerre contre le feu au sein d’une équipe improbable en charge de la protection du parc national de Piston Peak.", "Dane Cook, Ed Harris, Julie Bowen", "Bobs Gannaway");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Star Wars: Episode VII - Le Réveil de la Force", "../img/Covers/movies/star-wars-7-2015.jpg", 138, 2015, "Action, Aventure, Science-Fiction", "Dans une galaxie lointaine, très lointaine, un nouvel épisode de la saga 'Star Wars', 30 ans après les événements du 'Retour du Jedi'.", "Daisy Ridley, John Boyega, Oscar Isaac", "J.J. Abrams");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Minions", "../img/Covers/movies/les-minions-2015.jpg", 91, 2015, "Animation, Aventure, Comédie", "A l'origine de simples organismes monocellulaires de couleur jaune, les Minions ont évolué au cours des âges au service de maîtres plus abjectes les uns que les autres. Les disparitions répétitives de ceux-ci, des tyrannosaures à Napoléon, ont plongé les Minions dans une profonde dépression.", "Sandra Bullock, Jon Hamm, Michael Keaton", "Kyle Balda, Pierre Coffin");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Jurassic World", "../img/Covers/movies/jurassic-world-2015.jpg", 124, 2015, "Action, Aventure, Science-Fiction", "L'Indominus Rex, un dinosaure génétiquement modifié, pure création de la scientifique Claire Dearing, sème la terreur dans le fameux parc d'attraction. Les espoirs de mettre fin à cette menace reptilienne se portent alors sur le dresseur de raptors Owen Grady et sa cool attitude.", "Chris Pratt, Bryce Dallas Howard, Ty Simpkins", "Colin Trevorrow");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Spectre", "../img/Covers/movies/spectre-2015.jpg", 148, 2015, "Action, Aventure, Thriller", "Un message cryptique surgi du passé entraîne James Bond dans une mission très personnelle à Mexico puis à Rome, où il rencontre Lucia Sciarra, la très belle veuve d’un célèbre criminel. Bond réussit à infiltrer une réunion secrète révélant une redoutable organisation baptisée Spectre.", "Daniel Craig, Christoph Waltz, Léa Seydoux", "Sam Mendes");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Fast & Furious 7", "../img/Covers/movies/fast-and-furious-7-2015.jpg", 137, 2015, "Action, Crime, Thriller", "Dominic Toretto et sa 'famille' doivent faire face à Deckard Shaw, bien décidé à se venger de la mort de son frère.", "Vin Diesel, Paul Walker, Dwayne Johnson", "James Wan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Nouvelles Aventures D'Aladin", "../img/Covers/movies/les-nouvelles-aventures-d-aladin-2015.jpg", 107, 2015, "Comédie", "À la veille de Noël, Sam et son meilleur pote Khalid se déguisent en Père-Noël afin de dérober tout ce qu’ils peuvent aux Galeries Lafayette. Mais Sam est rapidement coincé par des enfants et doit leur raconter une histoire... l’histoire d’Aladin... enfin Sa version.", "Kev Adams, Jean-Paul Rouve, Vanessa Guide", "Arthur Benzaquen");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Vice Versa", "../img/Covers/movies/vice-versa-2015.jpg", 95, 2015, "Animation, Aventure, Comédie", "Au Quartier Général, le centre de contrôle situé dans la tête de la petite Riley, 11 ans, cinq Émotions sont au travail. Lorsque la famille de Riley emménage dans une grande ville, avec tout ce que cela peut avoir d’effrayant, les Émotions ont fort à faire pour guider la jeune fille durant cette difficile transition.", "Amy Poehler, Bill Hader, Lewis Black", "Pete Docter, Ronnie Del Carmen");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Avengers : L'ère d'Ultron", "../img/Covers/movies/avengers-2-2015.jpg", 141, 2015, "Action, Aventure, Science-Fiction", "Alors que Tony Stark tente de relancer un programme de maintien de la paix jusque-là suspendu, les choses tournent mal et les super-héros Iron Man, Captain America, Thor, Hulk, Black Widow et Hawkeye vont devoir à nouveau unir leurs forces pour combattre le plus puissant de leurs adversaires : Ultron...", "Robert Downey Jr., Chris Evans, Mark Ruffalo", "Joss Whedon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Cinquante Nuances de Grey", "../img/Covers/movies/cinquante-nuances-de-grey-2015.jpg", 125, 2015, "Drame, Romance, Thriller", "L'histoire d'une romance passionnelle, et sexuelle, entre un jeune homme riche amateur de femmes, et une étudiante vierge de 22 ans.", "Dakota Johnson, Jamie Dornan, Jennifer Ehle", "Sam Taylor-Johnson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Profs 2", "../img/Covers/movies/les-profs-2-2015.jpg", 92, 2015, "Comédie", "Les pires Profs de France débarquent en Angleterre pour une mission ultra-secrète. Avec Boulard, le Roi des cancres, ils sont parachutés dans le meilleur lycée du pays, et ils vont appliquer leurs célèbres méthodes sur la future élite de la nation.", "Kev Adams, Isabelle Nanty, Didier Bourdon", "Pierre-François Martin-Laval");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("American Sniper", "../img/Covers/movies/american-sniper-2014.jpg", 133, 2014, "Action, Biopic, Drame", "Tireur d'élite des Navy SEAL, Chris Kyle est envoyé en Irak dans un seul but : protéger ses camarades. Sa précision chirurgicale sauve d'innombrables vies humaines sur le champ de bataille et, tandis que les récits de ses exploits se multiplient, il décroche le surnom de 'La Légende'.", "Bradley Cooper, Sienna Miller, Kyle Gallner", "Clint Eastwood");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Labyrinthe : La Terre brûlée", "../img/Covers/movies/le-labyrinthe-2-2015.jpg", 131, 2015, "Action, Aventure, Science-Fiction", "Thomas et les autres Blocards vont devoir faire face à leur plus grand défi, rechercher des indices à propos de la mystérieuse et puissante organisation connue sous le nom de WICKED. Leur périple les amène à la Terre Brûlée, un paysage de désolation rempli d'obstacles inimaginables.", "Dylan O'Brien, Kaya Scodelario, Thomas Brodie-Sangster", "Wes Ball");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Papa ou maman", "../img/Covers/movies/papa-ou-maman-2015.jpg", 85, 2015, "Comédie, Drame, Romance", "Florence et Vincent Leroy ont tout réussi. Leurs métiers, leur mariage, leurs enfants. Et aujourd’hui, c’est leur divorce qu’ils veulent réussir. Les ex-époux modèles se déclarent la guerre : et ils vont tout faire pour NE PAS avoir la garde des enfants.", "Laurent Lafitte, Marina Foïs, Alexandre Desrousseaux", "Martin Bourboulon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Mission: Impossible - Rogue Nation", "../img/Covers/movies/mission-impossible-5-2015.jpg", 131, 2015, "Action, Aventure, Thriller", "L’équipe IMF (Impossible Mission Force) est dissoute et Ethan Hunt se retrouve désormais isolé,alors que le groupe doit affronter un réseau d’agents spéciaux particulièrement entraînés, le Syndicat.", "Tom Cruise, Rebecca Ferguson, Jeremy Renner", "Christopher McQuarrie");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Hunger Games - La Révolte : Partie 2", "../img/Covers/movies/hunger-games-4-2015.jpg", 137, 2015, "Action, Aventure, Science-Fiction", "Alors que Panem est ravagé par une guerre désormais totale, Katniss et le Président Snow vont s’affronter pour la dernière fois. Katniss et ses plus proches amis – Gale, Finnick, et Peeta – sont envoyés en mission pour le District 13 : ils vont risquer leur vie pour tenter d’assassiner le Président Snow, qui s’est juré de détruire Katniss. Les pièges mortels, les ennemis et les choix déchirants qui attendent Katniss seront des épreuves bien pires que tout ce qu’elle a déjà pu affronter dans l’arène…", "Jennifer Lawrence, Josh Hutcherson, Liam Hemsworth", "Francis Lawrence");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Taken 3", "../img/Covers/movies/taken-3-2014.jpg", 108, 2014, "Action, Aventure, Crime", "L’ex-agent spécial Bryan Mills voit son retour à une vie tranquille bouleversé lorsqu’il est accusé à tort du meurtre de son ex-femme, chez lui, à Los Angeles. En fuite et traqué par l’inspecteur Dotzler, Mills va devoir employer ses compétences particulières une dernière fois....", "Liam Neeson, Forest Whitaker, Maggie Grace", "Olivier Megaton");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Babysitting 2", "../img/Covers/movies/babysitting-2-2015.jpg", 93, 2015, "Comédie", "Sonia souhaite présenter Franck à son père, Jean-Pierre directeur d’un hôtel écologique au Brésil. Toute la bande s’y retrouve ainsi pour y passer des vacances de rêve. Un matin, les garçons partent en excursion dans la forêt amazonienne. Jean-Pierre leur confie sa mère acariâtre Yolande. Le lendemain, ils ont tous disparu… On a juste retrouvé la petite caméra avec laquelle ils étaient partis. Sonia et son père vont regarder cette vidéo pour retrouver leur trace…", "Philippe Lacheau, Alice David, Vincent Desagnat", "Nicolas Benamou, Philippe Lacheau");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Seul sur Mars", "../img/Covers/movies/seul-sur-mars-2015.jpg", 144, 2015, "Aventure, Drame, Science-Fiction", "Lors d’une expédition sur Mars, l’astronaute Mark Watney (Matt Damon) est laissé pour mort par ses coéquipiers, une tempête les ayant obligés à décoller en urgence. Mais Mark a survécu et il est désormais seul, sans moyen de repartir, sur une planète hostile.", "Matt Damon, Jessica Chastain, Kristen Wiig", "Ridley Scott");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Divergente 2 : l’insurrection", "../img/Covers/movies/divergente-2-2015.jpg", 119, 2015, "Action, Aventure, Science-Fiction", "Dans un monde post-apocalyptique où la société a été réorganisée autour de 5 factions, Tris a mis à jour un complot mené par la faction dominante, les Erudits, dirigés par Janine. Abandonnant une ville à feu et à sang, à la recherche d’alliés, Tris et Four sont désormais traqués par les autorités.", "Shailene Woodley, Ansel Elgort, Theo James", "Robert Schwentke");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Mad Max: Fury Road", "../img/Covers/movies/mad-max-fury-road-2015.jpg", 120, 2015, "Action, Aventure, Science-Fiction", "Hanté par un lourd passé, Mad Max estime que le meilleur moyen de survivre est de rester seul. Cependant, il se retrouve embarqué par une bande qui parcourt la Désolation à bord d'un véhicule militaire piloté par l'Imperator Furiosa.", "Tom Hardy, Charlize Theron, Nicholas Hoult", "George Miller");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Voyage d'Arlo", "../img/Covers/movies/le-voyage-d-arlo-2015.jpg", 93, 2015, "Animation, Aventure, Comédie", "Et si la catastrophe cataclysmique qui a bouleversé la Terre et provoqué l'extinction des dinosaures n'avait jamais eu lieu ? Et si les dinosaures ne s'étaient jamais éteints, et vivaient parmi nous de nos jours ?", "Jeffrey Wright, Frances McDormand, Maleah Nipay-Padilla", "Peter Sohn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Hotel Transylvanie 2", "../img/Covers/movies/hotel-transylvanie-2-2015.jpg", 89, 2015, "Animation, Aventure, Comédie", "Dracula est dorénavant grand-père et est bien décidé à initier son petit garçon à la crinière rousse aux rudiments du monde de l'épouvante. Bien sûr, rien ne va se passer comme prévu pour notre drôlissime vampire et sa bande de monstres - Frankenstein, la Momie, l'Homme invisible, le Loup-garou et le Blob...", "Adam Sandler, Andy Samberg, Selena Gomez", "Genndy Tartakovsky");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Petit Prince", "../img/Covers/movies/le-petit-prince-2015.jpg", 108, 2015, "Animation, Aventure, Drame", "Une fillette sérieuse fait la rencontre d'un vieil aviateur, excentrique et facétieux. Celui-ci lui conte l'histoire d'un mystérieux Petit Prince.", "Jeff Bridges, Mackenzie Foy, Rachel McAdams", "Mark Osborne");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ant-Man", "../img/Covers/movies/ant-man-2015.jpg", 117, 2015, "Action, Comédie, Science-Fiction", "Scott Lang, cambrioleur de haut vol, va devoir apprendre à se comporter en héros et aider son mentor, le Dr Hank Pym, à protéger le secret de son spectaculaire costume d’Ant-Man, afin d’affronter une effroyable menace…", "Paul Rudd, Michael Douglas, Corey Stoll", "Peyton Reed");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Cendrillon", "../img/Covers/movies/cendrillon-2015.jpg", 105, 2015, "Aventure, Drame, Famille", "Dans cette histoire, le père de la jeune Ella, un marchand, s’est remarié après la mort tragique de la mère de la jeune fille. Pour l’amour de son père, Ella accueille à bras ouverts sa nouvelle belle-mère, Lady Tremaine, et ses filles Anastasia et Drisella. Mais lorsque le père d’Ella disparaît à son tour, la jeune fille se retrouve à la merci de sa nouvelle famille, jalouse et cruelle.", "Lily James, Cate Blanchett, Richard Madden", "Kenneth Branagh");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Kingsman : Services secrets", "../img/Covers/movies/kingsman-2015.jpg", 129, 2014, "Action, Aventure, Comédie", "KINGSMAN, l’élite du renseignement britannique en costumes trois pièces, est à la recherche de sang neuf. Pour recruter leur nouvel agent secret, elle doit faire subir un entrainement de haut vol à de jeunes privilégiés aspirant au job rêvé.", "Colin Firth, Taron Egerton, Samuel L. Jackson", "Matthew Vaughn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Nouveaux Héros", "../img/Covers/movies/les-nouveaux-heros-2014.jpg", 102, 2014, "Animation, Action, Aventure", "Un petit génie de la robotique nommé Hiro Hamada découvre qu’un complot criminel menace de détruire la ville de San Fransokyo. Avec l’aide de son plus proche ami, Baymax le robot infirmier, et de ses compagnons qu’il va transformer en une bande de superhéros high-tech, Hiro va tout faire pour sauver la ville et sa population de l’infâme Yokai…", "Ryan Potter, Scott Adsit, Jamie Chung", "Don Hall, Chris Williams");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Nuit au musée : Le Secret des Pharaons", "../img/Covers/movies/la-nuit-au-musee-3-2014.jpg", 98, 2014, "Aventure, Comédie, Famille", "Ben Stiller réendosse le rôle de Larry, le gardien de musée le plus survolté, dans le dernier volet de la saga La Nuit au Musée. Il quitte New York pour Londres où il va vivre sa plus grande aventure. Accompagné de ses amis le Président Roosevelt, Attila, le Romain Octavius, Jedediah et le pharaon Ahkmenrah, ainsi que de nouveaux personnages délirants, il va tenter de sauver la magie avant qu’elle ne disparaisse à tout jamais.", "Ben Stiller, Robin Williams, Owen Wilson", "Shawn Levy");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Bis", "../img/Covers/movies/bis-2015.jpg", 98, 2015, "Comédie, Fantastique", "Éric et Patrice sont amis depuis le lycée. Au fil des années, chacun a pris un chemin très différent : d’un côté Éric, hédoniste sans attaches aux multiples conquêtes, et de l’autre Patrice, père de famille « monogame » à la vie bien rangée...", "Franck Dubosc, Kad Merad, Alexandra Lamy", "Dominique Farrugia");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Terminator Genisys", "../img/Covers/movies/terminator-5-2015.jpg", 126, 2015, "Action, Aventure, Science-Fiction", "Le leader de la résistance John Connor envoie le sergent Kyle Reese dans le passé pour protéger sa mère, Sarah Connor et préserver l'avenir de l’humanité. Des événements inattendus provoquent une fracture temporelle et Sarah et Kyle se retrouvent dans une nouvelle version du passé.", "Arnold Schwarzenegger, Jason Clarke, Emilia Clarke", "Alan Taylor");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Belle et Sébastien : L'aventure continue", "../img/Covers/movies/belle-et-sebastien-2-2015.jpg", 97, 2015, "Aventure, Famille", "D'après l'oeuvre de Cécile Aubry : Septembre 1945. Au village, on a fêté la fin de la guerre. Sébastien a grandi, il a maintenant 10 ans. Belle et lui attendent impatiemment le retour d’Angelina... Mais Angelina ne revient pas. Elle a disparu  dans un accident d’avion au cœur des forêts transalpines.", "Félix Bossuet, Tchéky Karyo, Thierry Neuvic", "Christian Duguay");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Bob l'éponge - Le film : Un héros sort de l'eau", "../img/Covers/movies/bob-l-eponge-2-2015.jpg", 92, 2015, "Animation, Aventure, Comédie", "Tout baigne à Bikini Bottom pour Bob l’éponge, l’éternel optimiste et ses amis: Patrick l’étoile de mer fidèle, Carlo le calamar égoïste, Sandy l’écureuil et Monsieur Krabs, le crustacé obsédé par l’argent! Cependant tout bascule quand la recette du pâté de crabe est volée par le diabolique pirate Steak Barbare.", "Tom Kenny, Antonio Banderas, Bill Fagerbakke", "Paul Tibbitt, Mike Mitchell");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("En route !", "../img/Covers/movies/en-route-2015.jpg", 94, 2015, "Animation, Aventure, Comédie", "Les BOOVS, aliens à l’ego surdimensionné, choisissent, pour échapper à leurs ennemis jurés les GORGS, de faire de la Terre leur nouvelle planète d’adoption. Mais OH, l’un d’entre eux, va révéler accidentellement la cachette de son peuple...", "Jim Parsons, Rihanna, Steve Martin", "Tim Johnson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les 4 Fantastiques", "../img/Covers/movies/les-4-fantastiques-2015.jpg", 100, 2015, "Action, Aventure, Science-Fiction", "Adaptation moderne et résolument nouvelle de la plus ancienne équipe de super-héros Marvel, le film se concentre sur quatre jeunes génies qui se retrouvent projetés dans un univers alternatif et dangereux, qui modifie leurs formes physiques mais aussi leurs vies de façon radicale.", "Miles Teller, Kate Mara, Michael B. Jordan", "Josh Trank");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("San Andreas", "../img/Covers/movies/san-andreas-2015.jpg", 114, 2015, "Action, Aventure, Thriller", "Lorsque la tristement célèbre Faille de San Andreas finit par s'ouvrir, et par provoquer un séisme de magnitude 9 en Californie, un pilote d'hélicoptère de secours en montagne et la femme dont il s'est séparé quittent Los Angeles pour San Francisco dans l'espoir de sauver leur fille unique.", "Dwayne Johnson, Carla Gugino, Alexandra Daddario", "Brad Peyton");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ted 2", "../img/Covers/movies/ted-2-2015.jpg", 115, 2015, "Comédie", "Nouveaux mariés, Ted et Tami-Lynn essayent d’avoir un bébé et demandent à John d’être le donneur en vue d’une insémination artificielle. Cependant, s’il veut avoir la garde de l’enfant, Ted va devoir prouver devant un tribunal qu’il est véritablement humain.", "Mark Wahlberg, Seth MacFarlane, Amanda Seyfried", "Seth MacFarlane");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Imitation Game", "../img/Covers/movies/imitation-game-2014.jpg", 114, 2014, "Biopic, Drame, Thriller", "1940 : Alan Turing, mathématicien, cryptologue, est chargé par le gouvernement Britannique de percer le secret de la célèbre machine de cryptage allemande Enigma, réputée inviolable.", "Benedict Cumberbatch, Keira Knightley, Matthew Goode", "Morten Tyldum");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Shaun le mouton", "../img/Covers/movies/shaun-le-mouton-2015.jpg", 85, 2015, "Animation, Aventure, Comédie", "Lorsque qu’une blague de Shaun entraîne accidentellement le fermier jusqu’à la Grande Ville, Shaun, Bitzer et le reste du troupeau se retrouvent embarqués dans une aventure complêêêêtement inattendue en plein grande ville… Shaun arrivera-t-il à retrouver le Fermier dans cette ville étrangère et inconnue avant de s’y perdre pour toujours ?", "Justin Fletcher, John Sparkes, Omid Djalili", "Mark Burton, Richard Starzak");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Zootopie", "../img/Covers/movies/zootopie-2016.jpg", 108, 2016, "Animation, Aventure, Comédie", "Zootopie est une ville qui ne ressemble à aucune autre : seuls les animaux y habitent ! Lorsque l'adorable lapine Judy Hopps fait son entrée dans la police, elle s’attaque à une épineuse affaire, l'obligeant à faire équipe avec Nick Wilde, un renard à la langue bien pendue et véritable virtuose de l’arnaque…", "Ginnifer Goodwin, Jason Bateman, Idris Elba", "Byron Howard, Rich Moore, Jared Bush");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Tuche 2 - Le rêve américain", "../img/Covers/movies/les-tuche-2-2016.jpg", 94, 2016, "Comédie", "À l’occasion de l’anniversaire de « coin-coin », le benjamin de la fratrie, la famille Tuche part le retrouver aux États-Unis : les choses ne vont pas se passer comme prévu, mais alors pas du tout.", "Jean-Paul Rouve, Isabelle Nanty, Claire Nadeau", "Olivier Baroux");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Vaiana, la légende du bout du monde", "../img/Covers/movies/vaiana-2016.jpg", 107, 2016, "Animation, Aventure, Comédie", "C’est depuis les îles océaniennes du Pacifique Sud que la jeune Vaiana, en navigatrice émérite, décide d’entamer ses recherches pour retrouver une île aussi mystérieuse que fabuleuse. Au cours de cette traversée au long cours, elle va faire équipe avec son idole, le légendaire demi-dieu Maui.", "Auli'i Cravalho, Dwayne Johnson, Rachel House", "Ron Clements, John Musker, Don Hall");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Rogue One: A Star Wars Story", "../img/Covers/movies/rogue-one-2016.jpg", 133, 2016, "Action, Aventure, Science-Fiction", "Situé entre les épisodes III et IV de la saga Star Wars, le film nous entraîne aux côtés d’individus ordinaires qui, pour rester fidèles à leurs valeurs, vont tenter l’impossible au péril de leur vie. Ils n’avaient pas prévu de devenir des héros, mais dans une époque de plus en plus sombre, ils vont devoir dérober les plans de l’Étoile de la Mort, l’arme de destruction ultime de l’Empire.", "Felicity Jones, Diego Luna, Alan Tudyk", "Gareth Edwards");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Revenant", "../img/Covers/movies/the-revenant-2015.jpg", 156, 2015, "Action, Aventure, Drame", "Dans une Amérique profondément sauvage, Hugh Glass, un trappeur, est attaqué par un ours et grièvement blessé. Abandonné par ses équipiers, il est laissé pour mort. Mais Glass refuse de mourir. Seul, armé de sa volonté et porté par l’amour qu’il voue à sa femme et à leur fils, Glass entreprend un voyage de plus de 300 km dans un environnement hostile, sur la piste de l’homme qui l’a trahi. Sa soif de vengeance va se transformer en une lutte héroïque pour braver tous les obstacles, revenir chez lui et trouver la rédemption.", "Leonardo DiCaprio, Tom Hardy, Will Poulter", "Alejandro González Iñárritu ");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Animaux fantastiques", "../img/Covers/movies/les-animaux-fantastiques-2016.jpg", 132, 2016, "Aventure, Famille, Fantastique", "1926. Norbert Dragonneau rentre à peine d'un périple à travers le monde où il a répertorié un bestiaire extraordinaire de créatures fantastiques. Il pense faire une courte halte à New York mais une série d'événements et de rencontres inattendues risquent de prolonger son séjour.", "Eddie Redmayne, Katherine Waterston, Alison Sudol", "David Yates");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Deadpool", "../img/Covers/movies/deadpool-2016.jpg", 108, 2016, "Action, Comédie", "À l'origine, il s'appelle Wade Wilson : un ancien militaire des Forces Spéciales devenu mercenaire. Après avoir subi une expérimentation hors norme qui va accélérer ses pouvoirs de guérison, il va devenir Deadpool. Armé de ses nouvelles capacités et d'un humour noir survolté, Deadpool va traquer l'homme qui a bien failli anéantir sa vie.", "Ryan Reynolds, Morena Baccarin, T.J. Miller", "Tim Miller");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Comme des bêtes", "../img/Covers/movies/comme-des-betes-2016.jpg", 87, 2016, "Animation, Aventure, Comédie", "La vie secrète que mènent nos animaux domestiques une fois que nous les laissons seuls à la maison pour partir au travail ou à l’école.", "Louis C.K., Eric Stonestreet, Kevin Hart", "Chris Renaud");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Livre de la jungle", "../img/Covers/movies/le-livre-de-la-jungle-2016.jpg", 106, 2016, "Animation, Action, Aventure", "Les aventures de Mowgli, un petit homme élevé dans la jungle par une famille de loups. Mais Mowgli n’est plus le bienvenu dans la jungle depuis que le redoutable tigre Shere Khan, qui porte les cicatrices des hommes, promet d’éliminer celui qu’il considère comme une menace. ", "Neel Sethi, Bill Murray, Ben Kingsley", "Jon Favreau");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("L'Âge de Glace : les Lois de l'Univers", "../img/Covers/movies/l-age-de-glace-5-2016.jpg", 94, 2016, "Animation, Aventure, Comédie", "Alors qu'une météorite menace le monde de l'Âge de Glace, Sid, Manny, Diego et le reste de la bande vont quitter leur foyer et embarquer dans une nouvelle aventure pleine de comédie, parcourant de nouvelles terres exotiques et rencontrant une galerie de parsonnages hauts en couleur.", "Ray Romano, Denis Leary, John Leguizamo", "Michael Thurmeier, Galen T. Chu");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Monde de Dory", "../img/Covers/movies/le-monde-de-dory-2016.jpg", 97, 2016, "Animation, Aventure, Comédie", "Dory, le poisson chirurgien bleu amnésique, retrouve ses amis Nemo et Marin. Tous trois se lancent à la recherche du passé de Dory. Pourra-t-elle retrouver ses souvenirs ? Qui sont ses parents ? Et où a-t-elle bien pu apprendre à parler la langue des baleines ?", "Ellen DeGeneres, Albert Brooks, Ed O'Neill", "Andrew Stanton, Angus MacLane");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Camping 3", "../img/Covers/movies/camping-3-2016.jpg", 105, 2016, "Comédie, Drame", "Comme chaque été, au Camping des Flots Bleus se retrouvent pour leurs vacances nos amis, Les Pic, Jacky et Laurette, Gatineau, tout juste divorcé de Sophie, le 37, et Patrick Chirac fidèle à ses habitudes.Cette année, Patrick a décidé de tester le co-voiturage...", "Franck Dubosc, Claude Brasseur, Mylène Demongeot", "Fabien Onteniente");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Captain America: Civil War", "../img/Covers/movies/captain-america-3-2016.jpg", 147, 2016, "Action, Science-Fiction", "Steve Rogers est désormais à la tête des Avengers, dont la mission est de protéger l’humanité. À la suite d’une de leurs interventions qui a causé d’importants dégâts collatéraux, le gouvernement décide de mettre en place un organisme de commandement et de supervision...", "Chris Evans, Robert Downey Jr., Scarlett Johansson", "Anthony Russo, Joe Russo");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Trolls", "../img/Covers/movies/les-trolls-2016.jpg", 92, 2016, "Animation, Aventure, Comédie", "Connus pour leur crête de cheveux fluos et magiques, les Trolls sont des créatures délirantes et joyeuses et surtout les rois de la pop. Mais leur monde d'arcs-en-ciel et de cupcakes est changé à jamais lorsque leur leader Poppy, accompagnée de Branche et tous ses amis , doit se lancer dans une mission de sauvetage qui l'entraînera loin de ce petit paradis.", "Anna Kendrick, Justin Timberlake, Zooey Deschanel", "Mike Mitchell, Walt Dohrn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Kung Fu Panda 3", "../img/Covers/movies/kung-fu-panda-3-2016.jpg", 95, 2016, "Animation, Action, Aventure", "Lorsque le maléfique Kaï décide de s'attaquer aux plus grands maîtres de kung-fu à travers toute la Chine, Po va devoir réussir l'impossible : transformer tout un village de pandas maladroits et rigolards en experts des arts martiaux, les redoutables Kung-Fu Pandas !", "Jack Black, Bryan Cranston, Dustin Hoffman", "Alessandro Carloni, Jennifer Yuh Nelson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Batman v Superman : L’Aube de la Justice", "../img/Covers/movies/batman-vs-superman-2016.jpg", 151, 2016, "Action, Aventure, Science-Fiction", "Craignant que Superman n'abuse de sa toute-puissance, le Chevalier noir décide de l'affronter : le monde a-t-il davantage besoin d'un super-héros aux pouvoirs sans limite ou d'un justicier à la force redoutable mais d'origine humaine ? Pendant ce temps-là, une terrible menace se profile à l'horizon…", "Ben Affleck, Henry Cavill, Amy Adams", "Zack Snyder");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Suicide Squad", "../img/Covers/movies/suicide-squad-2016.jpg", 123, 2016, "Action, Aventure, Fantastique", "C'est tellement jouissif d'être un salopard ! Face à une menace aussi énigmatique qu'invincible, l'agent secret Amanda Waller réunit une armada de crapules de la pire espèce. Armés jusqu'aux dents par le gouvernement, ces Super-Méchants s'embarquent alors pour une mission-suicide...", "Will Smith, Jared Leto, Margot Robbie", "David Ayer");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Visiteurs - La Révolution", "../img/Covers/movies/les-visiteurs-revolution-2016.jpg", 110, 2016, "Comédie, Fantastique", "Bloqués dans les couloirs du temps, Godefroy de Montmirail et son fidèle serviteur Jacquouille sont projetés dans une époque de profonds bouleversements politiques et sociaux : la Révolution Française... Plus précisément, La Terreur, période de grands dangers...", "Christian Clavier, Jean Reno, Franck Dubosc", "Jean-Marie Poiré");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Retour chez ma mère", "../img/Covers/movies/retour-chez-ma-mere-2016.jpg", 97, 2016, "Comédie, Drame", "À 40 ans, Stéphanie est contrainte de retourner vivre chez sa mère. Elle est accueillie les bras ouverts : à elle les joies de l'appartement surchauffé, de Francis Cabrel en boucle, des parties de Scrabble endiablées et des précieux conseils maternels sur la façon de se tenir à table et de mener sa vie…", "Josiane Balasko, Alexandra Lamy, Mathilde Seigner", "Eric Lavaine");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("X-Men: Apocalypse", "../img/Covers/movies/x-men-apocalypse-2016.jpg", 144, 2016, "Action, Aventure, Science-Fiction", "Né il y a plus de 5000 ans en Egypte ancienne, Apocalypse est le premier mutant au monde. Sa capacité à changer d'apparence, sa force exceptionnelle, ses pouvoirs de télépathie et de téléportation, font de lui un dieu vivant, évoluant à des moments clés de l'Histoire.", "James McAvoy, Michael Fassbender, Jennifer Lawrence", "Bryan Singer");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Divergente 3 : au-delà du mur", "../img/Covers/movies/divergente-3-2016.jpg", 120, 2016, "Action, Aventure, Mystère", "Sous le choc, Tris et Quatre doivent fuir et franchir le mur encerclant Chicago. Pour la première fois, ils quittent la seule ville et famille qu'ils aient connues. Mais au delà du mur se trouve un monde hostile qu'ils vont devoir affronter. Tris et Quatre doivent rapidement déterminer en qui ils peuvent avoir confiance alors qu’une bataille menaçant l’humanité toute entière est sur le point d'éclater...", "Shailene Woodley, Theo James, Jeff Daniels", "Robert Schwentke");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Demain Tout Commence", "../img/Covers/movies/demain-tout-commence-2016.jpg", 118, 2016, "Comédie, Drame", "L'histoire d'un homme qui se retrouve père du jour au lendemain. La traque de la mère du nourrisson, partie faire sa vie ailleurs, va le conduire à Londres où il devient cascadeur dans un show TV.", "Omar Sy, Clémence Poésy, Antoine Bertrand", "Hugo Gélin");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Bridget Jones Baby", "../img/Covers/movies/bridget-jones-baby-2016.jpg", 123, 2016, "Comédie, Drame, Romance", "Après avoir rompu avec Mark Darcy, Bridget se retrouve de nouveau célibataire, 40 ans passés, plus concentrée sur sa carrière et ses amis que sur sa vie amoureuse. Pour une fois, tout est sous contrôle ! Jusqu’à ce que Bridget fasse la rencontre de Jack… Puis retrouve Darcy… Puis découvre qu’elle est enceinte… Mais de qui ??? ", "Renée Zellweger, Gemma Jones, Jim Broadbent", "Sharon Maguire");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Pattaya", "../img/Covers/movies/pattaya-2016.jpg", 100, 2016, "Comédie", "Franky et Krimo rêvent de quitter la grisaille de leur quartier pour partir en voyage dans la célèbre et sulfureuse station balnéaire thaïlandaise de PATTAYA. Pour pouvoir s’y rendre à moindre coût, les deux amis ont la folle idée d’inscrire à son insu le nain de leur quartier au championnat du monde de Boxe Thaï des Nains.", "Franck Gastambide, Malik Bentalha, Anouar Toubali", "Franck Gastambide");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Brice 3", "../img/Covers/movies/brice-3-2016.jpg", 95, 2016, "Action, Aventure, Comédie", "Brice est de retour. Le monde a changé, mais pas lui. Quand son meilleur ami, Marius, l’appelle à l’aide, il part dans une grande aventure à l’autre bout du monde… Les voyages forment la « jaunesse » mais restera-t-il le roi de la casse ?", "Jean Dujardin, Clovis Cornillac, Bruno Salomone", "James Huth");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Chocolat", "../img/Covers/movies/chocolat-2016.jpg", 119, 2016, "Biopic, Drame", "Du cirque au théâtre, de l'anonymat à la gloire, l'incroyable destin du clown Chocolat, premier artiste noir de la scène française. Le duo inédit qu'il forme avec Footit, va rencontrer un immense succès populaire dans le Paris de la Belle époque avant que la célébrité, l'argent facile, le jeu et les discriminations n'usent leur amitié et la carrière de Chocolat. Le film retrace l'histoire de cet artiste hors du commun. ", "Omar Sy, James Thierrée, Clotilde Hesme", "Roschdy Zem");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Doctor Strange", "../img/Covers/movies/doctor-strange-2016.jpg", 115, 2016, "Action, Aventure, Fantastique", "Doctor Strange suit l'histoire du Docteur Stephen Strange, talentueux neurochirurgien qui, après un tragique accident de voiture, doit mettre son égo de côté et apprendre les secrets d'un monde caché de mysticisme et de dimensions alternatives. Basé à New York, dans le quartier de Greenwich Village, Doctor Strange doit jouer les intermédiaires entre le monde réel et ce qui se trouve au-delà, en utlisant un vaste éventail d'aptitudes métaphysiques et d'artefacts pour protéger le Marvel Cinematic Universe.", "Benedict Cumberbatch, Chiwetel Ejiofor, Rachel McAdams", "Scott Derrickson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Alvin et les Chipmunks - A fond la caisse", "../img/Covers/movies/alvin-et-les-chipmunks-4-2016.jpg", 92, 2015, "Aventure, Comédie, Famille", "Après une série de malentendus, Alvin, Simon et Théodore comprennent que Dave va demander sa petite amie en mariage à Miami et risque de les abandonner. Ils ont trois jours pour le retrouver et empêcher ce mariage. Ils se lancent alors dans un road trip à travers l'Amérique : aventure, musique et grosses bêtises.", "Jason Lee, Justin Long, Matthew Gray Gubler", "Walt Becker");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Huit salopards", "../img/Covers/movies/les-huit-salopards-2016.jpg", 168, 2015, "Crime, Drame, Mystère", "Quelques années après la Guerre de Sécession, le chasseur de primes John Ruth, dit Le Bourreau, fait route vers Red Rock, où il conduit sa prisonnière Daisy Domergue se faire pendre. Sur leur route, ils rencontrent le Major Marquis Warren, un ancien soldat lui aussi devenu chasseur de primes...", "Samuel L. Jackson, Kurt Russell, Jennifer Jason Leigh", "Quentin Tarantino");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Warcraft : Le commencement", "../img/Covers/movies/warcraft-2016.jpg", 123, 2016, "Action, Aventure, Fantastique", "Le pacifique royaume d'Azeroth est au bord de la guerre alors que sa civilisation doit faire face à une redoutable race d’envahisseurs: des guerriers Orcs fuyant leur monde moribond pour en coloniser un autre. Alors qu’un portail s’ouvre pour connecter les deux mondes, une armée fait face à la destruction et l'autre à l'extinction.", "Travis Fimmel, Paula Patton, Ben Foster", "Duncan Jones");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Creed - L'Héritage de Rocky Balboa", "../img/Covers/movies/creed-2016.jpg", 80, 2016, "Crime, Drame", "Adonis Johnson n'a jamais connu son père, le célèbre champion du monde poids lourd Apollo Creed décédé avant sa naissance. Pourtant, il a la boxe dans le sang et décide d'être entraîné par le meilleur de sa catégorie. À Philadelphie, il retrouve la trace de Rocky Balboa, que son père avait affronté autrefois, et lui demande de devenir son entraîneur. D'abord réticent, l'ancien champion décèle une force inébranlable chez Adonis et finit par accepter…", "Armine Aroutiunian, Angelica Cabrera, Jabari King", "Jovanni Santos Graupera");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Médecin De Campagne", "../img/Covers/movies/medecin-de-campagne-2016.jpg", 102, 2016, "Comédie, Drame", "Tous les habitants, dans ce coin de campagne, peuvent compter sur Jean-Pierre, le médecin qui les ausculte, les soigne et les rassure jour et nuit, 7 jours sur 7. Malade à son tour, Jean-Pierre voit débarquer Nathalie,  médecin depuis peu, venue de l’hôpital pour le seconder.", "François Cluzet, Marianne Denicourt, Christophe Odent", "Thomas Lilti");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Jason Bourne", "../img/Covers/movies/jason-bourne-2016.jpg", 123, 2016, "Action, Thriller", "La traque de Jason Bourne par les services secrets américains se poursuit. Des îles Canaries à Londres en passant par Las Vegas...", "Matt Damon, Tommy Lee Jones, Alicia Vikander", "Paul Greengrass");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Conjuring 2 : Le Cas Enfield", "../img/Covers/movies/conjuring-2-2016.jpg", 134, 2016, "Horror, Mystère, Thriller", "Lorraine et Ed Warren se rendent dans le nord de Londres pour venir en aide à une mère qui élève seule ses quatre enfants dans une maison hantée par des esprits maléfiques. Il s'agira d'une de leurs enquêtes paranormales les plus terrifiantes…", "Vera Farmiga, Patrick Wilson, Madison Wolfe", "James Wan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Tarzan", "../img/Covers/movies/tarzan-2016.jpg", 110, 2016, "Action, Aventure, Drame", "Après avoir grandi dans la jungle africaine, Tarzan a renoué avec ses origines aristocratiques, répondant désormais au nom de John Clayton, Lord Greystoke. Il mène une vie paisible auprès de son épouse Jane jusqu'au jour où il est convié au Congo en tant qu'émissaire du Commerce. ", "Alexander Skarsgård,, Rory J Saper, Christian Stevens", "David Yates");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Alice de l'autre côté du miroir", "../img/Covers/movies/alice-de-l-autre-cote-du-miroir-2016.jpg", 113, 2016, "Aventure, Famille, Fantastique", "Les nouvelles aventures d'Alice et du Chapelier Fou. Alice replonge au pays des merveilles pour aider ses amis à combattre le Maître du Temps.", "Mia Wasikowska, Johnny Depp, Helena Bonham Carter", "James Bobin");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ballerina", "../img/Covers/movies/ballerina-2016.jpg", 90, 2016, "Animation", "Félicie est une jeune orpheline bretonne qui n’a qu’une passion : la danse. Avec son meilleur ami Victor qui aimerait devenir un grand inventeur, ils mettent au point un plan rocambolesque pour s’échapper de l’orphelinat, direction Paris, ville lumière et sa Tour Eiffel en construction !", "Camille Cottin, Elle Fanning, Malik Bentalha", "Eric Summer, Eric Warin");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Independence Day: Resurgence", "../img/Covers/movies/independence-day-2-2016.jpg", 120, 2016, "Action, Aventure, Science-Fiction", "Nous avons toujours su qu'ils reviendraient. La terre est menacée par une catastrophe d’une ampleur inimaginable. Pour la protéger, toutes les nations ont collaboré autour d’un programme de défense colossal exploitant la technologie extraterrestre récupérée. ", "Liam Hemsworth, Jeff Goldblum, Bill Pullman", "Roland Emmerich");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Assassin's Creed", "../img/Covers/movies/assassin-s-creed-2016.jpg", 115, 2016, "Action, Aventure, Science-Fiction", "Grâce à une technologie révolutionnaire qui révèle la mémoire génétique, Callum Lynch revit les aventures de son ancêtre, Aguilar, dans l'Espagne du XVe siècle. Callum découvre qu'il est issu d'une mystérieuse société secrète, les Assassins, et amasse les connaissances dont il aura besoin pour affronter une autre redoutable organisation : l'Ordre des Templiers.", "Michael Fassbender, Marion Cotillard, Jeremy Irons", "Justin Kurzel");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Papa Ou maman 2", "../img/Covers/movies/papa-ou-maman-2-2016.jpg", 90, 2016, "Comédie, Drame, Romance", "Deux ans ont passé. Après avoir raté leur séparation, les Leroy semblent parfaitement réussir leur divorce. Mais l'apparition de deux nouveaux amoureux dans la vie de Vincent et de Florence va mettre le feu aux poudres. Le match entre les ex-époux reprend.", "Laurent Lafitte, Marina Foïs, Alexandre Desrousseaux", "Martin Bourboulon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Sully", "../img/Covers/movies/sully-2016.jpg", 96, 2016, "Biopic, Drame", "Le 15 janvier 2009, l'incroyable se produit : un avion qui vient de subir une terrible avarie réussit à se poser sans encombre sur les eaux glacées du fleuve Hudson, au large de Manhattan. Bilan : les 155 passagers ont la vie sauve ! ", "Tom Hanks, Aaron Eckhart, Laura Linney", "Clint Eastwood");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Cigognes et compagnie", "../img/Covers/movies/cigognes-et-compagnie-2016.jpg", 87, 2016, "Animation, Aventure, Comédie", "Pendant longtemps, les cigognes livraient les bébés. Désormais, elles acheminent des colis pour un géant de l'Internet. Junior, coursier star de l'entreprise, s'apprête à être promu. Mais il actionne accidentellement la Machine à Fabriquer les Bébés… qui produit une adorable petite fille, en totale infraction avec la loi !", "Andy Samberg, Katie Crown, Kelsey Grammer", "Nicholas Stoller, Doug Sweetland");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La 5ème vague", "../img/Covers/movies/la-5eme-vague-2016.jpg", 112, 2016, "Action, Aventure, Science-Fiction", "Cassie Sullivan, adolescente de 16 ans, essaie de survivre dans un monde en proie à des invasions extraterrestres, appelés Les Autres, ayant déjà décimé la population et part à la recherche de son frère.", "Chloë Grace Moretz, Matthew Zuk, Gabriela Lopez", "J Blakeson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Moi, Moche et Méchant 3", "../img/Covers/movies/moi-moche-et-mechant-3-2017.jpg", 89, 2017, "Animation, Aventure, Comédie", "Gru rencontre son frère jumeau Dru, charmant, joyeux et plus prospère, qui veut faire équipe avec lui pour un dernier hold-up criminel.", "Steve Carell, Kristen Wiig, Trey Parker", "Kyle Balda, Pierre Coffin, Eric Guillon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Star Wars: Episode VIII - Les Derniers Jedi", "../img/Covers/movies/star-wars-8-2017.jpg", 152, 2017, "Action, Aventure, Fantastique", "Les héros du Réveil de la force rejoignent les figures légendaires de la galaxie dans une aventure épique qui révèle des secrets ancestraux sur la Force et entraîne de surprenantes révélations sur le passé…", "Daisy Ridley, John Boyega, Mark Hamill", "Rian Johnson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Baby Boss", "../img/Covers/movies/baby-boss-2017.jpg", 97, 2017, "Animation, Aventure, Comédie", "C'est toujours un choc de voir ses parents rentrer à la maison avec un bébé dans les bras – surtout quand il porte une cravate, qu’il se balade avec un attaché-case et qu’il a la voix d’un quinquagénaire ! Tim, 7 ans, a en réalité affaire à un espion que lui seul peut aider à accomplir sa mission ultra secrète…", "Alec Baldwin, Steve Buscemi, Jimmy Kimmel", "Tom McGrath");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Fast & Furious 8", "../img/Covers/movies/fast-and-furious-8-2017.jpg", 136, 2017, "Action, Crime, Thriller", "Maintenant que Dom et Letty sont en lune de miel, que Brian et Mia se sont rangés et que le reste de l’équipe a été disculpé, la bande de globetrotteurs retrouve un semblant de vie normale. Mais quand une mystérieuse femme entraîne Dom dans le monde de la criminalité, ce dernier ne pourra éviter de trahir ses proches qui vont faire face à des épreuves qu’ils n’avaient jamais rencontrées jusqu’alors.", "Vin Diesel, Jason Statham, Dwayne Johnson", "F. Gary Gray");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Tous en scène", "../img/Covers/movies/tous-en-scene-2016.jpg", 108, 2016, "Animation, Comédie, Famille", "Buster Moon est un élégant koala qui dirige un grand théâtre, jadis illustre, mais aujourd’hui tombé en désuétude. Buster est un éternel optimiste, un peu bougon, qui aime son précieux théâtre au-delà de tout et serait prêt à tout pour le sauver.", "Matthew McConaughey, Reese Witherspoon, Seth MacFarlane", "Garth Jennings, Christophe Lourdelet");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Belle et la Bête", "../img/Covers/movies/la-belle-et-la-bete-2017.jpg", 129, 2017, "Aventure, Famille, Fantastique", "Fin du XVIIIè siècle, dans un petit village français. Belle, jeune fille rêveuse et passionnée de littérature, vit avec son père, un vieil inventeur farfelu. S'étant perdu une nuit dans la fôret, ce dernier se réfugie au château de la Bête, qui le jette au cachot...", "Emma Watson, Dan Stevens, Luke Evans", "Bill Condon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Coco", "../img/Covers/movies/coco-2017.jpg", 105, 2017, "Animation, Aventure, Drame", "Depuis déjà plusieurs générations, la musique est bannie dans la famille de Miguel. Un vrai déchirement pour le jeune garçon dont le rêve ultime est de devenir un musicien aussi accompli que son idole, Ernesto de la Cruz. ", "Anthony Gonzalez, Gael García Bernal, Benjamin Bratt", "Lee Unkrich, Adrian Molina");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Gardiens de la Galaxie 2", "../img/Covers/movies/les-gardiens-de-la-galaxie-2-2017.jpg", 136, 2017, "Action, Aventure, Comédie", "Les Gardiens de la Galaxie doivent combattre pour rester unis alors qu'ils découvrent les mystères de la filiation de Peter Quill. Les vieux ennemis vont devenir de nouveaux alliés et des personnages bien connus des fans de comics vont venir aider nos héros et continuer à étendre l'univers Marvel.", "Chris Pratt, Zoe Saldana, Dave Bautista", "James Gunn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Cinquante Nuances plus sombres", "../img/Covers/movies/cinquante-nuances-plus-sombres-2017.jpg", 118, 2017, "Drame, Mystère, Romance", "C’est un Christian blessé qui tente de reconquérir Anastasia. Cette dernière exige un nouveau contrat avant de lui laisser une seconde chance. Mais une ombre surgit du passé de Christian et plane sur les deux amants, déterminée à détruire un quelconque espoir de vie commune.", "Dakota Johnson, Jamie Dornan, Eric Johnson", "James Foley");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Planète des Singes - Suprématie", "../img/Covers/movies/la-planete-des-singes-suprematie-2017.jpg", 140, 2017, "Action, Aventure, Drame", "Dans ce volet final de la trilogie, César, à la tête des Singes, doit défendre les siens contre une armée humaine prônant leur destruction. L’issue du combat déterminera non seulement le destin de chaque espèce, mais aussi l’avenir de la planète.", "Andy Serkis, Woody Harrelson, Steve Zahn", "Matt Reeves");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La La Land", "../img/Covers/movies/la-la-land-2016.jpg", 128, 2016, "Comédie, Drame, Musical", "Au cœur de Los Angeles, une actrice en devenir prénommée Mia sert des cafés entre deux auditions. De son côté, Sebastian, passionné de jazz, joue du piano dans des clubs miteux pour assurer sa subsistance. Tous deux sont bien loin de la vie rêvée à laquelle ils aspirent…", "Ryan Gosling, Emma Stone, Rosemarie DeWitt", "Damien Chazelle");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Cars 3", "../img/Covers/movies/cars-3-2017.jpg", 102, 2017, "Animation, Aventure, Comédie", "Dépassé par une nouvelle génération de bolides ultra-rapides, le célèbre Flash McQueen se retrouve mis sur la touche d’un sport qu’il adore. Pour revenir dans la course et prouver, en souvenir de Doc Hudson, que le n° 95 a toujours sa place dans la Piston Cup, il devra faire preuve d’ingéniosité. L’aide d’une jeune mécanicienne pleine d’enthousiasme, Cruz Ramirez, qui rêve elle aussi de victoire, lui sera d’un précieux secours...", "Owen Wilson, Cristela Alonzo, Chris Cooper", "Brian Fee");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Dunkerque", "../img/Covers/movies/dunkerque-2017.jpg", 106, 2017, "Action, Drame, Historique", "À Dunkerque, des soldats britanniques, français et belges sont encerclés par l'armée allemande et évacués dans l'une des entreprises les plus ambitieuses de la Seconde Guerre mondiale.", "Fionn Whitehead, Barry Keoghan, Mark Rylance", "Christopher Nolan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Épouse-moi mon pote", "../img/Covers/movies/epouse-moi-mon-pote-2017.jpg", 92, 2017, "Comédie, Romance", "Yassine, jeune étudiant marocain vient à Paris faire ses études d’architecture avec un visa étudiant. Suite à un événement malencontreux, il rate son examen, perd son visa et se retrouve en France en situation irrégulière. Pour y remédier, il se marie avec son meilleur ami. ", "Tarek Boudali, Philippe Lacheau, Charlotte Gabris", "Tarek Boudali");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Thor: Ragnarok", "../img/Covers/movies/thor-ragnarok-2017.jpg", 130, 2017, "Action, Aventure, Comédie", "Privé de son puissant marteau, Thor est retenu prisonnier sur une lointaine planète aux confins de l’univers. Pour sauver Asgard, il va devoir lutter contre le temps afin d’empêcher l’impitoyable Hela d’accomplir le Ragnarök...", "Chris Hemsworth, Tom Hiddleston, Cate Blanchett", "Taika Waititi");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Spider-Man: Homecoming", "../img/Covers/movies/spider-man-homecoming-2017.jpg", 133, 2017, "Action, Aventure, Science-Fiction", "Le jeune Peter Parker découvre peu à peu sa nouvelle identité, celle de Spider-Man, le super-héros lanceur de toile. Galvanisé par son expérience avec les Avengers, Peter rentre chez lui auprès de sa tante May, sous l’œil attentif de son nouveau mentor, Tony Stark.", "Tom Holland, Michael Keaton, Robert Downey Jr.", "Jon Watts");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Logan", "../img/Covers/movies/logan-2017.jpg", 137, 2017, "Action, Drame, Science-Fiction", "Dans un futur proche, un certain Logan, épuisé de fatigue, s’occupe d’un Professeur X souffrant, dans un lieu gardé secret à la frontière Mexicaine. Mais les tentatives de Logan pour se retrancher du monde et rompre avec son passé vont s’épuiser lorsqu’une jeune mutante traquée par de sombres individus va se retrouver soudainement face à lui.", "Hugh Jackman, Patrick Stewart, Dafne Keen", "James Mangold");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ça", "../img/Covers/movies/ca-2017.jpg", 135, 2017, "Horreur", "À Derry, dans le Maine, sept gamins ayant du mal à s'intégrer se sont regroupés au sein du 'Club des Ratés'. Rejetés par leurs camarades, ils sont les cibles favorites des gros durs de l'école. Ils ont aussi en commun d'avoir éprouvé leur plus grande terreur face à un terrible prédateur métamorphe qu'ils appellent 'Ça'…", "Bill Skarsgård, Jaeden Martell, Finn Wolfhard", "Andy Muschietti");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Wonder Woman", "../img/Covers/movies/wonder-woman-2017.jpg", 141, 2017, "Action, Aventure, Fantastique", "Avant d'être Wonder Woman, elle s'appelait Diana, princesse des Amazones, entraînée pour être une guerrière impossible à conquérir. Elle est élevée sur une île isolée et paradisiaque, mais lorsqu'un pilote américain s'écrase sur leur rivage et annonce qu'un conflit à grande échelle fait rage dans le monde, Diana quitte son foyer, convaincue qu'elle doit arrêter cette menace.", "Gal Gadot, Chris Pine, Robin Wright", "Patty Jenkins");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("L'école buissonnière", "../img/Covers/movies/lecole-buissonniere-2017.jpg", 116, 2017, "Comédie, Drame, Famille", "Paris 1930. Paul n’a toujours eu qu’un seul et même horizon : les hauts murs de l’orphelinat, sévère bâtisse de la banlieue ouvrière parisienne. Confié à une joyeuse dame de la campagne, Célestine et à son mari, Borel, le garde-chasse un peu raide d’un vaste domaine en Sologne, l’enfant des villes, récalcitrant et buté, arrive dans un monde mystérieux et inquiétant, celui d’une région souveraine et sauvage. ", "François Cluzet, Jean Scandel, Eric Elmosnino", "Nicolas Vanier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Seven Sisters", "../img/Covers/movies/seven-sisters-2017.jpg", 123, 2017, "Action, Crime, Fantastique", "2073. La Terre est surpeuplée. Le gouvernement décide d’instaurer une politique d’enfant unique, appliquée de main de fer par le Bureau d’Allocation des Naissances, sous l’égide de Nicolette Cayman. ", "Noomi Rapace, Glenn Close, Willem Dafoe", "Tommy Wirkola");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Schtroumpfs et le village perdu", "../img/Covers/movies/les-schtroumpfs-et-le-village-perdu-2017.jpg", 90, 2017, "Animation, Aventure, Comédie", "La Schtroumpfette, le Schtroumpf costaud, le Schtroumpf à lunettes et le Schtroumpf maladroit ont filé en douce pour suivre une carte vers un mystérieux village. Mais le chemin qui y mène regorge d'embuches, de créatures magiques et de souterrains labyrinthiques...", "Demi Lovato, Rainn Wilson, Joe Manganiello", "Kelly Asbury");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Split", "../img/Covers/movies/split-2017.jpg", 117, 2017, "Horreur, Thriller", "Kevin a manifesté 23 personnalités devant son psychiatre de longue date, le Dr Fletcher, mais il en reste une, immergée, qui commence à se matérialiser et à dominer toutes les autres. Contraint d'enlever trois adolescentes, dont la volontaire Casey, Kevin se bat pour survivre parmi tous ceux qui évoluent en lui-même...", "James McAvoy, Anya Taylor-Joy, Betty Buckley", "M. Night Shyamalan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Kingsman : Le Cercle d'or", "../img/Covers/movies/kingsman-2-2017.jpg", 141, 2017, "Action, Aventure, Comédie", "KINGSMAN, l’élite du renseignement britannique en costume trois pièces, fait face à une menace sans précédent. Alors qu’une bombe s’abat et détruit leur quartier général, les agents font la découverte d’une puissante organisation alliée nommée Statesman, fondée il y a bien longtemps aux Etats-Unis.", "Taron Egerton, Colin Firth, Mark Strong", "Matthew Vaughn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Justice League", "../img/Covers/movies/justice-league-2017.jpg", 120, 2017, "Action, Aventure, Fantastique", "Après avoir retrouvé foi en l'humanité, Bruce Wayne, inspiré par l'altruisme de Superman, sollicite l'aide de sa nouvelle alliée, Diana Prince, pour affronter un ennemi plus redoutable que jamais. Ensemble, Batman et Wonder Woman ne tardent pas à recruter une équipe de méta-humains...", "Ben Affleck, Gal Gadot, Jason Momoa", "Zack Snyder");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Santa & Cie", "../img/Covers/movies/santa-et-cie-2017.jpg", 92, 2017, "Comédie, Famille, Fantastique", "Rien ne va plus à l'approche du réveillon : les 92 000 lutins chargés de fabriquer les cadeaux des enfants tombent tous malades en même temps ! C'est un coup dur pour Santa (Claus), plus connu sous le nom de Père Noël... il n'a pas le choix : il doit se rendre d'urgence sur Terre avec ses rennes...", "Alain Chabat, Golshifteh Farahani, Pio Marmaï", "Alain Chabat");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Kong: Skull Island", "../img/Covers/movies/kong-skull-island-2017.jpg", 118, 2017, "Action, Aventure, Fantastique", "Un groupe d'explorateurs plus différents les uns que les autres s'aventurent au cœur d'une île inconnue du Pacifique, aussi belle que dangereuse. Ils ne savent pas encore qu'ils viennent de pénétrer sur le territoire de Kong…", "Tom Hiddleston, Samuel L. Jackson, Brie Larson", "Jordan Vogt-Roberts");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Baywatch - Alerte à Malibu", "../img/Covers/movies/baywatch-2017.jpg", 116, 2017, "Action, Comédie, Crime", "Le légendaire sauveteur Mitch Buchannon  est contraint de s’associer à une nouvelle recrue, Matt Brody, aussi ambitieux que tête brûlée ! Ensemble, ils vont tenter de déjouer un complot criminel qui menace l'avenir de la Baie…", "Dwayne Johnson, Zac Efron, Alexandra Daddario", "Seth Gordon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Transformers: The Last Knight", "../img/Covers/movies/transformers-5-2017.jpg", 154, 2017, "Action, Aventure, Science-Fiction", "Humains et Transformers sont en guerre. Optimus Prime n’est plus là… La clé de notre salut est enfouie dans les secrets du passé, dans l’histoire cachée des Transformers sur Terre. Sauver notre monde sera la mission d’une alliance inattendue : Cade Yeager, Bumblebee, un Lord anglais et un professeur d’Oxford.", "Mark Wahlberg, Anthony Hopkins, Josh Duhamel", "Michael Bay");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Jumanji : Bienvenue dans la jungle", "../img/Covers/movies/jumanji-2017.jpg", 119, 2017, "Action, Aventure, Comédie", "Quatre lycéens découvrent une vieille console contenant un jeu vidéo dont ils n’avaient jamais entendu parler : Jumanji. En voulant jouer, ils se retrouvent mystérieusement propulsés dans la jungle de Jumanji, où ils deviennent leurs avatars", "Dwayne Johnson, Karen Gillan, Kevin Hart", "Jake Kasdan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Momie", "../img/Covers/movies/la-momie-2017.jpg", 110, 2017, "Action, Aventure, Fantastique", "Bien qu’elle ait été consciencieusement enterrée dans un tombeau au fin fond d’un insondable désert, une princesse de l’ancienne Égypte, dont le destin lui a été injustement ravi, revient à la vie et va déverser sur notre monde des siècles de rancœurs accumulées et de terreur dépassant l’entendement humain.", "Tom Cruise, Sofia Boutella, Annabelle Wallis", "Alex Kurtzman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Paddington 2", "../img/Covers/movies/paddington-2-2017.jpg", 103, 2017, "Aventure, Comédie, Famille", "Installé dans sa nouvelle famille à Londres. Paddington est devenu un membre populaire de la communauté locale. A la recherche du cadeau parfait pour le 100e anniversaire de sa chère Tante Lucy, Paddington tombe sur un livre animé exceptionnel.", "Ben Whishaw, Hugh Grant, Hugh Bonneville", "Paul King");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Patients", "../img/Covers/movies/patients-2016.jpg", 110, 2016, "Comédie, Drame", "Se laver, s'habiller, marcher, jouer au basket, voici ce que Ben ne peut plus faire à son arrivée dans un centre de rééducation suite à un grave accident. Ses nouveaux amis sont tétras, paras, traumas crâniens.... ", "Pablo Pauly, Soufiane Guerrab, Moussa Mansaly", "Mehdi Idir, Grand Corps Malade");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Alien: Covenant", "../img/Covers/movies/alien-covenant-2017.jpg", 122, 2017, "Horreur, Science-Fiction, Thriller", "Les membres d’équipage du vaisseau Covenant, à destination d’une planète située au fin fond de notre galaxie, découvrent ce qu’ils pensent être un paradis encore intouché. Il s’agit en fait d’un monde sombre et dangereux, cachant une menace terrible. Ils vont tout tenter pour s’échapper. ", "Michael Fassbender, Katherine Waterston, Billy Crudup", "Ridley Scott");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Blade Runner 2049", "../img/Covers/movies/blade-runner-2049-2017.jpg", 164, 2017, "Action, Drame, Mystère", "En 2049, la société est fragilisée par les nombreuses tensions entre les humains et leurs esclaves créés par bioingénierie. L’officier K est un Blade Runner : il fait partie d’une force d’intervention d’élite chargée de trouver et d’éliminer ceux qui n’obéissent pas aux ordres des humains.", "Harrison Ford, Ryan Gosling, Ana de Armas", "Denis Villeneuve");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Annabelle 2 : la Création du Mal", "../img/Covers/movies/annabelle-2-2017.jpg", 109, 2017, "Horreur, Mystère, Thriller", "Elle est de retour ! Encore traumatisés par la mort tragique de leur petite fille, un fabricant de poupées et sa femme recueillent une bonne sœur et les toutes jeunes pensionnaires d'un orphelinat dévasté. Mais ce petit monde est bientôt la cible d'Annabelle, créature du fabricant possédée par un démon…", "Anthony LaPaglia, Samara Lee, Miranda Otto", "David F. Sandberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Get Out", "../img/Covers/movies/get-out-2017.jpg", 104, 2017, "Horreur, Mystère, Thriller", "Couple mixte, Chris et sa petite amie Rose  filent le parfait amour. Le moment est donc venu de rencontrer la belle famille, Missy et Dean lors d’un week-end sur leur domaine dans le nord de l’État. Chris commence par penser que l’atmosphère tendue est liée à leur différence de couleur de peau, mais très vite une série d’incidents de plus en plus inquiétants lui permet de découvrir l’inimaginable.", "Daniel Kaluuya, Allison Williams, Bradley Whitford", "Jordan Peele");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Sahara", "../img/Covers/movies/sahara-2017.jpg", 86, 2017, "Animation, Aventure, Comédie", "Lassés d’être les souffre-douleur de leur communauté, Ajar le serpent et son pote Pitt le scorpion décident de tenter leur chance dans l'oasis voisine où vit la haute bourgeoisie du désert saharien et d'y retrouver Eva, une belle serpente dont Ajar est tombé fou amoureux.", "Omar Sy, Louane Emera, Franck Gastambide", "Pierre Coré");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("L'Ascension", "../img/Covers/movies/lascension-2017.jpg", 103, 2017, "Comédie, Drame, Romance", "'Pour toi, je pourrais gravir l'Everest !' Samy aurait mieux fait de se taire ce jour là... D'autant que Nadia ne croit pas beaucoup à des belles paroles. Par amour pour elle, Samy quitte sa cité HLM et part gravir les mythiques 8848 mètres qui font de l'Everest le Toit du Monde. Un départ qui fait vibrer ses copains, puis tout le 9-3 et c'est bientôt la France entière qui suit avec émotion les exploits de ce jeune mec ordinaire mais amoureux.", "Ahmed Sylla, Alice Belaïdi, Kévin Razy", "Ludovic Bernard");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le brio", "../img/Covers/movies/le-brio-2017.jpg", 95, 2017, "Comédie, Drame", "Neïla Salah a grandi à Créteil et rêve de devenir avocate. Inscrite à la grande université parisienne d’Assas, elle se confronte dès le premier jour à Pierre Mazard, professeur connu pour ses provocations et ses dérapages.", "Daniel Auteuil, Camélia Jordana, Yasin Houicha", "Yvan Attal");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("À bras ouverts", "../img/Covers/movies/a-bras-ouverts-2017.jpg", 92, 2017, "Comédie", "Alors qu'il fait la promotion de son nouveau roman invitant les gens à accueillir chez eux les gens dans le besoin, un intellectuel de gauche marié à une riche héritière est accusé d'hypocrisie par un journaliste pugnace, qui le met au défi d'appliquer ce qu'il préconise dans son ouvrage.", "Christian Clavier, Bryan Smeers, Ary Abittan", "Philippe de Chauveron");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ghost in the Shell", "../img/Covers/movies/ghost-in-the-shell-2017.jpg", 107, 2017, "Action, Crime, Drame", "Dans un futur proche, le Major est un agent d’élite, humaine dotée d’un corps aux capacités cybernétiques. Alors qu’elle affronte un nouvel ennemi capable de pirater les esprits, elle découvre qu’on lui a menti sur ses origines. Rien ne l’arrêtera pour comprendre son passé.", "Scarlett Johansson, Pilou Asbæk, Takeshi Kitano", "Rupert Sanders");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Indestructibles 2", "../img/Covers/movies/les-indestructibles-2-2018.jpg", 118, 2018, "Animation, Action, Aventure", "La suite des aventures de la famille Indestructibles. Alors qu'Hélène (Elastic Girl) prend la tête de la mission 'sauver le monde', Bob (Mr Indestructible) se retrouve en dehors de sa zone de confort à devoir gérer de manière chaotique les tâches de la vie quotidienne.", "Craig T. Nelson, Holly Hunter, Sarah Vowell", "Brad Bird");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Tuche 3", "../img/Covers/movies/les-tuche-3-2018.jpg", 95, 2018, "Comédie", "Jeff Tuche, se réjouit de l’arrivée du TGV dans son cher village. Malheureusement, le train à grande vitesse ne fait que passer, sans s’arrêter à Bouzolles. Déçu, il tente de joindre le président de la République pour que son village ne reste pas isolé du reste du territoire. ", "Jean-Paul Rouve, Isabelle Nanty, Claire Nadeau", "Olivier Baroux");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Ch’tite famille", "../img/Covers/movies/la-chtite-famille-2018.jpg", 107, 2018, "Comédie", "Valentin D. et Constance Brandt, un couple d’architectes designers en vogue préparent le vernissage de leur rétrospective au Palais de Tokyo. Mais ce que personne ne sait, c’est que pour s’intégrer au monde du design et du luxe parisien, Valentin a menti sur ses origines prolétaires et ch'tis.", "Dany Boon, Line Renaud, Laurence Arné", "Dany Boon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Avengers: Infinity War", "../img/Covers/movies/avengers-infinity-war-2018.jpg", 149, 2018, "Action, Aventure, Science-Fiction", "Les Avengers et leurs alliés devront être prêts à tout sacrifier pour neutraliser le redoutable Thanos avant que son attaque éclair ne conduise à la destruction complète de l’univers. ", "Robert Downey Jr., Chris Hemsworth, Mark Ruffalo", "Anthony Russo, Joe Russo");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Grand Bain", "../img/Covers/movies/le-grand-bain-2018.jpg", 121, 2018, "Comédie, Drame, Sport", "C’est dans les couloirs de leur piscine municipale que Bertrand, Marcus, Simon, Laurent, Thierry et les autres s’entraînent sous l’autorité toute relative de Delphine, ancienne gloire des bassins. Ensemble, ils se sentent libres et utiles. Ils vont mettre toute leur énergie dans une discipline jusque-là propriété de la gent féminine : la natation synchronisée...", "Mathieu Amalric, Guillaume Canet, Benoît Poelvoorde", "Gilles Lellouche");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Animaux fantastiques : Les crimes de Grindelwald", "../img/Covers/movies/les-animaux-fantastiques-2-2018.jpg", 134, 2018, "Aventure, Famille, Fantastique", "1927. Quelques mois après sa capture, le célèbre sorcier Gellert Grindelwald s'évade comme il l'avait promis et de façon spectaculaire. Réunissant de plus en plus de partisans, il est à l'origine d'attaque d'humains normaux par des sorciers et seul celui qu'il considérait autrefois comme un ami, Albus Dumbledore, semble capable de l'arrêter. ", "Eddie Redmayne, Katherine Waterston, Dan Fogler", "David Yates");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Bohemian Rhapsody", "../img/Covers/movies/bohemian-rhapsody-2018.jpg", 134, 2018, "Biopic, Drame, Musical", "Du succès fulgurant de Freddie Mercury à ses excès, risquant la quasi-implosion du groupe, jusqu’à son retour triomphal sur scène lors du concert Live Aid, alors qu’il était frappé par la maladie, découvrez la vie exceptionnelle d’un homme qui continue d’inspirer les outsiders, les rêveurs et tous ceux qui aiment la musique.", "Rami Malek, Lucy Boynton, Gwilym Lee", "Bryan Singer");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Taxi 5", "../img/Covers/movies/taxi-5-2018.jpg", 102, 2018, "Action, Comédie, Crime", "Sylvain Marot, super flic parisien et pilote d’exception, est muté contre son gré à la Police Municipale de Marseille. L’ex-commissaire Gibert, devenu Maire de la ville et au plus bas dans les sondages, va alors lui confier la mission de stopper le redoutable « Gang des Italiens », qui écume des bijouteries à l’aide de puissantes Ferrari.", "Franck Gastambide, Malik Bentalha, Bernard Farcy", "Franck Gastambide");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Jurassic World: Fallen Kingdom", "../img/Covers/movies/jurassic-world-2-2018.jpg", 128, 2018, "Action, Aventure, Science-Fiction", "Cela fait maintenant trois ans que les dinosaures se sont échappés et ont détruit le parc à thème Jurassic World. Isla Nublar a été abandonnée par les humains alors que les dinosaures survivants sont livrés à eux-mêmes dans la jungle. Lorsque le volcan inactif de l'île commence à rugir, Owen et Claire s’organisent pour sauver les dinosaures restants de l’extinction..", "Chris Pratt, Bryce Dallas Howard, Rafe Spall", "J.A. Bayona");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Black Panther", "../img/Covers/movies/black-panther-2018.jpg", 134, 2018, "Action, Aventure, Science-Fiction", "T’Challa revient chez lui prendre sa place sur le trône du Wakanda, une nation africaine technologiquement très avancée. Mais lorsqu’un vieil ennemi resurgit, le courage de T’Challa est mis à rude épreuve, aussi bien en tant que souverain qu’en tant que Black Panther.", "Chadwick Boseman, Michael B. Jordan, Lupita Nyong'o", "Ryan Coogler");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Hôtel Transylvanie 3 : Des vacances monstrueuses", "../img/Covers/movies/hotel-transylvanie-3-2018.jpg", 97, 2018, "Animation, Aventure, Comédie", "Notre famille de monstres préférée embarque pour une croisière de rêve afin que Drac puisse enfin souffler un peu et savourer des vacances au lieu de s’occuper de tout le monde à l’hôtel. Mais la mystérieuse Ericka, la capitaine humaine du navire, cache un secret qui les menace tous…", "Adam Sandler, Andy Samberg, Selena Gomez", "Genndy Tartakovsky");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Mission: Impossible - Fallout", "../img/Covers/movies/mission-impossible-6-2018.jpg", 147, 2018, "Action, Aventure, Thriller", "Les meilleures intentions finissent souvent par se retourner contre vous… Dans MISSION : IMPOSSIBLE – FALLOUT, Ethan Hunt accompagné de son équipe de l’IMF – Impossible Mission Force et de quelques fidèles alliées sont lancés dans une course contre la montre, suite au terrible échec d’une mission.", "Tom Cruise, Henry Cavill, Ving Rhames", "Christopher McQuarrie");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Astérix - Le Secret de la Potion Magique", "../img/Covers/movies/asterix-secret-de-la-potion-2018.jpg", 87, 2018, "Animation, Aventure, Comédie", "À la suite d’une chute lors de la cueillette du gui, le druide Panoramix décide qu’il est temps d’assurer l’avenir du village. Accompagné d’Astérix et Obélix, il entreprend de parcourir le monde gaulois à la recherche d’un jeune druide talentueux à qui transmettre le Secret de la Potion Magique…", "Christian Clavier, Guillaume Briat, Alex Lutz", "Alexandre Astier, Louis Clichy, Jack Cox");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Labyrinthe : le remède mortel", "../img/Covers/movies/le-labyrinthe-3-2018.jpg", 143, 2018, "Action, Aventure, Science-Fiction", "Dans ce dernier volet de l’épopée LE LABYRINTHE, Thomas et les Blocards s’engagent dans une ultime mission, plus dangereuse que jamais. Afin de sauver leurs amis, ils devront pénétrer dans la légendaire et sinueuse Dernière Ville contrôlée par la terrible organisation WICKED.", "Dylan O'Brien, Ki Hong Lee, Kaya Scodelario", "Wes Ball");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Cinquante Nuances plus claires", "../img/Covers/movies/cinquante-nuances-plus-claires-2018.jpg", 105, 2018, "Drame, Mystère, Romance", "Pensant avoir laissé derrière eux les ombres du passé, les jeunes mariés Christian et Ana profitent pleinement de leur relation tortueuse et partagent une vie de luxe. Mais alors qu’Anastasia commence tout juste à s’adapter à son nouveau rôle de Madame Grey et que Christian s’ouvre finalement à elle, de nouvelles menaces viennent mettre en péril leur vie commune avant même qu’elle n’ait débuté.", "Dakota Johnson, Jamie Dornan, Eric Johnson", "James Foley");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Deadpool 2", "../img/Covers/movies/deadpool-2-2018.jpg", 119, 2018, "Action, Aventure, Comédie", "L’insolent mercenaire de Marvel remet le masque !Plus grand, plus-mieux, et occasionnellement les fesses à l’air, il devra affronter un Super-Soldat dressé pour tuer, repenser l’amitié, la famille, et ce que signifie l’héroïsme – tout en bottant cinquante nuances de culs, car comme chacun sait, pour faire le Bien, il faut parfois se salir les doigts.", "Ryan Reynolds, Josh Brolin, Morena Baccarin", "David Leitch");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Tout le Monde Debout", "../img/Covers/movies/tout-le-monde-debout-2018.jpg", 107, 2018, "Comédie, Romance", "Jocelyn, homme d'affaire en pleine réussite, est un dragueur et un menteur invétéré. Lassé d'être lui-même, il se retrouve malgré lui à séduire une jeune et jolie femme en se faisant passer pour un handicapé. Jusqu'au jour où elle lui présente sa sœur elle-même handicapée...", "Franck Dubosc, Alexandra Lamy, Elsa Zylberstein", "Franck Dubosc");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Alad'2", "../img/Covers/movies/alad-2-2018.jpg", 98, 2018, "Comédie, Fantastique", "Après avoir libéré Bagdad de l’emprise de son terrible Vizir, Aladin s’ennuie au palais et ne s’est toujours pas décidé à demander en mariage la princesse. Mais un terrible dictateur, Shah Zaman, s’invite au Palais et annonce qu’il est venu prendre la ville et épouser la Princesse.", "Kev Adams, Jamel Debbouze, Vanessa Guide", "Lionel Steketee");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Venom", "../img/Covers/movies/venom-2018.jpg", 112, 2018, "Action, Aventure, Science-Fiction", "Possédé par un symbiote qui agit de manière autonome, le journaliste Eddie Brock devient le protecteur létal Venom.", "Tom Hardy, Michelle Williams, Riz Ahmed", "Ruben Fleischer");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ready Player One", "../img/Covers/movies/ready-player-one-2018.jpg", 140, 2018, "Action, Aventure, Science-Fiction", "2045. Le monde est au bord du chaos. Les êtres humains se réfugient dans l'OASIS, univers virtuel mis au point par le brillant et excentrique James Halliday. Avant de disparaître, celui-ci a décidé de léguer son immense fortune à quiconque découvrira l'œuf de Pâques numérique qu'il a pris soin de dissimuler dans l'OASIS.", "Tye Sheridan, Olivia Cooke, Ben Mendelsohn", "Steven Spielberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Grinch", "../img/Covers/movies/le-grinch-2018.jpg", 85, 2018, "Animation, Comédie, Famille", "Chaque année à Noël, les Chous viennent perturber la tranquillité solitaire du Grinch avec des célébrations toujours plus grandioses, brillantes et bruyantes. Quand les Chous déclarent qu’ils vont célébrer Noël trois fois plus fort cette année, le Grinch réalise qu’il n’a plus qu’une solution pour retrouver la paix et la tranquillité: il doit voler Noël.", "Benedict Cumberbatch, Cameron Seely, Rashida Jones", "Yarrow Cheney, Scott Mosier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Aquaman", "../img/Covers/movies/aquaman-2018.jpg", 143, 2018, "Action, Aventure, Fantastique", "Les origines d’un héros malgré lui, dont le destin est d’unir deux mondes opposés, la terre et la mer. Cette histoire épique est celle d’un homme ordinaire destiné à devenir le roi des Sept Mers.", "Jason Momoa, Amber Heard, Willem Dafoe", "James Wan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("A Star Is Born", "../img/Covers/movies/a-star-is-born-2018.jpg", 136, 2018, "Drame, Musical, Romance", "Jackson Maine, musicien chevronné, découvre Ally, une chanteuse qui a du mal à percer. Alors que la jeune femme est sur le point de renoncer à faire carrière, Jackson tombe amoureux d'elle et la propulse sur le devant de la scène. Bientôt éclipsé par le succès d'Ally, il vit de plus en plus mal son propre déclin…", "Lady Gaga, Bradley Cooper, Sam Elliott", "Bradley Cooper");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Yéti & Compagnie", "../img/Covers/movies/yeti-et-compagnie-2018.jpg", 96, 2018, "Animation, Aventure, Comédie", "Vivant dans un petit village reculé, un jeune et intrépide yéti découvre une créature étrange qui, pensait-il jusque-là, n'existait que dans les contes : un humain ! Si c'est pour lui l'occasion de connaître la célébrité – et de conquérir la fille de ses rêves –, cette nouvelle sème le trouble dans la communauté yéti. Car qui sait les surprises que leur réserve encore le vaste monde ?", "Channing Tatum, James Corden, Zendaya", "Karey Kirkpatrick, Jason Reisig");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ant-Man et la Guêpe", "../img/Covers/movies/ant-man-et-la-guepe-2018.jpg", 118, 2018, "Action, Aventure, Comédie", "Scott Lang a bien du mal à concilier sa vie de super-héros et ses responsabilités de père. Mais ses réflexions sur les conséquences de ses choix tournent court lorsque Hope van Dyne et le Dr Hank Pym lui confient une nouvelle mission urgente… Scott va devoir renfiler son costume et apprendre à se battre aux côtés de La Guêpe afin de faire la lumière sur des secrets enfouis de longue date…", "Paul Rudd, Evangeline Lilly, Michael Peña", "Peyton Reed");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Pierre Lapin", "../img/Covers/movies/pierre-lapin-2018.jpg", 95, 2018, "Animation, Aventure, Comédie", "Le petit lapin préféré des jeunes lecteurs depuis des générations est désormais le héros d’un film plein d’aventures et d’espièglerie ! L'éternelle lutte de Pierre Lapin avec M. McGregor pour les légumes du potager va atteindre des sommets. Sans parler de leur rivalité pour plaire à cette charmante voisine qui adore les animaux… Bien au-delà du jardin, de nombreuses péripéties les entraîneront de la magnifique région des lacs en Angleterre jusqu’à Londres !", "James Corden, Fayssal Bazzi, Domhnall Gleeson", "Will Gluck");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Belle et Sébastien 3: le dernier chapitre", "../img/Covers/movies/belle-et-sebastien-3-2018.jpg", 91, 2017, "Aventure, Famille", "Deux ans ont passé. Sébastien est à l'aube de l'adolescence et Belle est devenue maman de trois adorables chiots. Pierre et Angelina sont sur le point de se marier et rêvent d'une nouvelle vie, ailleurs... Au grand dam de Sébastien qui refuse de quitter sa montagne.", "Félix Bossuet, Tchéky Karyo, Clovis Cornillac", "Clovis Cornillac");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("En eaux troubles", "../img/Covers/movies/en-eaux-troubles-2018.jpg", 113, 2018, "Action, Horreur, Science-Fiction", "Au cœur de l’océan Pacifique, le sous-marin d’une équipe de chercheurs a été attaqué par une créature gigantesque qu’on croyait disparue : le Megalodon, un requin préhistorique de 23 mètres de long.Le sauveteur-plongeur Jonas Taylor doit risquer sa vie pour sauver les hommes et les femmes prisonniers de l'embarcation… et affronter le prédateur le plus terrible de tous les temps.", "Jason Statham, Bingbing Li, Rainn Wilson", "Jon Turteltaub");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Jeu", "../img/Covers/movies/le-jeu-2018.jpg", 90, 2018, "Comédie, Drame", "Le temps d'un dîner, des couples d'amis décident de jouer à un 'jeu' : chacun doit poser son téléphone portable au milieu de la table et chaque SMS, appel téléphonique, mail, message Facebook,... devra être partagé avec les autres. Il ne faudra pas attendre bien longtemps pour que ce 'jeu' se transforme en cauchemar.", "Bérénice Bejo, Suzanne Clément, Stéphane De Groodt", "Fred Cavayé");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ferdinand", "../img/Covers/movies/ferdinand-2018.jpg", 108, 2017, "Animation, Aventure, Comédie", "Ferdinand est un taureau au grand cœur. Victime de son imposante apparence, il se retrouve malencontreusement capturé et arraché à son village d’origine. Bien déterminé à retrouver sa famille et ses racines, il se lance alors dans une incroyable aventure à travers l’Espagne, accompagné de la plus déjantée des équipes !", "John Cena, Kate McKinnon, Bobby Cannavale", "Carlos Saldanha");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Nonne", "../img/Covers/movies/la-nonne-2018.jpg", 96, 2018, "Horreur, Mystère, Thriller", "Quand on apprend le suicide d'une jeune nonne dans une abbaye roumaine, la stupéfaction est totale dans l'Église catholique. Le Vatican missionne aussitôt un prêtre au passé trouble et une novice pour mener l'enquête. Risquant leur vie, les deux ecclésiastiques doivent affronter une force maléfique qui bouscule leur foi et menace de détruire leur âme. Bientôt, l'abbaye est en proie à une lutte sans merci entre les vivants et les damnés…", "Taissa Farmiga, Demian Bichir, Bonnie Aarons ", "Corin Hardy");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Solo: A Star Wars Story", "../img/Covers/movies/solo-star-wars-story-2018.jpg", 135, 2018, "Action, Aventure, Science-Fiction", "Embarquez à bord du Faucon Millenium et partez à l’aventure en compagnie du plus célèbre vaurien de la galaxie. Au cours de périlleuses aventures dans les bas-fonds d’un monde criminel, Han Solo va faire la connaissance de son imposant futur copilote Chewbacca et croiser la route du charmant escroc Lando Calrissian…", "Alden Ehrenreich, Woody Harrelson, Emilia Clarke", "Ron Howard");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("BlacKkKlansman - J'ai infiltré le Ku Klux Klan", "../img/Covers/movies/blackkklansman-2018.jpg", 135, 2018, "Biopic, Comédie, Crime", "Ron Stallworth, un officier de police afro-américain du Colorado, a réussi à infiltrer le Ku Klux Klan local et devient presque le chef du chapitre local.", "John David Washington, Adam Driver, Laura Harrier", "Spike Lee");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Tomb Raider", "../img/Covers/movies/tomb-raider-2018.jpg", 119, 2018, "Action, Aventure, Fantastique", "Lara Croft, 21 ans, n'a ni projet, ni ambition : fille d'un explorateur excentrique porté disparu depuis sept ans, cette jeune femme rebelle et indépendante refuse de reprendre l'empire de son père. Convaincue qu'il n'est pas mort, elle met le cap sur la destination où son père a été vu pour la dernière fois : la tombe légendaire d'une île mythique au large du Japon. ", "Alicia Vikander, Dominic West, Walton Goggins", "Roar Uthaug");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Neuilly sa mère, sa mère", "../img/Covers/movies/neuilly-sa-mere-sa-mere-2018.jpg", 105, 2018, "Comédie", "En 2008, Sami Benboudaoud découvrait l’enfer de Neuilly-sur-seine ! Dix ans plus tard, alors que tout va pour le mieux pour Sami qui termine brillamment ses études de sciences politiques, plus rien ne va pour son cousin Charles de Chazelle...", "Denis Podalydès, Samy Seghir, Jérémy Denisty", "Gabriel Julien-Laferrière, Djamel Bensalah");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("American Nightmare 4 : Les Origines", "../img/Covers/movies/american-nightmare-4-2018.jpg", 97, 2018, "Action, Horreur, Science-Fiction", "Pour faire passer le taux de criminalité en-dessous de 1% le reste de l’année, les « Nouveaux Pères Fondateurs » testent une théorie sociale qui permettrait d’évacuer la violence durant une nuit dans une ville isolée. Mais lorsque l’agressivité des tyrans rencontre la rage de communautés marginalisées, le phénomène va s’étendre au-delà des frontières de la ville test jusqu’à atteindre la nation entière.", "Y'lan Noel, Lex Scott Davis, Joivan Wade", "Gerard McMurray");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Première année", "../img/Covers/movies/premiere-annee-2018.jpg", 92, 2018, "Comédie, Drame", "Antoine entame sa première année de médecine pour la troisième fois. Benjamin arrive directement du lycée, mais il réalise rapidement que cette année ne sera pas une promenade de santé. Dans un environnement compétitif violent, avec des journées de cours ardues et des nuits dédiées aux révisions plutôt qu'à la fête...", "Vincent Lacoste, William Lebghil, Michel Lerousseau", "Thomas Lilti");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Roi Lion", "../img/Covers/movies/le-roi-lion-2019.jpg", 118, 2019, "Animation, Aventure, Drame", "Au fond de la savane africaine, tous les animaux célèbrent la naissance de Simba, leur futur roi. Les mois passent. Simba idolâtre son père, le roi Mufasa, qui prend à cœur de lui faire comprendre les enjeux de sa royale destinée. Mais tout le monde ne semble pas de cet avis. Scar, le frère de Mufasa, l'ancien héritier du trône, a ses propres plans...", "Donald Glover, Beyoncé, Seth Rogen", "Jon Favreau");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Avengers: Endgame", "../img/Covers/movies/avengers-endgame-2019.jpg", 181, 2019, "Action, Aventure, Drame", "Thanos ayant anéanti la moitié de l’univers, les Avengers restants resserrent les rangs dans ce vingt-deuxième film des Studios Marvel, grande conclusion d’un des chapitres de l’Univers Cinématographique Marvel.", "Robert Downey Jr., Chris Evans, Mark Ruffalo", "Anthony Russo, Joe Russo");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Reine des neiges II", "../img/Covers/movies/la-reine-des-neiges-2-2019.jpg", 103, 2019, "Animation, Aventure, Comédie", "Pourquoi Elsa est-elle née avec des pouvoirs magiques ? La jeune fille rêve de l’apprendre, mais la réponse met son royaume en danger. Avec l’aide d’Anna, Kristoff, Olaf et Sven, Elsa entreprend un voyage aussi périlleux qu’extraordinaire.", "Kristen Bell, Idina Menzel, Josh Gad", "Chris Buck, Jennifer Lee");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Qu'est-ce qu'on a encore fait au Bon Dieu?", "../img/Covers/movies/quest-ce-quon-a-encore-fait-au-bon-dieu-2019.jpg", 99, 2019, "Comédie, Drame", "Claude et Marie Verneuil font face à une nouvelle crise. Leurs gendres, Rachid, David, Chao et Charles sont décidés à quitter la France avec femmes et enfants pour tenter leur chance à l’étranger.Incapables d’imaginer leur famille loin d’eux, Claude et Marie sont prêts à tout pour les retenir...", "Christian Clavier, Chantal Lauby, Ary Abittan", "Philippe de Chauveron");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Joker", "../img/Covers/movies/joker-2019.jpg", 122, 2019, "Crime, Drame, Thriller", "Le film, qui relate une histoire originale inédite sur grand écran, se focalise sur la figure emblématique de l’ennemi juré de Batman. Il brosse le portrait d’Arthur Fleck, un homme sans concession méprisé par la société.", "Joaquin Phoenix, Robert De Niro, Zazie Beetz", "Todd Phillips");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Toy Story 4", "../img/Covers/movies/toy-story-4-2019.jpg", 100, 2019, "Animation, Aventure, Comédie", "Woody a toujours privilégié la joie de ses jeunes propriétaires – Andy puis Bonnie – et de ses compagnons, n’hésitant pas à prendre tous les risques pour eux, aussi inconsidérés soient-ils. L’arrivée de Fourchette un nouveau jouet qui ne veut pas en être un met toute la petite bande en émoi.", "Tom Hanks, Tim Allen, Annie Potts", "Josh Cooley");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Star Wars: Episode IX -  L'Ascension de Skywalker", "../img/Covers/movies/star-wars-9-2019.jpg", 141, 2019, "Action, Aventure, Fantastique", "La conclusion de la saga Skywalker. De nouvelles légendes vont naître dans cette bataille épique pour la liberté.", "Daisy Ridley, John Boyega, Oscar Isaac", "J.J. Abrams");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Dragons 3 : Le monde caché", "../img/Covers/movies/dragons-3-2019.jpg", 104, 2019, "Animation, Action, Aventure", "Harold est maintenant le chef de Berk au côté d’Astrid et Krokmou, en tant que dragon, est devenu le leader de son espèce. Ils réalisent enfin leurs rêves de vivre en paix entre vikings et dragons. Mais lorsque l’apparition soudaine d’une Furie Eclair coïncide avec la plus grande menace que le village n’ait jamais connue, Harold et Krokmou sont forcés de quitter leur village pour un voyage dans un monde caché...", "Jay Baruchel, America Ferrera, F. Murray Abraham", "Dean DeBlois");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Captain Marvel", "../img/Covers/movies/captain-marvel-2019.jpg", 123, 2019, "Action, Aventure, Science-Fiction", "Captain Marvel raconte l’histoire de Carol Danvers qui va devenir l’une des super-héroïnes les plus puissantes de l’univers lorsque la Terre se révèle l’enjeu d’une guerre galactique entre deux races extraterrestres.", "Brie Larson, Samuel L. Jackson, Ben Mendelsohn", "Anna Boden, Ryan Fleck");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Spider-Man: Far from Home", "../img/Covers/movies/spider-man-far-from-home-2019.jpg", 129, 2019, "Action, Aventure", "L'araignée sympa du quartier décide de rejoindre ses meilleurs amis Ned, MJ, et le reste de la bande pour des vacances en Europe. Cependant, le projet de Peter de laisser son costume de super-héros derrière lui pendant quelques semaines est rapidement compromis quand il accepte à contrecoeur d'aider Nick Fury à découvrir le mystère de plusieurs attaques de créatures, qui ravagent le continent !", "Tom Holland, Samuel L. Jackson, Jake Gyllenhaal", "Jon Watts");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Nous Finirons Ensemble", "../img/Covers/movies/nous-finirons-ensemble-2019.jpg", 134, 2019, "Comédie, Drame, Famille", "Préoccupé, Max est parti dans sa maison au bord de la mer pour se ressourcer. Sa bande de potes, qu’il n’a pas vue depuis plus de 3 ans débarque par surprise pour lui fêter son anniversaire ! La surprise est entière mais l’accueil l’est beaucoup moins... ", "François Cluzet, Marion Cotillard, Gilles Lellouche", "Guillaume Canet");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Maléfique : Le Pouvoir du Mal", "../img/Covers/movies/malefique-2-2019.jpg", 119, 2019, "Aventure, Famille, Fantastique", "« Maléfique : Le Pouvoir du Mal » continue d’explorer les relations complexes entre la sorcière et la future reine, alors qu’elles nouent d’autres alliances et affrontent de nouveaux adversaires dans leur combat pour protéger leurs terres et les créatures magiques qui les peuplent.", "Angelina Jolie, Elle Fanning, Harris Dickinson", "Joachim Rønning");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Once Upon a Time in Hollywood", "../img/Covers/movies/once-upon-a-time-in-hollywood-2019.jpg", 161, 2019, "Comédie, Drame", "En 1969, la star de télévision Rick Dalton et le cascadeur Cliff Booth, sa doublure de longue date, poursuivent leurs carrières au sein d’une industrie qu’ils ne reconnaissent plus. ", "Leonardo DiCaprio, Brad Pitt, Margot Robbie", "Quentin Tarantino");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Fast & Furious : Hobbs & Shaw", "../img/Covers/movies/fast-furious-hobbs-shaw-2019.jpg", 136, 2019, "Action, Aventure", "Depuis que Hobbs, fidèle agent de sécurité au service diplomatique des Etats-Unis, combatif mais droit, et Shaw, un homme sans foi ni loi, ancien membre de l’élite militaire britannique, se sont affrontés en 2015 dans Fast & Furious 7 ; les deux hommes font tout ce qu’ils peuvent pour se nuire l’un à l’autre.Mais lorsque Brixton, un anarchiste génétiquement modifié, met la main sur une arme de destruction massive après avoir battu le meilleur agent du MI6 qui se trouve être la sœur de Shaw. Les deux ennemis de longue date vont devoir alors faire équipe pour faire tomber le seul adversaire capable de les anéantir. ", "Dwayne Johnson, Jason Statham, Idris Elba", "David Leitch");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Aladdin", "../img/Covers/movies/aladdin-2019.jpg", 128, 2019, "Aventure, Comédie, Famille", "Quand un charmant garçon des rues du nom d’Aladdin cherche à conquérir le cœur de la belle, énigmatique et fougueuse princesse Jasmine, il fait appel au tout puissant Génie, le seul qui puisse lui permettre de réaliser trois vœux, dont celui de devenir le prince Ali pour mieux accéder au palais…", "Will Smith, Mena Massoud, Naomi Scott", "Guy Ritchie");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Jumanji: Next Level", "../img/Covers/movies/jumanji-3-2019.jpg", 123, 2019, "Action, Aventure, Comédie", "L'équipe est de retour mais le jeu a changé. Alors qu'ils retournent dans Jumanji pour secourir l'un des leurs, ils découvrent un monde totalement inattendu. Des déserts arides aux montagnes enneigées, les joueurs vont devoir braver des espaces inconnus et inexplorés, afin de sortir du jeu le plus dangereux du monde.", "Dwayne Johnson, Jack Black, Kevin Hart", "Jake Kasdan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Dumbo", "../img/Covers/movies/dumbo-2019.jpg", 112, 2019, "Aventure, Famille, Fantastique", "Les enfants de Holt Farrier, ex-artiste de cirque chargé de s’occuper d’un éléphanteau dont les oreilles démesurées sont la risée du public, découvrent que ce dernier sait voler...", "Colin Farrell, Michael Keaton, Danny DeVito", "Tim Burton");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Comme des bêtes 2", "../img/Covers/movies/comme-des-betes-2-2019.jpg", 86, 2019, "Animation, Aventure, Comédie", "Le Fox-Terrier Max doit faire face à un grand bouleversement : sa propriétaire Katie s’est mariée et a eu un adorable bébé, Liam. Max est tellement obsédé par la garde du petit, qu’il en développe des troubles obsessionnels du comportement. Lors d’une excursion en famille dans une ferme, Max et le gros Duke vont faire la connaissance de vaches souffrant d’intolérances aux canidés, de renards hostiles et d’une dinde monstrueuse, ce qui ne va pas arranger les tocs de Max...", "Patton Oswalt, Kevin Hart, Harrison Ford", "Chris Renaud, Jonathan del Val");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ralph 2.0", "../img/Covers/movies/ralph-2-0-2018.jpg", 112, 2018, "Animation, Aventure, Comédie", "Ralph et son amie Vanellope von Schweetz vont prendre tous les risques en s’aventurant dans l’étrange univers d’Internet à la recherche d’une pièce de rechange pour réparer la borne de Sugar Rush, le jeu vidéo dans lequel vit Vanellope. Rapidement dépassés par le monde qui les entoure, ils vont devoir demander de l’aide aux habitants d’Internet...", "John C. Reilly, Sarah Silverman, Gal Gadot", "Phil Johnston, Rich Moore");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Hors Normes", "../img/Covers/movies/hors-normes-2019.jpg", 114, 2019, "Comédie, Drame", "Bruno et Malik vivent depuis 20 ans dans un monde à part, celui des enfants et adolescents autistes. Au sein de leurs deux associations respectives, ils forment des jeunes issus des quartiers difficiles pour encadrer ces cas qualifiés 'd'hyper complexes'.", "Vincent Cassel, Reda Kateb, Hélène Vincent", "Eric Toledano, Olivier Nakache");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Alita: Battle Angel", "../img/Covers/movies/alita-2019.jpg", 122, 2019, "Action, Aventure, Science-Fiction", "Lorsqu’Alita se réveille sans aucun souvenir de qui elle est, dans un futur qu’elle ne reconnaît pas, elle est accueillie par Ido, un médecin qui comprend que derrière ce corps de cyborg abandonné, se cache une jeune femme au passé extraordinaire. Ce n’est que lorsque les forces dangereuses et corrompues qui gèrent la ville d’Iron City se lancent à sa poursuite qu’Alita découvre la clé de son passé...", "Rosa Salazar, Christoph Waltz, Jennifer Connelly", "Robert Rodriguez");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Abominable", "../img/Covers/movies/abominable-2019.jpg", 97, 2019, "Animation, Aventure, Comédie", "Tout commence sur le toit d’un immeuble à Shanghai, avec l’improbable rencontre d’une jeune adolescente, l’intrépide Yi, avec un jeune Yeti. La jeune fille et ses amis Jin et Peng vont tenter de ramener chez lui celui qu’ils appellent désormais Everest, leur nouvel et étrange ami, afin qu’il puisse retrouver sa famille sur le toit du monde...", "Chloe Bennet, Albert Tsai, Tenzing Norgay Trainor", "Jill Culton, Todd Wilderman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Au nom de la terre", "../img/Covers/movies/au-nom-de-la-terre-2019.jpg", 103, 2019, "Biopic, Drame", "Pierre a 25 ans quand il rentre du Wyoming pour retrouver Claire sa fiancée et reprendre la ferme familiale. Vingt ans plus tard, l'exploitation s’est agrandie, la famille aussi. C’est le temps des jours heureux, du moins au début… Les dettes s’accumulent et Pierre s’épuise au travail, il sombre peu à peu...", "Guillaume Canet, Veerle Baetens, Anthony Bajon", "Édouard Bergeon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Mule", "../img/Covers/movies/la-mule-2018.jpg", 116, 2018, "Crime, Drame, Thriller", "À 80 ans, Earl Stone est aux abois. Il est non seulement fauché et seul, mais son entreprise risque d'être saisie. Il accepte alors un boulot qui – en apparence – ne lui demande que de faire le chauffeur. Sauf que, sans le savoir, il s'est engagé à être passeur de drogue pour un cartel mexicain.", "Clint Eastwood, Bradley Cooper, Laurence Fishburne", "Clint Eastwood");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Vie scolaire", "../img/Covers/movies/la-vie-scolaire-2019.jpg", 111, 2019, "Comédie, Drame", "Une année au coeur de l'école de la république, de la vie... et de la démerde ! Samia, jeune CPE novice, débarque de son Ardèche natale dans un collège réputé difficile de la ville de Saint-Denis. Elle y découvre les problèmes récurrents de discipline, la réalité sociale pesant sur le quartier, mais aussi l'incroyable vitalité et l'humour, tant des élèves que de son équipe de surveillants. ", "Zita Hanrot, Liam Pierron, Soufiane Guerrab", "Mehdi Idir, Grand Corps Malade");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Creed II", "../img/Covers/movies/creed-2-2018.jpg", 130, 2018, "Action, Drame, Sport", "Un jeune boxeur, qui va être père de famille, veut affronter le fils de celui qui a tué son père sur le ring il y a trente ans.", "Michael B. Jordan, Sylvester Stallone, Tessa Thompson", "Steven Caple Jr.");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Parasite", "../img/Covers/movies/parasite-2019.jpg", 132, 2019, "Drame, Thriller", "Toute la famille de Ki-taek est au chômage, et s’intéresse fortement au train de vie de la richissime famille Park. Un jour, leur fils réussit à se faire recommander pour donner des cours particuliers d’anglais chez les Park. C’est le début d’un engrenage incontrôlable...", "Song Kang-ho, Lee Sun-kyun, Cho Yeo-jeong", "Bong Joon Ho");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Misérables", "../img/Covers/movies/les-miserables-2019.jpg", 104, 2019, "Crime, Drame, Thriller", "Stéphane intègre la Brigade Anti-Criminalité de Montfermeil, dans le 93. Il fait la rencontre de ses nouveaux coéquipiers et découvre les tensions entre les différents groupes du quartier. Alors qu’ils se trouvent débordés lors d’une interpellation, un drone filme leurs moindres faits et gestes…", "Damien Bonnard, Alexis Manenti, Djebril Zonga", "Ladj Ly");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Pokémon Détective Pikachu", "../img/Covers/movies/pokemon-detective-pikachu-2019.jpg", 104, 2019, "Action, Aventure, Comédie", "Après la disparition de Harry Goodman, un détective privé, son fils Tim va tenter de découvrir ce qui s’est passé.  Le détective Pikachu, ancien partenaire de Harry, participe alors à l’enquête : un super-détective adorable à la sagacité hilarante, qui en laisse plus d’un perplexe, dont lui-même.", "Ryan Reynolds, Justice Smith, Kathryn Newton", "Rob Letterman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Nicky Larson et le parfum de Cupidon", "../img/Covers/movies/nicky-larson-2018.jpg", 91, 2018, "Action, Comédie, Crime", "Nicky Larson est le meilleur des gardes du corps, un détective privé hors-pair. Il est appelé pour une mission à hauts risques : récupérer le parfum de Cupidon, un parfum qui rendrait irrésistible celui qui l’utilise…", "Philippe Lacheau, Élodie Fontan, Tarek Boudali", "Philippe Lacheau");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Chant du loup", "../img/Covers/movies/le-chant-du-loup-2019.jpg", 115, 2019, "Action, Thriller", "Un jeune homme a le don rare de reconnaître chaque son qu’il entend. A bord d’un sous-marin nucléaire français, tout repose sur lui, l’Oreille d’Or. Réputé infaillible, il commet pourtant une erreur qui met l’équipage en danger de mort. Il veut retrouver la confiance de ses camarades mais sa quête les entraîne dans une situation encore plus dramatique.", "François Civil, Omar Sy, Mathieu Kassovitz", "Antonin Baudry");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ça : Chapitre 2", "../img/Covers/movies/ca-chapitre-2-2019.jpg", 169, 2019, "Drame, Fantastique, Horreur", "27 ans après la victoire du Club des Ratés sur Grippe-Sou, le sinistre Clown est de retour pour semer la terreur dans les rues de Derry. Désormais adultes, les membres du Club ont tous quitté la petite ville pour faire leur vie. Cependant, lorsqu'on signale de nouvelles disparitions d'enfants, Mike, le seul du groupe à être demeuré sur place, demande aux autres de le rejoindre. ", "Jessica Chastain, James McAvoy, Bill Hader", "Andy Muschietti");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("J'accuse", "../img/Covers/movies/j-accuse-2019.jpg", 132, 2019, "Drame, Historique, Thriller", "Pendant les 12 années qu’elle dura, l’Affaire Dreyfus déchira la France, provoquant un véritable séisme dans le monde entier. Dans cet immense scandale, le plus grand sans doute de la fin du XIXème siècle, se mêlent erreur judiciaire, déni de justice et antisémitisme. ", "Louis Garrel, Christophe Maratier, Pierre Poirot", "Roman Polanski");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Donne-moi des ailes", "../img/Covers/movies/donne-moi-des-ailes-2019.jpg", 113, 2019, "Aventure, Drame, Famille", "Christian, scientifique visionnaire, étudie les oies sauvages. Pour son fils, ado obnubilé par les jeux vidéos, l’idée de passer des vacances avec son père en pleine nature est un cauchemar. Pourtant, père et fils vont se rapprocher autour d’un projet fou : sauver une espèce en voie de disparition.", "Jean-Paul Rouve, Mélanie Doutey, Louis Vazquez", "Nicolas Vanier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Parc des merveilles", "../img/Covers/movies/le-parc-des-merveilles-2019.jpg", 85, 2019, "Animation, Aventure, Comédie", "Le Parc des Merveilles raconte l’histoire d’un parc d’attractions fabuleux né de l’imagination extraordinaire d’une petite fille appelée June. Un jour, le Parc prend vie...", "Sofia Mali, Jennifer Garner, Ken Hudson Campbell", "Dylan Brown");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("X-Men: Dark Phoenix", "../img/Covers/movies/x-men-dark-phoenix-2019.jpg", 113, 2019, "Action, Aventure, Science-Fiction", "Au cours d'une mission de sauvetage dans l'espace, Jean Grey frôle la mort, frappée par une mystérieuse force cosmique. De retour sur Terre, cette force la rend non seulement infiniment plus puissante, mais aussi beaucoup plus instable.", "James McAvoy, Michael Fassbender, Jennifer Lawrence", "Simon Kinberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La belle époque", "../img/Covers/movies/la-belle-epoque-2019.jpg", 115, 2019, "Comédie, Drame, Fantastique", "Victor, un sexagénaire désabusé, voit sa vie bouleversée le jour où Antoine, un brillant entrepreneur, lui propose une attraction d’un genre nouveau : mélangeant artifices théâtraux et reconstitution historique, cette entreprise propose à ses clients de replonger dans l’époque de leur choix.", "Daniel Auteuil, Guillaume Canet, Doria Tillier", "Nicolas Bedos");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Glass", "../img/Covers/movies/glass-2019.jpg", 129, 2019, "Drame, Horreur, Science-Fiction", "David Dunn - l’homme incassable - poursuit sa traque de La Bête, surnom donné à Kevin Crumb. De son côté, le mystérieux homme souffrant du syndrome des os de verre Elijah Price suscite à nouveau l’intérêt des forces de l’ordre en affirmant détenir des informations capitales sur les deux hommes…", "James McAvoy, Bruce Willis, Samuel L. Jackson", "M. Night Shyamalan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("After - Chapitre 1", "../img/Covers/movies/after-chapitre-1-2019.jpg", 105, 2019, "Drame, Romance", "A young woman falls for a guy with a dark secret and the two embark on a rocky relationship. Based on the novel by Anna Todd.", "Josephine Langford, Hero Fiennes Tiffin, Khadijha Red Thunder", "Jenny Gage");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Joyeuse retraite!", "../img/Covers/movies/joyeuse-retraite-2019.jpg", 97, 2019, "Comédie", "L’heure de la retraite est enfin arrivée pour Philippe et Marilou ! Ils s’apprêtent à réaliser leur rêve : partir vivre sous le soleil du Portugal. Au revoir le travail, au revoir la famille, au revoir les emmerdes ! Ils pensaient enfin être tranquilles… mais leur famille a d’autres projets pour eux !", "Michèle Laroque, Thierry Lhermitte, Judith Magre", "Fabrice Bracq");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("C'est quoi cette mamie ?!", "../img/Covers/movies/c-est-quoi-cette-mamie-2019.jpg", 99, 2019, "Comédie", "Une tribu des 7 demi-frères et sœurs doit quitter le grand appart’ avant la rentrée. Chacun part en vacances pour l’été avec ses parents respectifs. Gulliver, le petit dernier, est envoyé tout seul chez sa grand-mère. Mais l’excentrique Mamie Aurore n’est pas une baby-sitter comme les autres !", "Chantal Ladesou, Julie Gayet, Thierry Neuvic", "Gabriel Julien-Laferrière");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Mans 66", "../img/Covers/movies/le-mans-66-2019.jpg", 152, 2019, "Action, Biopic, Drame", "Basé sur une histoire vraie, le film suit une équipe d'excentriques ingénieurs américains menés par le visionnaire Carroll Shelby et son pilote britannique Ken Miles, qui sont envoyés par Henry Ford II pour construire à partir de rien une nouvelle automobile qui doit détrôner la Ferrari à la compétition du Mans de 1966.", "Matt Damon, Christian Bale, Jon Bernthal", "James Mangold");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Shazam!", "../img/Covers/movies/shazam-2019.jpg", 132, 2019, "Action, Aventure, Comédie", "Un garçon de quatorze ans découvre qu'il a le pouvoir de se transformer en un super-héros adulte en prononçant le mot magique :  'Shazam !'.", "Zachary Levi, Mark Strong, Asher Angel", "David F. Sandberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ad Astra", "../img/Covers/movies/ad-astra-2019.jpg", 123, 2019, "Aventure, Drame, Mystère", "L’astronaute Roy McBride s’aventure jusqu’aux confins du système solaire à la recherche de son père disparu et pour résoudre un mystère qui menace la survie de notre planète.", "Brad Pitt, Tommy Lee Jones, Ruth Negga", "James Gray");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Inséparables", "../img/Covers/movies/inseparables-2019.jpg", 94, 2019, "Comédie", "Mika, un petit escroc, a fait un rapide tour en prison, où il a fait la connaissance de « Poutine », un détenu cinglé et imprévisible. Sitôt sa peine purgée, il décide de repartir à zéro et de refaire sa vie. Alors qu’il s’apprête à épouser la fille d’un riche homme d’affaires, son passé le rattrape : Poutine débarque sans prévenir ! Mika va vite réaliser qu’on ne se débarrasse pas aisément d’un tel boulet...", "Ahmed Sylla, Alban Ivanov, Judith El Zein", "Varante Soudjian");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Tanguy, le retour", "../img/Covers/movies/tanguy-le-retour-2019.jpg", 93, 2019, "Comédie", "16 ans plus tard, Tanguy, qui a maintenant 44 ans, revient chez ses parents avec sa fille Zhu sous le bras car Meï Lin l’a quitté. Catastrophés de voir leur 'tout-petit' dans cet état, Paul et Édith font tout pour lui redonner goût à la vie, sans réaliser que ce faisant, ils tressent la corde pour se pendre. Car Tanguy recommence à se sentir bien chez ses parents…", "Sabine Azéma, André Dussollier, Éric Berger", "Étienne Chatiliez");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Famille Addams", "../img/Covers/movies/la-famille-addams-2019.jpg", 86, 2019, "Animation, Comédie, Famille", "La famille Addams, qui vivait jusque-là retranchée dans leur demeure, juchée en haut d’une colline brumeuse du New Jersey, se prépare à recevoir des membres éloignés encore plus étranges qu’eux à l’occasion de la Mazurka de Pugsley. Une cérémonie aux allures de rite de passage qui doit se dérouler devant la famille au complet et où le jeune garçon doit prouver qu’il est prêt à devenir un véritable mâle Addams.", "Kev Adams, Oscar Isaac, Mélanie Bernier", "Conrad Vernon, Greg Tiernan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Tenet", "../img/Covers/movies/tenet-2020.jpg", 150, 2020, "Action, Science-Fiction, Thriller", "Muni d'un seul mot – Tenet – et décidé à se battre pour sauver le monde, notre protagoniste sillonne l'univers crépusculaire de l'espionnage international. Sa mission le projettera dans une dimension qui dépasse le temps. Pourtant, il ne s'agit pas d'un voyage dans le temps, mais d'un renversement temporel…", "John David Washington, Robert Pattinson, Elizabeth Debicki", "Christopher Nolan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Sonic le film", "../img/Covers/movies/sonic-le-film-2020.jpg", 99, 2020, "Action, Aventure, Comédie", "L'histoire du hérisson bleu le plus rapide du monde qui arrive sur Terre, sa nouvelle maison. Sonic et son nouveau meilleur ami Tom font équipe pour sauver la planète du diabolique Dr. Robotnik, bien déterminé à régner sur le monde entier.", "Ben Schwartz, James Marsden, Jim Carrey", "Jeff Fowler");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Bad Boys for Life", "../img/Covers/movies/bad-boys-for-life-2020.jpg", 124, 2020, "Action, Comédie, Crime", "Les Bad Boys Mike Lowrey et Marcus Burnett se retrouvent pour résoudre une ultime affaire.", "Will Smith, Martin Lawrence, Vanessa Hudgens", "Adil El Arbi, Bilall Fallah");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ducobu 3", "../img/Covers/movies/ducobu-3-2020.jpg", 94, 2020, "Comédie, Famille", "Nouvelle rentrée des classes pour l'élève Ducobu, Léonie Gratin et l'instituteur Latouche. Mais cette année, un rival de taille pour Ducobu débarque à l’école : ' TGV ', le roi de la triche 2.0. Alors que la situation financière de Saint-Potache devient désastreuse, les deux cancres vont devoir unir leurs créativités pour remporter un concours de chant et sauver leur école.", "Elie Semoun, Émilie Caen, Frédérique Bel", "Elie Semoun");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Voyage du Dr Dolittle", "../img/Covers/movies/le-voyage-du-dr-dolittle-2020.jpg", 101, 2020, "Aventure, Comédie, Famille", "Après la perte de sa femme sept ans plus tôt, l’excentrique Dr. John Dolittle, célèbre docteur et vétérinaire de l’Angleterre de la Reine Victoria s’isole derrière les murs de son manoir, avec pour seule compagnie sa ménagerie d’animaux exotiques.", "Robert Downey Jr., Antonio Banderas, Michael Sheen", "Stephen Gaghan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("L'appel de la forêt", "../img/Covers/movies/l-appel-de-la-foret-2020.jpg", 100, 2020, "Aventure, Drame, Famille", "Un chien de traîneau en Alaska lutte pour sa survie en pleine nature.", "Harrison Ford, Omar Sy, Cara Gee", "Chris Sanders");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("10 jours sans maman", "../img/Covers/movies/10-jours-sans-maman-2020.jpg", 104, 2020, "Comédie, Famille", "Antoine, DRH d'une grande enseigne de bricolage, est en passe de devenir le numéro 1 de sa boîte. C'est le moment que choisit sa femme, Isabelle, pour faire une pause et prendre l'air quelques jours pour s'occuper d'elle. Antoine se retrouve alors seul à devoir gérer la maison et leurs quatre enfants.", "Franck Dubosc, Aure Atika, Alice David", "Ludovic Bernard");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("30 jours max", "../img/Covers/movies/30-jours-max-2020.jpg", 87, 2020, "Action, Comédie, Crime", "Rayane, policier maladroit, héroïque malgré lui dans 30 JOURS MAX, se trouve cette fois confronté à une situation des plus rocambolesques. Sa grand-mère a été kidnappée par un cartel mexicain, et il a 3 jours max pour la libérer.", "Tarek Boudali, Philippe Lacheau, Julien Arruti", "Tarek Boudali");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les blagues de Toto", "../img/Covers/movies/les-blagues-de-toto-2020.jpg", 84, 2020, "Comédie, Famille", "A l’école, Toto est bien plus doué pour faire rire ses copains qu’écouter les leçons de la maîtresse. Avec ses parents aussi, les blagues de Toto se transforment souvent en catastrophes… La dernière en date ? La chute d’une sculpture pendant un évènement organisé par le patron de son père. Mais cette fois-ci, Toto assure qu’il est innocent et refuse d’être accusé d’une bêtise que pour une fois, il n’a pas faite ! Avec ses meilleurs amis, il va mener l’enquête.", "Gavril Dartevelle, Guillaume de Tonquédec, Anne Marivin", "Pascal Bourdiaux");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Birds of Prey et la fantabuleuse histoire de Harley Quinn", "../img/Covers/movies/birds-of-prey-2020.jpg", 109, 2020, "Action, Comédie, Crime", "Lorsque Roman Sionis, l'ennemi le plus abominable – et le plus narcissique – de Gotham, et son fidèle acolyte Zsasz décident de s'en prendre à une certaine Cass, la ville est passée au peigne fin pour retrouver la trace de la jeune fille. Les parcours de Harley, de la Chasseuse, de Black Canary et de Renee Montoya se télescopent et ce quatuor improbable n'a d'autre choix que de faire équipe pour éliminer Roman…", "Margot Robbie, Rosie Perez, Mary Elizabeth Winstead", "Cathy Yan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Trolls 2 - Tournée mondiale", "../img/Covers/movies/les-trolls-2-2020.jpg", 91, 2020, "Animation, Aventure, Comédie", "Reine Barb, membre de la royauté hard-rock, aidée de son père Roi Thrash, veut détruire tous les autres genres de musique pour laisser le rock régner en maître. Le destin du monde en jeu, Poppy et Branch, accompagnés de leurs amis – Biggie, Chenille, Satin, Cooper  et Guy Diamond – partent visiter tous les autres territoires pour unifier les Trolls contre Barb, qui cherche à tous les reléguer au second-plan.", "Anna Kendrick, Justin Timberlake, Rachel Bloom", "Walt Dohrn, David P. Smith");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Spider-Man: No Way Home", "../img/Covers/movies/spider-man-no-way-home-2021.jpg", 148, 2021, "Action, Aventure, Fantastique", "Pour la première fois dans son histoire cinématographique, Spider-Man, le héros sympa du quartier est démasqué et ne peut désormais plus séparer sa vie normale de ses lourdes responsabilités de super-héros. Quand il demande de l'aide à Doctor Strange, les enjeux deviennent encore plus dangereux...", "Tom Holland, Zendaya, Benedict Cumberbatch", "Jon Watts");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Mourir peut attendre", "../img/Covers/movies/mourir-peut-attendre-2021.jpg", 163, 2021, "Action, Aventure, Thriller", "Dans MOURIR PEUT ATTENDRE, Bond a quitté les services secrets et coule des jours heureux en Jamaïque. Mais sa tranquillité est de courte durée car son vieil ami Felix Leiter de la CIA débarque pour solliciter son aide : il s'agit de sauver un scientifique qui vient d'être kidnappé.", "Daniel Craig, Ana de Armas, Rami Malek", "Cary Joji Fukunaga");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Dune", "../img/Covers/movies/dune-2021.jpg", 155, 2021, "Action, Aventure, Drame", "L'histoire de Paul Atreides, voué à connaître un destin hors du commun qui le dépasse totalement. Car s'il veut préserver l'avenir de sa famille et de son peuple, il devra se rendre sur la planète la plus dangereuse de l'univers.", "Timothée Chalamet, Rebecca Ferguson, Zendaya", "Denis Villeneuve");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Kaamelott – Premier volet", "../img/Covers/movies/kaamelott-premier-volet-2021.jpg", 120, 2021, "Aventure, Comédie, Fantastique", "Le tyrannique Lancelot-du-Lac et ses mercenaires saxons font régner la terreur sur le royaume de Logres. Arthur parviendra-t-il à fédérer les clans rebelles, renverser son rival, reprendre Kaamelott et restaurer la paix sur l'île de Bretagne ?", "Alexandre Astier, Lionnel Astier, Antoine de Caunes", "Alexandre Astier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Bac Nord", "../img/Covers/movies/bac-nord-2020.jpg", 105, 2020, "Action, Thriller, Policier", "2012. Les quartiers Nord de Marseille détiennent un triste record : la zone au taux de criminalité le plus élevé de France. Poussée par sa hiérarchie, la BAC Nord, brigade de terrain, cherche sans cesse à améliorer ses résultats.", "Gilles Lellouche, Karim Leklou, François Civil", "Cédric Jimenez");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Encanto, la fantastique famille Madrigal", "../img/Covers/movies/encanto-2021.jpg", 102, 2021, "Animation, Comédie, Famille", "Dans un mystérieux endroit niché au cœur des montagnes de Colombie, la fantastique famille Madrigal habite une maison enchantée dans une cité pleine de vie, un endroit merveilleux appelé Encanto.", "Camille Timmerman, Stephanie Beatriz, José Garcia", "Jared Bush, Byron Howard, Charise Castro Smith");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Fast & Furious 9", "../img/Covers/movies/fast-furious-9-2021.jpg", 143, 2021, "Action, Crime, Thriller", "Dom Toretto rassemble son équipe pour démanteler un complot à échelle mondiale mené par le tueur le plus implacable qu’ils aient jamais affronté, aussi redoutable avec une arme que derrière un volant : un homme qui n’est autre que le frère désavoué de Dom, Jakob.", "Vin Diesel, Michelle Rodriguez, Jordana Brewster", "Justin Lin");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Tuche 4", "../img/Covers/movies/les-tuche-4-2021.jpg", 101, 2021, "Comédie", "Après avoir démissionné de son poste de président de la république, Jeff et sa famille sont heureux de retrouver leur village de Bouzolles. A l’approche des fêtes de fin d’année, Cathy demande un unique cadeau : renouer les liens avec sa sœur Maguy, et son mari Jean-Yves avec qui Jeff est fâché depuis 10 ans.", "Jean-Paul Rouve, Isabelle Nanty, Michel Blanc", "Olivier Baroux");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Conjuring 3 : sous l'emprise du diable", "../img/Covers/movies/conjuring-3-2021.jpg", 112, 2021, "Horreur, Mystère, Thriller", "Dans cette affaire issue de leurs dossiers secrets – l'une des plus spectaculaires – , Ed et Lorrain commencent par se battre pour protéger l'âme d'un petit garçon, puis basculent dans un monde radicalement inconnu. Ce sera la première fois dans l'histoire des États-Unis qu'un homme soupçonné de meurtre plaide la possession démoniaque comme ligne de défense.", "Patrick Wilson, Vera Farmiga, Ruairi O'Connor", "Michael Chaves");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Eternels", "../img/Covers/movies/les-eternels-2021.jpg", 156, 2021, "Action, Aventure, Fantastique", "Lorsque les Déviants, des créatures monstrueuses que l’on croyait disparues depuis longtemps, réapparaissent mystérieusement, les Éternels sont à nouveau obligés de se réunir pour défendre l’humanité…", "Gemma Chan, Richard Madden, Angelina Jolie", "Chloé Zhao");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Black Widow", "../img/Covers/movies/black-widow-2021.jpg", 134, 2021, "Action, Aventure, Science-Fiction", "Natasha Romanoff, alias Black Widow, voit resurgir la part la plus sombre de son passé pour faire face à une redoutable conspiration liée à sa vie d’autrefois. Poursuivie par une force qui ne reculera devant rien pour l’abattre, Natasha doit renouer avec ses activités d’espionne et avec des liens qui furent brisés, bien avant qu’elle ne rejoigne les Avengers.", "Scarlett Johansson, Florence Pugh, David Harbour", "Cate Shortland");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Venom: Let There Be Carnage", "../img/Covers/movies/venom-let-there-be-carnage-2021.jpg", 97, 2021, "Action, Science-Fiction, Thriller", "Tom Hardy est de retour sur grand écran sous les traits de Venom, l'un des personnages les plus complexes de l'univers Marvel.", "Tom Hardy, Woody Harrelson, Michelle Williams", "Andy Serkis");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("OSS 117: Alerte rouge en Afrique noire", "../img/Covers/movies/oss-117-alerte-rouge-en-afrique-noire-2021.jpg", 117, 2021, "Action, Comédie", "1981. Hubert Bonisseur de La Bath, alias OSS 117, est de retour. Pour cette nouvelle mission, plus délicate, plus périlleuse et plus torride que jamais, il est contraint de faire équipe avec un jeune collègue, le prometteur OSS 1001.", "Jean Dujardin, Pierre Niney, Fatou N'Diaye", "Nicolas Bedos");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Bodin's en Thaïlande", "../img/Covers/movies/les-bodins-en-thailande-2021.jpg", 98, 2021, "Comédie", "Maria Bodin, vieille fermière roublarde et autoritaire de 87 ans, doit faire face à une nouvelle épreuve : son grand nigaud de fils a perdu le goût de la vie. Suivant l’avis du psychiatre, la mère Bodin se résigne donc à casser sa tirelire pour payer des vacances à son fils… En Thaïlande !", "Vincent Dubois, Jean-Christian Fraiscinet, Nicolas Marié", "Frédéric Forestier");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Eiffel", "../img/Covers/movies/eiffel-2021.jpg", 108, 2021, "Biopic, Drame, Historique", "Venant tout juste de terminer sa collaboration sur la Statue de la Liberté, Gustave Eiffel est au sommet de sa carrière. Le gouvernement français veut qu’il crée quelque chose de spectaculaire pour l’Exposition Universelle de 1889 à Paris, mais Eiffel ne s’intéresse qu’au projet de métropolitain.", "Romain Duris, Emma Mackey, Pierre Deladonchamps", "Martin Bourboulon");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Pat’ Patrouille - Le film", "../img/Covers/movies/la-pat-patrouille-le-film-2021.jpg", 86, 2021, "Animation, Action, Aventure", "La Pat’ Patrouille part en mission pour sa première grande aventure au cinéma ! Près de chez eux, leur plus grand rival, Monsieur Hellinger, devient le maire d' Aventureville et commence à semer le trouble.", "Tyler Perry, Ron Pardo, Will Brisbin", "Cal Brunker");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Cruella", "../img/Covers/movies/cruella-2021.jpg", 134, 2021, "Aventure, Comédie, Crime", "Londres, années 70, en plein mouvement punk rock. Escroc pleine de talent, Estella est résolue à se faire un nom dans le milieu de la mode. Elle se lie d’amitié avec deux jeunes vauriens qui apprécient ses compétences d’arnaqueuse et mène avec eux une existence criminelle dans les rues de Londres.", "Emma Stone, Emma Thompson, Joel Fry", "Craig Gillespie");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Shang-Chi et la Légende des Dix Anneaux", "../img/Covers/movies/shang-chi-et-la-legende-des-dix-anneaux-2021.jpg", 132, 2021, "Action, Aventure, Fantastique", "Shang-Chi va devoir affronter un passé qu’il pensait avoir laissé derrière lui lorsqu’il est pris dans la toile de la mystérieuse organisation des dix anneaux.", "Simu Liu, Awkwafina, Tony Leung Chiu-wai", "Destin Daniel Cretton");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Adieu Les Cons", "../img/Covers/movies/adieu-les-cons-2020.jpg", 87, 2020, "Comédie, Drame", "Lorsque Suze Trappet apprend à 43 ans qu’elle est sérieusement malade, elle décide de partir à la recherche de l'enfant qu’elle a été forcée d'abandonner quand elle avait 15 ans.Sa quête administrative va lui faire croiser JB, quinquagénaire en plein burn out, et M. Blin, archiviste aveugle d’un enthousiasme impressionnant. À eux trois, ils se lancent dans une quête aussi spectaculaire qu’improbable.", "Virginie Efira, Albert Dupontel, Nicolas Marié", "Albert Dupontel");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Boîte noire", "../img/Covers/movies/boite-noire-2021.jpg", 129, 2021, "Drame, Mystère, Thriller", "Technicien au BEA, Mathieu Vasseur est propulsé enquêteur en chef sur une catastrophe aérienne sans précédent. L’analyse minutieuse des boîtes noires va pousser Mathieu à mener en secret sa propre investigation...", "Pierre Niney, Lou de Laâge, André Dussollier", "Yann Gozlan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Loup et le lion", "../img/Covers/movies/le-loup-et-le-lion-2021.jpg", 99, 2021, "Aventure, Drame, Famille", "A la mort de son grand père, Alma, jeune pianiste de 20 ans revient dans la maison de son enfance, perdue sur une île déserte du Canada. Là, tout bascule quand un louveteau et un lionceau en détresse surgissent dans sa vie.", "Molly Kunz, Graham Greene, Charlie Carrick", "Gilles de Maistre");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Tous en scène 2", "../img/Covers/movies/tous-en-scene-2-2021.jpg", 110, 2021, "Animation, Aventure, Comédie", "Buster Moon et sa troupe de bêtes de scène voient encore plus grand avec un spectacle complètement fou. Ils n’ont qu’un seul problème : persuader la star la plus anti sociale que le monde ait jamais connu de remonter sur scène avec eux.", "Matthew McConaughey, Reese Witherspoon, Scarlett Johansson", "Garth Jennings, Christophe Lourdelet");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Croods 2 : une nouvelle ère", "../img/Covers/movies/les-croods-2-2020.jpg", 95, 2020, "Animation, Aventure, Comédie", "Les Croods ont besoin d'un nouvel endroit où habiter. La famille préhistorique part alors en quête d'un endroit plus sûr. Quand ils découvrent un paradis idyllique entouré de murs, ils pensent que tous leurs problèmes sont résolus... Mais une famille y vit déjà : les Betterman.", "Nicolas Cage, Emma Stone, Ryan Reynolds", "Joel Crawford");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Baby Boss 2 : une affaire de famille", "../img/Covers/movies/baby-boss-2-2021.jpg", 107, 2021, "Animation, Aventure, Comédie", "Dans la suite de BABY BOSS, les frères Templeton – Tim et Ted, ex-Baby Boss – sont désormais adultes et se sont perdus de vue. Tandis que Tim est père au foyer, Ted est patron d’un fonds spéculatif. Mais un nouveau Baby Boss au caractère bien trempé s’apprête à ressouder les liens entre les deux frères…", "Alec Baldwin, James Marsden, Amy Sedaris", "Tom McGrath");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Avatar : la voie de l'eau ", "../img/Covers/movies/avatar-la-voie-de-l-eau-2022.jpg", 192, 2022, "Action, Aventure, Fantastique", "Se déroulant plus d’une décennie après les événements relatés dans le premier film, AVATAR : LA VOIE DE L’EAU raconte l'histoire des membres de la famille Sully (Jake, Neytiri et leurs enfants), les épreuves auxquelles ils sont confrontés, les chemins qu’ils doivent emprunter pour se protéger les uns les autres, les batailles qu’ils doivent mener pour rester en vie et les tragédies qu'ils endurent.", "Zoe Saldana, Sam Worthington, Kate Winslet", "James Cameron");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Top Gun: Maverick", "../img/Covers/movies/top-gun-maverick-2022.jpg", 130, 2022, "Action, Drame", "Après avoir été l’un des meilleurs pilotes de chasse de la Marine américaine, Pete 'Maverick' Mitchell continue à repousser ses limites en tant que pilote d'essai. Il est chargé de former un détachement de jeunes diplômés de l’école Top Gun pour une mission spéciale qu’aucun pilote n'aurait jamais imaginée.", "Tom Cruise, Jennifer Connelly, Miles Teller", "Joseph Kosinski");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Minions 2 : Il était une fois Gru", "../img/Covers/movies/les-minions-2-2022.jpg", 87, 2022, "Animation, Aventure, Comédie", "Alors que les années 70 battent leur plein, Gru qui grandit en banlieue au milieu des jeans à pattes d’éléphants et des chevelures en fleur, met sur pied un plan machiavélique à souhait pour réussir à intégrer un groupe célèbre de super méchants, connu sous le nom de Vicious 6, dont il est le plus grand fan.", "Steve Carell, Pierre Coffin, Alan Arkin", "Kyle Balda, Brad Ableson, Jonathan del Val");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Jurassic World: Le Monde d'après", "../img/Covers/movies/jurassic-world-le-monde-d-apres-2022.jpg", 147, 2022, "Action, Aventure, Science-Fiction", "Quatre ans après la destruction de Isla Nublar. Les dinosaures font désormais partie du quotidien de l’humanité entière. Un équilibre fragile qui va remettre en question la domination de l’espèce humaine.", "Chris Pratt, Bryce Dallas Howard, Laura Dern", "Colin Trevorrow");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Black Panther: Wakanda Forever", "../img/Covers/movies/black-panther-wakanda-forever-2022.jpg", 161, 2022, "Action, Aventure, Drame", "La Reine Ramonda, Shuri, M’Baku, Okoye et les Dora Milaje luttent pour protéger leur nation des ingérences d’autres puissances mondiales après la mort du roi T’Challa. Mais une terrible menace surgit d’un royaume caché au plus profond des océans : Talokan.", "Letitia Wright, Lupita Nyong'o, Danai Gurira", "Ryan Coogler");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Doctor Strange in the Multiverse of Madness", "../img/Covers/movies/doctor-strange-in-the-multiverse-of-madness-2022.jpg", 126, 2022, "Action, Aventure, Fantastique", "L'univers cinématographique Marvel déverrouille et repousse les limites du multivers encore plus loin. Voyagez dans l’inconnu avec Doctor Strange, qui avec l’aide d’anciens et de nouveaux alliés mystiques, traverse les réalités hallucinantes et dangereuses du multivers pour affronter un nouvel adversaire mystérieux.", "Benedict Cumberbatch, Elizabeth Olsen, Chiwetel Ejiofor", "Sam Raimi");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("The Batman", "../img/Covers/movies/the-batman-2022.jpg", 176, 2022, "Action, Crime, Drame", "Lorsqu'un tueur s'en prend à l'élite de Gotham par une série de machinations sadiques, une piste d'indices cryptiques envoie Bruce Wayne alias Batman sur une enquête dans la pègre, où il rencontre des personnages tels que Selina Kyle, alias Catwoman, Oswald Cobblepot, alias le Pingouin, Carmine Falcone et Edward Nashton, alias l’Homme-Mystère.", "Robert Pattinson, Zoë Kravitz, Jeffrey Wright", "Matt Reeves");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Thor: Love and Thunder", "../img/Covers/movies/thor-love-and-thunder-2022.jpg", 118, 2022, "Action, Aventure, Fantastique", "Alors que Thor est en pleine introspection et en quête de sérénité, sa retraite est interrompue par un tueur galactique connu sous le nom de Gorr, qui s’est donné pour mission d’exterminer tous les dieux.", "Chris Hemsworth, Natalie Portman, Christian Bale", "Taika Waititi");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Animaux Fantastiques : les Secrets de Dumbledore", "../img/Covers/movies/les-animaux-fantastiques-2022.jpg", 142, 2022, "Aventure, Famille, Fantastique", "Le professeur Albus Dumbledore sait que le puissant mage noir Gellert Grindelwald cherche à prendre le contrôle du monde des sorciers. Incapable de l’empêcher d’agir seul, il sollicite le magizoologiste Norbert Dragonneau pour qu’il réunisse une équipe.", "Eddie Redmayne, Jude Law, Ezra Miller", "David Yates");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Uncharted", "../img/Covers/movies/uncharted-2022.jpg", 116, 2022, "Action, Aventure", "Nathan Drake, voleur astucieux et intrépide, est recruté par le chasseur de trésors chevronné Victor « Sully » Sullivan pour retrouver la fortune de Ferdinand Magellan, disparue il y a 500 ans.", "Tom Holland, Mark Wahlberg, Antonio Banderas", "Ruben Fleischer");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Qu'est-ce qu'on a tous fait au Bon Dieu ?", "../img/Covers/movies/quest-ce-quon-a-tous-fait-au-bon-dieu-2021.jpg", 98, 2021, "Comédie", "Ce sont bientôt les 40 ans de mariage de Claude et Marie Verneuil. Pour cette occasion, leurs quatre filles décident d’organiser une grande fête surprise dans la maison familiale de Chinon et d’y inviter les parents de chacun des gendres, pour quelques jours.", "Christian Clavier, Chantal Lauby, Ary Abittan", "Philippe de Chauveron");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Novembre", "../img/Covers/movies/novembre-2022.jpg", 106, 2022, "Policier, Drame, Thriller", "Une plongée au cœur de l’Anti-Terrorisme pendant les 5 jours d'enquête qui ont suivi les attentats du 13 novembre.", "Jean Dujardin, Anaïs Demoustier, Sandrine Kiberlain", "Cédric Jimenez");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Simone, le voyage du siècle", "../img/Covers/movies/simone-le-voyage-du-siecle-2022.jpg", 140, 2022, "Biopic, Drame, Historique", "Le destin de Simone Veil, son enfance, ses combats politiques, ses tragédies. Le portrait épique et intime d’une femme au parcours hors du commun qui a bousculé son époque en défendant un message humaniste toujours d’une brûlante actualité.", "Elsa Zylberstein, Rebecca Marder, Élodie Bouchez", "Olivier Dahan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Sonic 2 le film", "../img/Covers/movies/sonic-2-le-film-2022.jpg", 122, 2022, "Action, Aventure, Comédie", "Bien installé dans la petite ville de Green Hills, Sonic veut maintenant prouver qu’il a l’étoffe d' un véritable héros. Un défi de taille se présente à lui quand le Dr Robotnik refait son apparition.", "James Marsden, Jim Carrey, Ben Schwartz", "Jeff Fowler");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Black Adam", "../img/Covers/movies/black-adam-2022.jpg", 125, 2022, "Action, Aventure, Fantastique", "Près de cinq millénaires après avoir reçu les super-pouvoirs des anciens dieux – et avoir été emprisonné dans la foulée –, Black Adam est libéré de sa tombe terrestre, prêt à exercer sa propre justice dans le monde moderne…", "Dwayne Johnson, Aldis Hodge, Pierce Brosnan", "Jaume Collet-Serra");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Maison de retraite", "../img/Covers/movies/maison-de-retraite-2022.jpg", 97, 2022, "Comédie", "Afin d’éviter la case prison, Milann, 30 ans, est contraint d’effectuer 300 heures de travaux d’intérêt généraldans une maison de retraite. Au fil des semaines, il découvre que l’établissement profite de la vulnérabilité de ses pensionnaires pour les arnaquer.", "Kev Adams, Gérard Depardieu, Daniel Prévost", "Thomas Gilou");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Chat Potté 2 : la dernière quête", "../img/Covers/movies/le-chat-potte-2-2022.jpg", 102, 2022, "Animation, Aventure, Comédie", "Le Chat Potté découvre que sa passion pour l'aventure et son mépris du danger ont fini par lui coûter cher : il a épuisé huit de ses neuf vies, et en a perdu le compte au passage. Afin de retomber sur ses pattes notre héros velu se lance littéralement dans la quête de sa vie.", "Antonio Banderas, Salma Hayek, Harvey Guillén", "Joel Crawford, Januel Mercado");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Super-héros malgré lui", "../img/Covers/movies/super-heros-malgre-lui-2021.jpg", 82, 2021, "Action, Comédie", "Apprenti comédien en galère, Cédric décroche enfin son premier rôle dans un film de super-héros. Un soir, il est victime d'un accident qui lui fait perdre la mémoire. A son réveil, il est persuadé d'être devenu le personnage du film avec une mission périlleuse à accomplir.", "Philippe Lacheau, Julien Arruti, Tarek Boudali", "Philippe Lacheau");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Bullet Train", "../img/Covers/movies/bullet-train-2022.jpg", 127, 2022, "Action, Comédie, Thriller", "Coccinelle est un assassin malchanceux et particulièrement déterminé à accomplir sa nouvelle mission paisiblement après que trop d'entre elles aient déraillé. Mais le destin en a décidé autrement et l'embarque dans le train le plus rapide au monde aux côtés d'adversaires redoutables...", "Brad Pitt, Joey King, Aaron Taylor-Johnson", "David Leitch");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Buzz l'éclair", "../img/Covers/movies/buzz-leclair-2022.jpg", 105, 2022, "Animation, Action, Aventure", "La véritable histoire du légendaire Ranger de l’espace qui, depuis, a inspiré le jouet que nous connaissons tous. Après s’être échoué avec sa commandante et son équipage sur une planète hostile située à 4,2 millions d’années-lumière de la Terre, Buzz l’Eclair tente de ramener tout ce petit monde sain et sauf à la maison.", "Chris Evans, Keke Palmer, Peter Sohn", "Angus MacLane");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Vaillante", "../img/Covers/movies/vaillante-2022.jpg", 92, 2022, "Animation, Aventure, Comédie", "Depuis qu'elle est enfant, Georgia Nolan n’a qu’une seule ambition : devenir pompier comme son père ! Hélas, à New York en 1932, les femmes n’ont pas le droit d'exercer cette profession...", "Alice Pol, Vincent Cassel, Valérie Lemercier", "Theodore Ty, Laurent Zeitoun");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Smile", "../img/Covers/movies/smile-2022.jpg", 115, 2022, "Horreur, Mystère, Thriller", "Après avoir été témoin d'un incident traumatisant impliquant l’une de ses patientes, la vie de la psychiatre Rose Cotter tourne au cauchemar. Terrassée par une force mystérieuse, Rose va devoir se confronter à son passé pour tenter de survivre…", "Sosie Bacon, Jessie T. Usher, Kyle Gallner", "Parker Finn");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Elvis", "../img/Covers/movies/elvis-2022.jpg", 159, 2022, "Biopic, Drame, Musical", "La vie et l'œuvre musicale d'Elvis Presley à travers le prisme de ses rapports complexes avec son mystérieux manager, le colonel Tom Parker. Le film explorera leurs relations sur une vingtaine d'années, de l'ascension du chanteur à son statut de star inégalé.", "Tom Hanks, Austin Butler, Olivia DeJonge", "Baz Luhrmann");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Krypto et les Super-Animaux", "../img/Covers/movies/krypto-et-les-super-animaux-2022.jpg", 105, 2022, "Animation, Action, Aventure", "Krypto, le super-chien de Superman, se trouve face à un défi immense : sauver son maître, enlevé par Lex Luthor et son maléfique cochon d’inde Lulu. Pour cela, il devra faire équipe avec une bande d’animaux au grand cœur mais plutôt maladroits.", "Dwayne Johnson, Kevin Hart, Kate McKinnon", "Jared Stern, Sam J. Levine");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Ducobu Président !", "../img/Covers/movies/ducobu-president-2022.jpg", 86, 2022, "Comédie, Famille", "Une nouvelle année scolaire démarre pour Ducobu ! A l’école Saint Potache, une élection exceptionnelle va avoir lieu pour élire le président des élèves. C’est le début d’une campagne électorale un peu folle dans laquelle vont se lancer les deux adversaires principaux : Ducobu et Léonie.", "Elie Semoun, Émilie Caen, Gabin Tomasino", "Elie Semoun");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Bad Guys", "../img/Covers/movies/les-bad-guys-2022.jpg", 100, 2022, "Animation, Action, Aventure", "LES BAD GUYS, la nouvelle comédie d’aventures de Dreamworks Animations, inspirée par la série éponyme de livres pour enfants à succès, met en scène une bande d’animaux, redoutables criminels de haut vol, qui sont sur le point de commettre leur méfait le plus éclatant : devenir des citoyens respectables.", "Sam Rockwell, Marc Maron, Awkwafina", "Pierre Perifel");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Grande Vadrouille", "../img/Covers/movies/la-grande-vadrouille-1966.jpg", 132, 1966, "Aventure, Comédie, Guerre", "En 1942, un avion anglais est abattu par les Allemands au-dessus de Paris. Les trois pilotes sautent en parachute et atterrissent dans différents endroits de la capitale. ", "Bourvil, Louis de Funès, Claudio Brook", "Gérard Oury");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les 101 Dalmatiens", "../img/Covers/movies/les-101-dalmatiens-1961.jpg", 79, 1961, "Animation, Aventure, Comédie", "Pongo et Perdita, deux magnifiques dalmatiens, deviennent parents de quinze bébés chiens. Mais l’infâme Cruella convoite les chiots. Un soir, ses sbires font main basse sur la portée. Pongo et Perdita se lancent alors dans un plan de sauvetage désespéré...", "Rod Taylor, Betty Lou Gerson, J. Pat O'Malley", "Clyde Geronimi, Hamilton Luske, Wolfgang Reitherman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Astérix et Obélix : Mission Cléopâtre", "../img/Covers/movies/asterix-et-obelix-mission-cleopatre-2002.jpg", 107, 2002, "Aventure, Comédie, Famille", "Cléopâtre décide, pour défier l'Empereur romain Jules César, de construire en trois mois un palais somptueux en plein désert. Si elle y parvient, celui-ci devra concéder que le peuple égyptien est le plus grand de tous les peuples...", "Gérard Depardieu, Christian Clavier, Jamel Debbouze", "Alain Chabat");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Visiteurs", "../img/Covers/movies/les-visiteurs-1993.jpg", 107, 1993, "Comédie, Fantastique", "En l'an de grace 1123, le comte de Montmirail et son fidèle écuyer, Jacquouille la Fripouille, se retrouvent propulsés en l'an 1992 apres avoir bu une potion magique fabriquée par l'enchanteur Eusaebius, censée leur permettre de se défaire d'un terrible sort.", "Christian Clavier, Jean Reno, Valérie Lemercier", "Jean-Marie Poiré");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Cendrillon", "../img/Covers/movies/cendrillon-1950.jpg", 74, 1950, "Animation, Famille, Fantastique", "La servante Cendrillon reçoit la visite de sa marraine fée qui la pare d'une robe de princesse à l'occasion d'une reception donnée par le prince. N'ayant que la permission de minuit, elle s'enfuit et perd une pantoufle de verre...", "Ilene Woods, James MacDonald, Eleanor Audley", "Clyde Geronimi, Wilfred Jackson, Hamilton Luske");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Aristochats", "../img/Covers/movies/les-aristochats-1970.jpg", 78, 1970, "Animation, Aventure, Comédie", "Paris, 1910. Madame de Bonnefamille, millionnaire excentrique, vit seule entourée de ses chats : Duchesse et ses trois petits, Marie, Toulouse et Berlioz. Un jour, elle convie son notaire pour léguer toute sa fortune à ses compagnons à quatre pattes.", "Phil Harris, Eva Gabor, Sterling Holloway", "Wolfgang Reitherman");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Belle et le Clochard", "../img/Covers/movies/la-belle-et-le-clochard-1955.jpg", 76, 1955, "Animation, Aventure, Comédie", "Les aventures amoureuses de Lady, ravissant cocker et de Clochard le batard, perturbées par tante Sarah et ses deux adorables chats siamois, Si et Am, diaboliques et sournois.", "Barbara Luddy, Larry Roberts, Peggy Lee", "Clyde Geronimi, Wilfred Jackson, Hamilton Luske");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Roi Lion", "../img/Covers/movies/le-roi-lion-1994.jpg", 88, 1994, "Animation, Aventure, Drame", "Le long combat de Simba le lionceau pour accéder à son rang de roi des animaux, après que le fourbe Scar, son oncle, a tué son père et pris sa place.", "Matthew Broderick, Jeremy Irons, James Earl Jones", "Roger Allers, Rob Minkoff");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Bambi", "../img/Covers/movies/bambi-1942.jpg", 69, 1942, "Animation, Aventure, Drame", "La terre s’éveille sous les rayons du soleil printanier. Une grande effervescence s’empare des hôtes de la forêt : un Petit Prince vient de naître. Tous les animaux des alentours viennent féliciter l’heureuse mère et admirer le jeune faon qui s’éveille à la vie. Il s’appellera Bambi. Curieux de tout, il va explorer son futur royaume, y rencontrer des compagnons attendrissant (le facétieux Panpan, l’adorable Fleur et la malicieuse Féline) et apprendre à appréhender le danger…", "Hardie Albright, Stan Alexander, Bobette Audrey", "James Algar, Samuel Armstrong, David Hand");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Taxi 2", "../img/Covers/movies/taxi-2-2000.jpg", 88, 2000, "Action, Comédie, Crime", "Daniel, le chauffeur de taxi dingue de vitesse, et Emilien, l'inspecteur de police inexpérimenté, se retrouvent à Paris. Le ministre de la défense japonais vient tester le savoir-faire hexagonal en matière de lutte antiterroriste...", "Samy Naceri, Frédéric Diefenthal, Emma Wiklund", "Gérard Krawczyk");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("E.T. l'extra-terrestre", "../img/Covers/movies/et-lextra-terrestre-1982.jpg", 115, 1982, "Aventure, Famille, Science-Fiction", "E.T., un extraterrestre, débarque en pleine nuit sur Terre et s'aventure vers Los Angeles. Traqué par des militaires, il se réfugie dans un pavillon de banlieue. Elliot, un jeune garçon, le découvre et va tenter de garder sa présence secrète.", "Henry Thomas, Drew Barrymore, Peter Coyote", "Steven Spielberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Harry Potter à l'école des sorciers", "../img/Covers/movies/harry-potter-1-2001.jpg", 152, 2001, "Aventure, Famille, Fantastique", "Le jour de son onzième anniversaire, Harry reçoit la visite inattendue d'un homme gigantesque se nommant Rubeus Hagrid. Celui-ci lui révèle qu'il est en fait le fils de deux puissants magiciens...", "Daniel Radcliffe, Rupert Grint, Richard Harris", "Chris Columbus");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Dîner de Cons", "../img/Covers/movies/le-diner-de-cons-1998.jpg", 80, 1998, "Comédie", "Tous les mercredis, Pierre Brochant et ses amis organisent un dîner où chacun doit amener un con. Celui qui a trouvé le plus spectaculaire est declaré vainqueur. Ce soir, Brochant exulte, il est sur d'avoir trouvé la perle rare, un con de classe mondiale: Francois Pignon, comptable au ministère des Finances et passionné de modèles réduits ...", "Thierry Lhermitte, Jacques Villeret, Francis Huster", "Francis Veber");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Grand Bleu", "../img/Covers/movies/le-grand-bleu-1988.jpg", 168, 1988, "Aventure, Drame, Sport", "La rivalité de deux enfants, dans la mer, en Grèce, qui se poursuit lorsqu'ils sont adultes. Lequel des deux plongera le plus loin et le plus profond ?", "Jean-Marc Barr, Jean Reno, Rosanna Arquette", "Luc Besson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Astérix et Obélix contre César", "../img/Covers/movies/asterix-et-obelix-contre-cesar-1999.jpg", 109, 1999, "Aventure, Comédie, Famille", "Résistant encore et toujours à l'envahisseur, les habitants d'un petit village d'Armorique défient César en s'emparant de l'impôt récolté au profit de Rome.", "Christian Clavier, Gérard Depardieu, Roberto Benigni", "Claude Zidi, Olaf Wijnants");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Fabuleux destin d'Amélie Poulain", "../img/Covers/movies/le-fabuleux-destin-damelie-poulain-2001.jpg", 122, 2001, "Comédie, Romance", "Amélie, une jeune serveuse dans un bar de Montmartre, passe son temps à observer les gens et à laisser son imagination divaguer. Elle s'est fixé un but : faire le bien de ceux qui l'entourent. Elle invente alors des stratagèmes pour intervenir incognito dans leur existence.", "Audrey Tautou, Mathieu Kassovitz, Rufus", "Jean-Pierre Jeunet");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Visiteurs 2 : Les couloirs du temps", "../img/Covers/movies/les-visiteurs-2-1998.jpg", 118, 1998, "Comédie, Fantastique", "Godefroy de Montmirail est reparti au Moyen Age pour épouser la douce Frénégonde. Mais une malencontreuse erreur s’est produite : au lieu de son valet Jacquouille, c’est son descendant, Jacquart, qui a suivi Godefroy. Jacquouille, lui, est resté au XXème siècle. Godefroy, qui le soupçonne d’avoir volé des bijoux à son futur beau-père, décide alors de repartir le chercher...", "Christian Clavier, Jean Reno, Muriel Robin", "Jean-Marie Poiré");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Star Wars : Episode I - La Menace fantôme", "../img/Covers/movies/star-wars-1-1999.jpg", 136, 1999, "Action, Aventure, Fantastique", "Il y a bien longtemps, dans une galaxie très lointaine... La Fédération du Commerce impose par la force la taxation des routes commerciales à la pacifique planète Naboo. Les chevaliers Jedi Qui-Gon Jinn et Obi-Wan Kenobi sont envoyés sur place...", "Ewan McGregor, Liam Neeson, Natalie Portman", "George Lucas");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Gendarme de Saint-Tropez", "../img/Covers/movies/le-gendarme-de-saint-tropez-1964.jpg", 90, 1964, "Comédie, Crime", "Suite à une promotion, le gendarme Cruchot quitte son petit village provincial pour prendre se nouvelles fonctions dans la commune de Saint-Tropez. Sa fille unique, la charmante Nicole est folle de joie et ne tarde pas à se faire une foule de nouveaux amis « yé-yé » tout en s’attirant de sacrés ennuis, tandis que Cruchot prend activement la direction d’opérations difficiles et délicates…", "Louis de Funès, Geneviève Grad, Michel Galabru", "Jean Girault");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Sixième Sens", "../img/Covers/movies/le-sixieme-sens-1999.jpg", 107, 1999, "Drame, Mystère, Thriller", "Connu pour son engagement et ses résultats remarquables, le docteur Malcolm Crowe, un psychologue pour enfants, est amené à s'occuper du jeune Cole Sear, un garçon de 9 ans, doué d'un 'sixième sens' qui lui révèle certains événements du passé et le confronte à des visions macabres et terrifiantes...", "Bruce Willis, Haley Joel Osment, Toni Collette", "M. Night Shyamalan");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("La Vérité si je mens ! 2", "../img/Covers/movies/la-verite-si-je-mens-2-2001.jpg", 105, 2001, "Comédie", "Dans le quartier du Sentier, Eddie Vuibert, Dov et Yvan sont confrontés aux procédés pour le moins expéditifs de leur nouveau client, Eurodiscount, une chaîne européenne d'hypermarchés. Karine, lasse des turpitudes de son volage époux, Dov, le chasse du foyer. La bande de copains se disloque...", "Richard Anconina, José Garcia, Bruno Solo", "Thomas Gilou");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Seigneur des anneaux : la communauté de l'anneau", "../img/Covers/movies/le-seigneur-des-anneaux-1-2001.jpg", 178, 2001, "Action, Aventure, Drame", "Frodon le Hobbit hérite de l'Anneau Unique, un instrument de pouvoir absolu qui permettrait à Sauron, le Seigneur des ténèbres, de régner sur la Terre du Milieu. Commence alors un vaste périple visant à la destruction de l'objet.", "Elijah Wood, Ian McKellen, Orlando Bloom", "Peter Jackson");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Le Bossu de Notre-Dame", "../img/Covers/movies/le-bossu-de-notre-dame-1996.jpg", 91, 1996, "Animation, Drame, Famille", "En l'an de grâce 1482 à Paris, Quasimodo, jeune orphelin contrefait, est le sonneur de Notre-Dame. Il vit reclus dans le clocher sous la férule du puissant juge Frollo, son maître...", "Demi Moore, Jason Alexander, Mary Kay Bergman", "Gary Trousdale, Kirk Wise");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Jurassic Park", "../img/Covers/movies/jurassic-park-1993.jpg", 127, 1993, "Action, Aventure, Science-Fiction", "C'est à partir d'une goutte de sang absorbée par un moustique fossilisé que John Hammond et son équipe ont réussi à faire renaître une dizaine d'espèces de dinosaures.", "Sam Neill, Laura Dern, Jeff Goldblum", "Steven Spielberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Taxi", "../img/Covers/movies/taxi-1998.jpg", 89, 1998, "Action, Comédie", "Daniel est un fou du volant. Cet ex-livreur de pizzas est aujourd'hui chauffeur de taxi et sait échapper aux radars les plus perfectionnés. Pourtant, un jour, il croise la route d'Emilien, policier recalé pour la huitième fois à son permis de conduire. Pour conserver son taxi, il accepte le marché que lui propose Emilien : l'aider à démanteler un gang de braqueurs de banques qui écume les succursales de la ville à bord de puissants véhicules. Road-movie urbain sur un scénario de Luc Besson, également producteur.", "Samy Naceri, Frédéric Diefenthal, Marion Cotillard", "Gérard Pirès");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Spider-Man", "../img/Covers/movies/spider-man-2002.jpg", 121, 2002, "Action, Aventure, Science-Fiction", "Après avoir été mordu par une araignée génétiquement modifiée, Peter Parker, un photographe, se découvre des pouvoirs surnaturels. Devenu Spider-Man, il engage une lutte sans merci contre le richissime industriel Norman Osborn, alias le Bouffon Vert.", "Tobey Maguire, Kirsten Dunst, Willem Dafoe", "Sam Raimi");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Les Dents de la Mer", "../img/Covers/movies/les-dents-de-la-mer-1975.jpg", 124, 1975, "Aventure, Mystère, Thriller", "A quelques jours du début de la saison estivale, les habitants de la petite station balnéaire d'Amity sont mis en émoi par la découverte sur le littoral du corps atrocement mutilé d'une jeune vacancière.", "Roy Scheider, Robert Shaw, Richard Dreyfuss", "Steven Spielberg");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Terminator 2 : le Jugement Dernier", "../img/Covers/movies/terminator-2-1991.jpg", 137, 1991, "Action, Science-Fiction", "Dans cette suite, un Terminator reprogrammé est envoyé du futur afin de protéger le jeune John Connor d'un implacable cyborg protéiforme.", "Arnold Schwarzenegger, Linda Hamilton, Edward Furlong", "James Cameron");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Mulan", "../img/Covers/movies/mulan-1998.jpg", 87, 1998, "Animation, Aventure, Comédie", "Mulan est une belle jeune fille qui vit dans un village chinois. Malgré son amour et son respect pour sa famille, son mépris des conventions l'éloigne des rôles dévolus aux filles dévouées. Quand son pays est envahi par les Huns, Mulan, n'écoutant que son courage, s'engage à la place de son père dans le but de lui sauver la vie. ", "Ming-Na Wen, Eddie Murphy, BD Wong", "Tony Bancroft, Barry Cook");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Men in Black", "../img/Covers/movies/men-in-black-1997.jpg", 98, 1997, "Action, Aventure, Comédie", "Chargés de protéger la Terre de toute infraction extraterrestre et de réguler l'immigration intergalactique sur notre planète, les Men in black ou MIB opèrent dans le plus grand secret. Vêtus de costumes sombres et équipés des toutes dernières technologies, ils passent inaperçus aux yeux des humains dont ils effacent régulièrement la mémoire récente : la présence d'aliens sur notre sol doit rester secrète.", "Tommy Lee Jones, Will Smith, Linda Fiorentino", "Barry Sonnenfeld");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Grease", "../img/Covers/movies/grease-1978.jpg", 110, 1978, "Comédie, Musical, Romance", "A la fin des vacances d'été, les amoureux Danny Zuko et Sandy Olsson, une jeune Australienne de bonne famille, doivent se séparer. A son retour au lycée Rydell, le jeune homme retrouve sa bande, les T-birds, blousons de cuir et cheveux gominés. Les parents de Sandy ayant décidé de s'installer aux Etats-Unis, la demoiselle intègre la même école...", "John Travolta, Olivia Newton-John, Stockard Channing", "Randal Kleiser");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Star Wars : Episode II - L'Attaque des clones", "../img/Covers/movies/star-wars-2-2002.jpg", 142, 2002, "Action, Aventure, Fantastique", "La sénatrice Padmé Amidala échappe de justesse à un attentat. Le Padawan Anakin Skywalker est chargé de sa protection. Son maître, Obi-Wan Kenobi, enquête sur cette tentative de meurtre et découvre la constitution d'une mystérieuse armée de clones.", "Hayden Christensen, Natalie Portman, Ewan McGregor", "George Lucas");
INSERT INTO Films (nom, image, duree, date_sortie, categorie, synopsis, actors, realisator) VALUES ("Independence Day", "../img/Covers/movies/independence-day-1996.jpg", 145, 1996, "Action, Aventure, Science-Fiction", "Nous avons toujours su qu'ils reviendraient. La terre est menacée par une catastrophe d’une ampleur inimaginable. Pour la protéger, toutes les nations ont collaboré autour d’un programme de défense colossal exploitant la technologie extraterrestre récupérée. ", "Will Smith, Bill Pullman, Jeff Goldblum", "Roland Emmerich");
