INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Breaking Bad", 5, 2008, "Drame, Crime", "Walter White, professeur de chimie dans un lycée d'Albuquerque au Nouveau-Mexique, est atteint d'un cancer pulmonaire en phase terminale. Il s'associe à Jesse Pinkman, un ancien élève, cancre, toxicomane et dealer, afin d'assurer l'avenir financier de sa famille après son décès. L'improbable duo va alors synthétiser et commercialiser la plus pure méthamphétamine en cristaux jamais vue dans les Amériques.", "Bryan Cranston, Aaron Paul, Anna Gunn", "Vince Gilligan", "../img/Covers/series/breaking_bad_2008.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Game of Thrones", 8, 2011, "Science-Fiction & Fantastique, Drame, Action & Adventure", "Il y a très longtemps, à une époque oubliée, une force a détruit l'équilibre des saisons. Dans un pays où l'été peut durer plusieurs années et l'hiver toute une vie, des forces sinistres et surnaturelles se pressent aux portes du Royaume des Sept Couronnes. La confrérie de la Garde de Nuit, protégeant le Royaume de toute créature pouvant provenir d'au-delà du Mur protecteur, n'a plus les ressources nécessaires pour assurer la sécurité de tous. Après un été de dix années, un hiver rigoureux s'abat sur le Royaume avec la promesse d'un avenir des plus sombres. Pendant ce temps, complots et rivalités se jouent sur le continent pour s'emparer du Trône de fer, le symbole du pouvoir absolu.", "Emilia Clarke, Kit Harington, Peter Dinklage", "David Benioff, D.B. Weiss", "../img/Covers/series/game_of_thrones_2011.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("The Walking Dead", 11, 2010, "Action & Adventure, Drame, Science-Fiction & Fantastique", "Après une apocalypse, ayant transformé la quasi-totalité de la population en zombies, un groupe d'hommes et de femmes, mené par le shérif adjoint Rick Grimes, tente de survivre… Ensemble, ils vont devoir, tant bien que mal, faire face à ce nouveau monde, devenu méconnaissable, à travers leur périple dans le Sud profond des États-Unis.", "Andrew Lincoln, Norman Reedus, Melissa McBride", "Frank Darabont, Angela Kang", "../img/Covers/series/the_walking_dead_2010.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Stranger Things", 4, 2016, "Drame, Science-Fiction & Fantastique, Mystère", "Quand un jeune garçon disparaît, une petite ville découvre une affaire mystérieuse, des expériences secrètes, des forces surnaturelles terrifiantes... et une fillette.", "Winona Ryder, David Harbour, Finn Wolfhard", "The Duffer Brothers", "../img/Covers/series/stranger_things_2016.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("The Witcher", 3, 2019, "Science-Fiction & Fantastique, Drame, Action & Adventure", "Le sorcier Geralt, un chasseur de monstres mutant, se bat pour trouver sa place dans un monde où les humains se révèlent souvent plus vicieux que les bêtes.", "Henry Cavill, Freya Allan, Anya Chalotra", "Lauren Schmidt Hissrich", "../img/Covers/series/the_witcher_2019.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("The Mandalorian", 3, 2019, "Science-Fiction & Fantastique, Action & Adventure, Drame", "Après les aventures de Jango et Boba Fett, un nouveau héros émerge dans l'univers Star Wars. L'intrigue, située entre la chute de l'Empire et l'émergence du Premier Ordre, suit les voyages d'un chasseur de primes solitaire dans les contrées les plus éloignées de la Galaxie, loin de l’autorité de la Nouvelle République.", "Pedro Pascal, Gina Carano, Giancarlo Esposito", "Jon Favreau", "../img/Covers/series/the_mandalorian_2019.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("The Crown", 6, 2016, "Drame", "Cette série dramatique retrace les rivalités politiques, intrigues et événements qui ont marqué le règne de la reine Élisabeth II et la seconde moitié du XXe siècle.", "Olivia Colman, Tobias Menzies, Helena Bonham Carter", "Peter Morgan", "../img/Covers/series/the_crown_2016.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Umbrella Academy", 3, 2019, "Action & Adventure, Science-Fiction & Fantastique, Drame", "À la mort de leur père, des frères et sœurs aux pouvoirs extraordinaires découvrent des secrets de famille traumatisants et une menace terrible qui plane sur l'humanité.", "Elliot Page, Tom Hopper, David Castañeda", "Steve Blackman", "../img/Covers/series/umbrella_academy_2019.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Narcos", 3, 2015, "Crime, Drame", "Cette série de gangsters inspirée de l'histoire du célèbre narcotrafiquant de Medellín raconte la guerre sanglante des cartels en Colombie.", "Pedro Pascal, Wagner Moura, Boyd Holbrook", "Chris Brancato, Carlo Bernard, Doug Miro", "../img/Covers/series/narcos_2015.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("The Haunting of Hill House", 1, 2018, "Mystère, Drame", "Entre passé et présent, les membres d'une famille éclatée font face aux souvenirs d'événements terrifiants survenus dans une maison qui les a autrefois obligés à fuir.", "Michiel Huisman, Carla Gugino, Henry Thomas", "Mike Flanagan", "../img/Covers/series/the_haunting_of_hill_house_2018.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Les 100", 7, 2014, "Science-Fiction & Fantastique, Drame, Action & Adventure", "Après une apocalypse causée par l'Homme lors d'une Troisième Guerre mondiale, les survivants se réfugient dans des stations spatiales et parviennent à y vivre et à se reproduire, atteignant le nombre de 4000. Mais 97 ans plus tard, le vaisseau mère, The Ark, est en piteux état.  Une centaine de jeunes délinquants, emprisonnés au fil des années pour des crimes ou des trahisons, sont choisis comme cobayes par les autorités pour redescendre sur Terre et tester les chances de survie. Dès leur arrivée, ils découvrent un nouveau monde dangereux mais fascinant...", "Eliza Taylor, Bob Morley, Marie Avgeropoulos", "Jason Rothenberg", "../img/Covers/series/les_100_2014.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("The Good Place", 4, 2016, "Science-Fiction & Fantastique, Comédie", "À sa mort, l'égocentrique Eleanor Shellstrop se retrouve par erreur dans un monde paradisiaque. Déterminée à y rester, elle va tenter de devenir une meilleure personne.", "Kristen Bell, William Jackson Harper, Jameela Jamil", "Michael Schur", "../img/Covers/series/the_good_place_2016.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("The Office", 9, 2005, "Comédie", "Le quotidien d'un groupe d'employés de bureau dans une fabrique de papier en Pennsylvanie. Michael Scott, le responsable régional, pense être le mec le plus drôle du bureau. Il ne se doute pas que ses employés le tolèrent uniquement parce que c'est lui qui signe les chèques. S'efforçant de paraître cool et apprécié de tous, Michael est en fait perçu comme étant pathétique...", "Steve Carell, Rainn Wilson, John Krasinski", "Greg Daniels", "../img/Covers/series/the_office_2005.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Friends", 10, 1994, "Comédie", "Les péripéties de 6 jeunes newyorkais liés par une profonde amitié. Entre amour, travail, famille, ils partagent leurs bonheurs et leurs soucis au Central Perk, leur café favori...", "Jennifer Aniston, Courteney Cox, Lisa Kudrow", "David Crane, Marta Kauffman", "../img/Covers/series/friends_1994.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Better Call Saul", 6, 2015, "Crime, Drame", "Six ans avant de croiser le chemin de Walter White, Saul Goodman, connu sous le nom de Jimmy McGill, est un avocat qui peine à joindre les deux bouts, à Albuquerque, au Nouveau-Mexique. Pour boucler ses fins de mois, il n'aura d'autres choix que se livrer à quelques petites escroqueries. Chemin faisant, il va faire des rencontres qui vont se révéler déterminantes dans son parcours : Nacho Varga, ou encore Mike Ehrmantraut, un criminel spécialisé dans le 'nettoyage', qui deviendra son futur homme de main.", "Bob Odenkirk, Rhea Seehorn, Jonathan Banks", "Vince Gilligan, Peter Gould", "../img/Covers/series/better_call_saul_2015.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Vikings", 6, 2013, "Action & Adventure, Drame, War & Politics, Documentaire", "Les exploits d'un groupe de vikings de la fin du 8ème siècle jusqu'au milieu du 11ème, mené par Ragnar Lodbrok, l'un des plus populaires héros viking de tous les temps, qui a régné quelques temps sur le Danemark et la Suède...", "Travis Fimmel, Katheryn Winnick, Clive Standen", "Michael Hirst", "../img/Covers/series/vikings_2013.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Tchernobyl", 1, 2019, "Drame", "L'histoire vraie de la pire catastrophe causée par l'homme et de ceux qui ont sacrifié leur vie pour sauver l'Europe du drame.", "Jared Harris, Stellan Skarsgård, Jessie Buckley", "Craig Mazin", "../img/Covers/series/tchernobyl_2019.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Teen Wolf", 6, 2011, "Science-Fiction & Fantastique, Drame, Comédie", "Scott McCall est un adolescent tout ce qu'il y a de plus ordinaire. Il n'est pas le plus populaire de son lycée, il n'en est pas la risée non plus... Un soir, son meilleur ami Stiles le convainc de suivre en cachette les recherches de la police pour retrouver un corps dans les bois alentours. Au cours de cette expédition, il se retrouve nez à nez avec une bête étrange et particulièrement féroce. Il réchappe de peu à cette attaque et, chanceux, avec une seule blessure: une vilaine morsure. Le lendemain, Scott se sent bizarre. Sans qu'il le sache encore, sa vie vient de changer. Il est devenu un loup-garou...", "Tyler Posey, Dylan O'Brien, Holland Roden", "Jeff Davis", "../img/Covers/series/teen_wolf_2011.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("House of Cards", 6, 2013, "Drame", "Le membre du Congrès américain Frank Underwood ne recule devant rien pour tout conquérir : pouvoir, sexe, corruption, manipulation et avidité.", "Kevin Spacey, Robin Wright, Michael Kelly", "Beau Willimon", "../img/Covers/series/house_of_cards_2013.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Love, Death & Robots", 3, 2019, "Animation, Science-Fiction & Fantastique", "Créatures terrifiantes, méchantes surprises et comédie noire convergent dans cette anthologie d’animation pour adulte présentée par Tim Miller et David Fincher. Une collection de courts métrages animés en tous genres: science fiction, fantasy, horreur et comédie.", "Scott Whyte, Nolan North, Matthew Yang King", "Tim Miller, David Fincher", "../img/Covers/series/love,_death_&_robots_2019.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Black Mirror", 6, 2011, "Science-Fiction & Fantastique, Drame, Mystère", "Chaque épisode de cette anthologie montre la dépendance des hommes vis-à-vis de tout ce qui a un écran...", "Daniel Lapaine, Hannah John-Kamen, Michaela Coel", "Charlie Brooker", "../img/Covers/series/black_mirror_2011.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Euphoria", 2, 2019, "Drame", "À 17 ans, Rue Bennett, fraîchement sortie de désintox, cherche à donner un sens à son existence. Elle se lie très vite à Jules Vaughn, une fille trans récemment arrivée en ville après le divorce de ses parents. Dans leur sillage gravitent Nate Jacobs, un sportif dont les problèmes de colère masquent des complexes sexuels ; Maddy Perez, la petite amie de Nate ; Chris McKay, star de l'équipe de football qui peine à suivre les cours ; Cassie Howard, dont le passif sexuel continue de la poursuivre ; Lexi Howard, jeune sœur de Cassie et amie d'enfance de Rue ; et Kat Hernandez, en pleine exploration de sa sexualité.  Adaptation US de la série éponyme israëlienne Euphoria (2012), elle-même tirée d'une histoire vraie.", "Zendaya, Hunter Schafer, Jacob Elordi", "Sam Levinson", "../img/Covers/series/euphoria_2019.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Heartstopper", 2, 2022, "Drame", "Partageant une passion commune pour le rugby, les deux adolescents Charlie et Nick deviennent vite amis. Le premier tombe bientôt amoureux de son camarade, même s’il pense qu’il n’a aucune chance.  Adaptation du comic éponyme d'Alice Oseman.", "Kit Connor, Joe Locke, Yasmin Finney", "Alice Oseman", "../img/Covers/series/heartstopper_2022.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("The End of the F***ing World", 2, 2017, "Comédie, Drame, Crime", "Un ado psychopathe en herbe et une rebelle en quête d'aventure embarquent pour un road trip d'enfer dans cette série à l'humour noir inspirée d'un roman graphique.", "Jessica Barden, Alex Lawther, Steve Oram", "Charlie Covell", "../img/Covers/series/the_end_of_the_fing_world_2017.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("The Society", 1, 2019, "Drame, Science-Fiction & Fantastique", "Quand tous les autres habitants de leur riche bourgade disparaissent mystérieusement, les jeunes de West Ham doivent créer leur propre société pour survivre.", "Kathryn Newton, Gideon Adlon, Sean Berdy", "Christopher Keyser", "../img/Covers/series/the_society_2019.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("The Last of Us", 1, 2023, "Drame", "Vingt ans après la destruction de la civilisation moderne, Joel, un survivant endurci, est engagé pour faire sortir Ellie, une jeune fille de 14 ans, d'une zone de quarantaine oppressante. Ce qui n'est au départ qu'un petit travail se transforme rapidement en un voyage brutal et époustouflant, alors que ce duo improbable dépend de l'autre pour sa survie.", "Pedro Pascal, Bella Ramsey, Nico Parker", "Craig Mazin", "../img/Covers/series/the_last_of_us_2023.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Sex Education", 4, 2019, "Comédie, Drame", "Le timide Otis connaît pourtant tout sur le sexe... grâce à sa maman thérapeute. La rebelle Maeve le décide donc à ouvrir un cabinet de conseil clandestin au lycée.", "Asa Butterfield, Gillian Anderson, Ncuti Gatwa", "Laurie Nunn", "../img/Covers/series/sex_education_2019.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Sense8", 2, 2015, "Science-Fiction & Fantastique, Drame", "Huit inconnus aux quatre coins du globe se découvrent soudainement connectés les uns aux autres de façon télépathique et deviennent les proies d'un mystérieux ennemi.", "Tuppence Middleton, Brian J. Smith, Doona Bae", "Lana Wachowski, Lilly Wachowski", "../img/Covers/series/sense8_2015.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Marvel's The Punisher", 2, 2017, "Action & Adventure, Crime, Drame", "Un ancien Marine déterminé à punir les criminels qui ont assassiné sa femme et ses enfants se retrouve plongé au cœur d'un complot militaire.", "Jon Bernthal, Ben Barnes, Amber Rose Revah", "Steve Lightfoot", "../img/Covers/series/marvels_the_punisher_2017.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("One Punch Man", 3, 2015, "Action & Adventure, Animation, Comédie", "Le super-héros le plus puissant au monde est capable de tuer n'importe qui sans se fatiguer. Et c'est justement ça son problème : l'absence de défi et donc la morosité.", "Makoto Furukawa, Kaito Ishikawa, Max Mittelman", "Yusuke Murata", "../img/Covers/series/one_punch_man_2015.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Les Simpson", 35, 1989, "Familial, Animation, Comédie", "Située à Springfield, ville américaine moyenne, la série se concentre sur les singeries et les aventures quotidiennes de la famille Simpson : Homer, Marge, Bart, Lisa et Maggie, ainsi que des milliers d'autres personnages.", "Dan Castellaneta, Julie Kavner, Nancy Cartwright", "Matt Groening", "../img/Covers/series/les_simpson_1989.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Grey's Anatomy", 19, 2005, "Drame", "Meredith Grey, fille d'un chirurgien très réputé, commence son internat de première année en médecine chirurgicale dans un hôpital de Seattle. La jeune femme s'efforce de maintenir de bonnes relations avec ses camarades internes, mais dans ce métier difficile la compétition fait rage.", "Ellen Pompeo, Chandra Wilson, James Pickens Jr.", "Shonda Rhimes", "../img/Covers/series/greys_anatomy_2005.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Gomorra", 5, 2014, "Crime, Drame", "Patriarche de la famille Savastano, Don Pietro domine la Camorra, la mafia napolitaine, mais doit faire face à ses nombreux rivaux, tout en préparant sa succession.", "Salvatore Esposito, Marco D'Amore, Cristiana Dell'Anna", "Roberto Saviano, Stefano Bises, Leonardo Fasoli", "../img/Covers/series/gomorra_2014.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Mr. Robot", 4, 2015, "Crime, Drame", "Elliot est un jeune programmeur anti-social qui souffre d'un trouble du comportement qui le pousse à croire qu'il ne peut rencontrer des gens qu'en les hackant. Il travaille pour une firme spécialisée dans la cyber-sécurité mais un homme connu sous le nom de Mr Robot l'approche un jour pour faire tomber une compagnie surpuissante qui fait partie de celles qu'il doit justement protéger…", "Rami Malek, Christian Slater, Carly Chaikin", "Sam Esmail", "../img/Covers/series/mr._robot_2015.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Dark", 3, 2017, "Crime, Drame, Science-Fiction & Fantastique, Mystère", "Un enfant disparu lance quatre familles dans une quête éperdue pour trouver des réponses. La chasse au coupable fait émerger les péchés et les secrets d'une petite ville.", "Louis Hofmann, Karoline Eichhorn, Lisa Vicari", "Baran bo Odar, Jantje Friese", "../img/Covers/series/dark_2017.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Rick et Morty", 7, 2013, "Animation, Comédie, Science-Fiction & Fantastique, Action & Adventure", "Un brillant inventeur et son petit fils un peu à l'Ouest partent à l'aventure...", "Justin Roiland, Chris Parnell, Spencer Grammer", "Dan Harmon, Justin Roiland", "../img/Covers/series/rick_et_morty_2013.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("You", 5, 2018, "Drame", "Joe, le gérant d'une librairie new-yorkaise, devient obsédé par Beck, une jeune aspirante écrivaine qui partage sa passion pour les livres et pour la poésie. Persuadé qu'ils sont faits l'un pour l'autre, il va alors se servir des réseaux sociaux pour nourrir son obsession, savoir en permanence où elle se trouve et ce qu'elle fait, et tenter de faire tomber tous les obstacles qui pourraient se dresser en travers du chemin de leur possible romance. Quitte à commettre des actes totalement fous...", "Penn Badgley, Victoria Pedretti, Ambyr Childers", "Greg Berlanti, Sera Gamble", "../img/Covers/series/you_2018.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("How to Sell Drugs Online (Fast)", 3, 2019, "Comédie, Crime, Drame", "Pour reconquérir son ex, un ado fou d'informatique se met à vendre de l'ecstasy en ligne, et devient l'un des plus gros dealers d'Europe... sans quitter sa chambre.", "Maximilian Mundt, Danilo Kamber, Lena Klenke", "Philipp Kässbohrer, Matthias Murmann", "../img/Covers/series/how_to_sell_drugs_online_fast_2019.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("13 Reasons Why", 4, 2017, "Drame, Mystère", "Inspirée des best-sellers de Jay Asher, 13 Reasons Why suit Clay Jensen, un adolescent qui découvre sous son porche au retour du lycée une mystérieuse boîte portant son nom. À l'intérieur, des cassettes enregistrées par Hannah Baker, une camarade de classe qui s'est tragiquement suicidée deux semaines auparavant. Les enregistrements révèlent que la jeune fille, dont il était amoureux, a décidé de mettre fin à ses jours pour treize raisons. Clay est-il l'une de ces raisons ?", "Dylan Minnette, Grace Saif, Christian Navarro", "Brian Yorkey", "../img/Covers/series/13_reasons_why_2017.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("The Rain", 3, 2018, "Science-Fiction & Fantastique, Drame", "Six ans après qu'un virus a décimé la majeure partie de la Scandinavie, un frère et une sœur intègrent un groupe de jeunes survivants en quête de sécurité et de réponses.", "Alba August, Lucas Lynggaard Tønnesen, Mikkel Boe Følsgaard", "Jannik Tai Mosholt, Christian Potalivo, Esben Toft Jacobsen", "../img/Covers/series/the_rain_2018.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Mercredi", 2, 2022, "Science-Fiction & Fantastique, Mystère, Comédie", "Infusée de mystère et de surnaturel, la série suit les aventures de Mercredi Addams, désormais étudiante à la Nevermore Academy. Alors qu'elle apprend à dompter ses nouveaux pouvoirs psychiques, la jeune fille doit aussi déjouer une série de meurtres monstrueux qui terrorisent la ville, et résoudre le mystère auquel ont été mêlés ses parents il y a 25 ans, le tout en tentant péniblement de nouer de nouvelles relations au sein de Nevermore.", "Jenna Ortega, Catherine Zeta-Jones, Luis Guzmán", "Tim Burton, Al Gough, Miles Millar", "../img/Covers/series/mercredi_2022.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Squid Game", 2, 2021, "Action & Adventure, Mystère, Drame", "Tentés par un prix alléchant en cas de victoire, des centaines de joueurs désargentés acceptent de s'affronter lors de jeux pour enfants aux enjeux mortels.", "Lee Jung-jae, Park Hae-soo, Wi Ha-jun", "Hwang Dong-hyuk", "../img/Covers/series/squid_game_2021.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Elite", 7, 2018, "Crime, Mystère, Drame", "Las Encinas est l’école la plus compétitive et huppée d’Espagne, accueillant les enfants de l’élite espagnole. C’est également dans cette école que sont admis trois adolescents boursiers, issus de la classe ouvrière, lorsque leur lycée s'effondre et que les élèves sont répartis dans plusieurs établissements scolaires. Ils s'estiment chanceux… mais peut-être ne le sont-ils pas tant que cela ? La confrontation entre ceux qui ont tout et ceux qui n’ont rien est explosive, et les tensions de classes sociales aboutiront à un meurtre. Qui se cache réellement derrière ce crime ? Est-ce l’un des nouveaux arrivants ? Ou se cache-t-il quelque chose de bien plus sombre ?", "Itzan Escamilla, Miguel Bernardeau, Álvaro Rico", "Carlos Montero, Darío Madrona", "../img/Covers/series/elite_2018.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("La Flamme", 1, 2020, "Comédie, Reality", "La vie lui a tout donné... sauf une co-pilote. Pendant neuf semaines dans une sublime villa, treize femmes vont s’affronter pour séduire Marc, pilote de ligne, et tenter d’allumer en lui... La Flamme ! Alors, armez les toboggans, vérifiez la porte opposée... Marc va emmener ses prétendantes au septième ciel !", "Jonathan Cohen, Ana Girardot, Camille Chamoux", "Jonathan Cohen, Jérémy Foenkinos, Stéphane Foenkinos", "../img/Covers/series/la_flamme_2020.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Le Flambeau, les aventuriers de Chupacabra", 1, 2022, "Comédie", "Au revoir la villa et bonjour Chupacabra. Après LA FLAMME, Marc et 15 autres candidats vont devoir s’affronter et survivre sur une île déserte. Épreuves physiques, manipulations mentales et trahisons émotionnelles seront les ingrédients de ce cocktail tropical.", "Jonathan Cohen, Ana Girardot, Camille Chamoux", "Jonathan Cohen, Jérémy Foenkinos, Stéphane Foenkinos", "../img/Covers/series/le_flambeau,_les_aventuriers_de_chupacabra_2022.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Lucifer", 6, 2016, "Crime, Science-Fiction & Fantastique", "Lassé d’être le Seigneur des Enfers, le diable s’installe à Los Angeles où il ouvre une boîte de nuit et se lie avec une policière de la brigade criminelle...", "Tom Ellis, Lauren German, Kevin Alejandro", "Tom Kapinos", "../img/Covers/series/lucifer_2016.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Lupin", 2, 2021, "Crime, Drame, Mystère", "Inspiré par les aventures d'Arsène Lupin, le gentleman cambrioleur Assane Diop décide de venger son père d'une terrible injustice.", "Omar Sy, Vincent Londez, Ludivine Sagnier", "George Kay, François Uzan", "../img/Covers/series/lupin_2021.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Emily in Paris", 4, 2020, "Drame, Comédie", "Quand elle décroche le boulot de ses rêves à Paris, Emily, jeune cadre ambitieuse de Chicago, adopte une nouvelle vie tout en jonglant entre marketing, amis et amours.", "Lily Collins, Philippine Leroy-Beaulieu, Ashley Park", "Darren Star", "../img/Covers/series/emily_in_paris_2020.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Manifest", 4, 2018, "Drame, Mystère, Science-Fiction & Fantastique", "Le vol Montego 828 à destination de New York disparaît des radars sans laisser de traces, avant de réapparaître cinq ans plus tard sans aucune explication. Pour les 191 passagers à bord, le temps ne s'est pas écoulé. Mais pour leurs proches, ces cinq années ont été éprouvantes... Que s'est-il réellement passé ? Comment les revenants vont-ils parvenir à s'adapter ? Sont-ils revenus changés ?", "Melissa Roxburgh, Josh Dallas, Athena Karkanis", "Jeff Rake", "../img/Covers/series/manifest_2018.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Snowpiercer", 3, 2020, "Science-Fiction & Fantastique", "Sept ans après que le monde est devenu une vaste étendue glacée, les survivants ont trouvé refuge dans un train en perpétuel mouvement. Composé de 1001 wagons, l'engin fait le tour du globe à toute vitesse. À bord, la guerre des classes, l’injustice sociale et la politique interne sèment le trouble.", "Jennifer Connelly, Daveed Diggs, Mickey Sumner", "Graeme Manson", "../img/Covers/series/snowpiercer_2020.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Family Business", 3, 2019, "Comédie", "Quand ils apprennent de source 'sûre' que le cannabis va être légalisé, un entrepreneur et sa famille décident de transformer leur boucherie sur le déclin en coffee-shop.", "Jonathan Cohen, Gérard Darmon, Julia Piaton", "Igor Gotesman", "../img/Covers/series/family_business_2019.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Orange Is the New Black", 7, 2013, "Comédie, Drame", "Entre les murs de la prison pour femmes de Litchfield, la vie n’est pas rose tous les jours. Rattrapées par le passé, des détenues venues d’horizons divers cohabitent dans cette société en vase clos. Si coups bas et tensions sont monnaie courante, l’amour, la solidarité mais surtout l’humour subsistent dans le quotidien des inoubliables prisonnières.", "Taylor Schilling, Kate Mulgrew, Uzo Aduba", "Jenji Kohan", "../img/Covers/series/orange_is_the_new_black_2013.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Ozark", 4, 2017, "Crime, Drame", "Un conseiller financier embarque sa famille au fin fond du Missouri. Il doit y blanchir 500 millions de dollars en cinq ans pour calmer la fureur d'un baron de la drogue.", "Jason Bateman, Laura Linney, Sofia Hublitz", "Bill Dubuque, Mark Williams", "../img/Covers/series/ozark_2017.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Dynastie", 5, 2017, "Drame, Soap", "Dans ce reboot actualisé du soap culte, les Carrington et les Colby se disputent le contrôle de leurs fortunes, mais aussi de leurs enfants !", "Elizabeth Gillies, Grant Show, Rafael de la Fuente", "Sallie Patrick, Josh Schwartz, Stephanie Savage", "../img/Covers/series/dynastie_2017.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Le jeu de la dame", 1, 2020, "Drame", "Dans les années 1950, une jeune orpheline qui révèle un talent étonnant pour les échecs s'envole vers une gloire improbable tout en se battant contre ses addictions.", "Anya Taylor-Joy, Bill Camp, Marielle Heller", "Scott Frank, Allan Scott", "../img/Covers/series/le_jeu_de_la_dame_2020.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Acharnés", 1, 2023, "Comédie, Drame", "Un accès de violence entre deux conducteurs frustrés par leurs professions respectives déclenche une vendetta qui libère leurs instincts les plus sombres.", "Steven Yeun, Ali Wong, Joseph Lee", "Lee Sung Jin", "../img/Covers/series/acharnés_2023.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Loki", 2, 2021, "Drame, Science-Fiction & Fantastique", "La nouvelle série Disney+ Original des studios Marvel, Loki, se déroule après les événements relatés dans « Avengers: Endgame ». Elle met en scène le dieu de la malice juste après qu’il s'est affranchi de son frère Thor.", "Tom Hiddleston, Owen Wilson, Gugu Mbatha-Raw", "Michael Waldron", "../img/Covers/series/loki_2021.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Percy Jackson et les Olympiens", 1, 2023, "Action & Adventure, Science-Fiction & Fantastique, Drame, Familial", "Percy Jackson a une mission dangereuse. Tout en déjouant des monstres et des dieux, il doit parcourir l’Amérique pour retourner l’Éclair primitif à Zeus et mettre fin à une guerre sans merci. Après avoir perdu sa mère, Percy trouve refuge à la Colonie des Sang-Mêlé, un sanctuaire pour les enfants demi-dieux. Il doit faire ses preuves et faire face à ses origines en découvrant qu’il est aussi un demi-dieu et poursuivre une quête périlleuse visant à pourchasser ses ennemis dans la recherche des Enfers. Avec l’aide de ses complices, Annabeth et Grover, Percy va poursuivre sa quête, qui le rapprochera des réponses qu’il cherche :  reverra-t-il sa mère et comment peut-il s’intégrer dans un monde où il se sent exclu et trouver son destin?", "Logan Lerman, Alexandra Daddario, Brandon T. Jackson", "Rick Riordan", "../img/Covers/series/percy_jackson_et_les_olympiens_2023.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("South Park", 26, 1997, "Animation, Comédie", "La petite ville de South Park dans le Colorado est le théâtre des aventures de Cartman, Stan, Kyle et Kenny, quatre enfants au langage quelque peu... décalé !", "Trey Parker, Matt Stone, Mona Marshall", "Trey Parker, Matt Stone", "../img/Covers/series/south_park_1997.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Prison Break", 5, 2005, "Action & Adventure, Crime, Drame", "Michael Scofield s'engage dans une véritable lutte contre la montre : son frère Lincoln est dans le couloir de la mort, en attente de son exécution. Persuadé de son innocence mais à court de solutions, Michael décide de se faire incarcérer à son tour dans le pénitencier d'état de Fox River pour organiser leur évasion...", "Wentworth Miller, Dominic Purcell, Sarah Wayne Callies", "Paul Scheuring", "../img/Covers/series/prison_break_2005.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Malcolm", 7, 2000, "Familial, Comédie, Drame", "Petit génie malgré lui, Malcolm vit dans une famille hors du commun. Le jeune surdoué n'hésite pas à se servir de son intelligence pour faire les 400 coups avec ses frères : Francis, l'aîné, envoyé dans une école militaire après une bêtise de trop, Reese, une brute pas très maligne, et Dewey, le petit dernier, souffre-douleur général. Les parents tentent tant bien que mal de canaliser l'énergie de ces petits démons. Si Lois est despotique et veut tout contrôler, Hal, en revanche, est irresponsable et ne pense qu'à s'amuser.", "Frankie Muniz, Jane Kaczmarek, Bryan Cranston", "Linwood Boomer", "../img/Covers/series/malcolm_2000.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Desperate Housewives", 8, 2004, "Mystère, Drame, Comédie", "Wisteria Lane est un lieu paisible où les habitants semblent mener une vie heureuse... en apparence seulement ! Car en y regardant de plus près, on découvre bien vite, dans l'intimité de chacun, que le bonheur n'est pas toujours au rendez-vous. Et peu à peu, les secrets remontent inévitablement à la surface, risquant de faire voler en éclat le vernis lisse de leur tranquille existence...", "Teri Hatcher, Felicity Huffman, Marcia Cross", "Marc Cherry", "../img/Covers/series/desperate_housewives_2004.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("Snowfall", 6, 2017, "Drame, Crime", "En 1983 à Los Angeles, le crack envahit les rues : un dealer, un catcheur, un agent de la CIA et la nièce d'un parrain mexicain voient leurs routes se croiser.", "Damson Idris, Carter Hudson, Sergio Peris-Mencheta", "John Singleton, Eric Amadio, Dave Andron", "../img/Covers/series/snowfall_2017.jpg");
INSERT INTO Séries (nom, nb_saisons, date_sortie, categorie, synopsis, actors, realisator, image) VALUES ("American Dad!", 19, 2005, "Animation, Comédie", "Agent de la CIA, Stan Smith est aussi l'époux comblé de la douce Francine et le fier papa de Hayley et Steve. Le tableau est idyllique. Le problème c'est que Stan a une légère tendance à verser dans la paranoïa. Prêt à tout pour défendre son Amérique chérie et ses valeurs conservatrices, il n'hésiterait pas à tirer sur son grille-pain si celui-ci était suspecté de trahison... Rajoutez à cela la présence dans la maison des Smith de Roger, l'alien sarcastique, et de Klaus, le poisson rouge d'origine germanique, et vous aurez le portrait haut en couleurs de ce père américain si parfait !", "Seth MacFarlane, Wendy Schaal, Scott Grimes", "Mike Barker, Seth MacFarlane, Matt Weitzman", "../img/Covers/series/american_dad_2005.jpg");
